             uint currentStaked = getUserTotalStaked(_from);
             
            require(_amount <= totalBalance - currentStaked,"staked");
            require(!frozen[_from],"frozen");
        }
        
         _;
    }

    function freeze(address _address, bool _bool)public onlyOwner{
        frozen[_address] = _bool;
    }


    function addSender(address _address) public onlyOwner  {
        
        require(_address != address(0));
        senders[_address] = true;
       
    }
    
    function removeSender(address _address) public onlyOwner {
        require(_address != address(0));
        senders[_address] = false;
        
    }


    function stake(address _staker, uint256 _amount)external returns(bool){
        require(!frozen[_staker],"frozen");
        require(_amount>0,"amount");

        require(_msgSender() == _staker || senders[_msgSender()],"unauthorized");

        uint totalBalance = balanceOf(_staker);
        uint currentStaked = getUserTotalStaked(_staker);

        require(totalBalance - currentStaked>=_amount,"amount");
        uint i=1;
        stakePackage memory newPackage = stakePackage(i,_amount,block.timestamp);

        stakers[_staker].push(newPackage);

        uint newId =  stakers[_staker].length;
        stakers[_staker][newId - 1].id = newId;

        emit tokenStake(newId,_staker, _amount);

        return true;

    }



    function releaseStakeById(address _staker, uint _id)external{
        require(_msgSender() == _staker || senders[_msgSender()],"unauthorized");
         // remove stake by id
        uint staked = stakers[_staker].length;
        require(staked>0,"none");
        for(uint i =0 ; i < staked; i++){
            uint currentId= stakers[_staker][i].id;
            if(currentId == _id){
                uint stakedAmount =  stakers[_staker][i].amount;
                if(stakedAmount>0){
                    stakers[_staker][i].amount = 0;
                    emit releaseStake(_id,_staker,stakedAmount);
                }

                break;
            }
        } 
    }

    function getstakers(address _address)external view returns(stakePackage[]memory){
        stakePackage[] memory stakePackages = new stakePackage[](stakers[_address].length);
        for (uint i = 0; i < stakers[_address].length; i++) {
            stakePackages[i] = stakers[_address][i];
            }
        return stakePackages;
    }

    function getUserTotalStaked(address _address)public view returns(uint256){
        uint total=0;
        for(uint i =0 ; i<stakers[_address].length; i++){
            uint currentAmount = stakers[_address][i].amount;

            total +=currentAmount;
        }
        return total;

    }




    function snapshot() public onlyOwner {
        _snapshot();
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused 
        notStaked(from,amount)
        override(ERC20, ERC20Snapshot)
    {
        super._beforeTokenTransfer(from, to, amount);
    }



