    "contracts/CloprBottles.sol": {
      "content": "// SPDX-License-Identifier: MIT
// Clopr Contracts

pragma solidity 0.8.21;

import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {ERC2981} from "@openzeppelin/contracts/token/common/ERC2981.sol";
import {ERC165} from "@openzeppelin/contracts/utils/introspection/ERC165.sol"; // import for natSpecs
import {ERC721A} from "./lib/standards/ERC721A.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {IERC2981} from "@openzeppelin/contracts/interfaces/IERC2981.sol";
import {IERC721} from "@openzeppelin/contracts/interfaces/IERC721.sol";
import {IERC165} from "@openzeppelin/contracts/utils/introspection/IERC165.sol";
import {IERC721Metadata} from "@openzeppelin/contracts/interfaces/IERC721Metadata.sol";
import {IERC4906} from "./interfaces/IERC4906.sol";
import {IERC721A} from "./lib/standards/IERC721A.sol";
import {IAccessControl} from "@openzeppelin/contracts/access/IAccessControl.sol";
import {ICloprBottles} from "./interfaces/ICloprBottles.sol";
import {IPotionDelegatedFillContract} from "./interfaces/IPotionDelegatedFillContract.sol";
import {IDelegateRegistry} from "./lib/delegateCash/IDelegateRegistry.sol";

/**
 * @title CloprBottles
 * @author Pybast.eth - Nefture
 * @custom:lead Antoine Bertin - Clopr
 * @notice This contract serves as the core of the Clopr protocol, managing the CloprBottles NFT collection, including potion-based assets, staking mechanisms, and dynamic metadata.
 */
contract CloprBottles is
    ERC721A,
    AccessControl,
    Ownable,
    ICloprBottles,
    ERC2981,
    IERC4906
{
    uint16 private constant STORY_POTION_ID = 42;

    bytes32 private constant MARKETING_MINT_ROLE =
        keccak256("MARKETING_MINT_ROLE");
    bytes32 private constant GRANT_WHITELIST_ROLE =
        keccak256("GRANT_WHITELIST_ROLE");
    bytes32 private constant MINT_PHASE_ROLE = keccak256("MINT_PHASE_ROLE");

    /// @dev delegate cash V2 contract
    IDelegateRegistry private constant DC =
        IDelegateRegistry(0x00000000000000447e69651d841bD8D104Bed493);

    uint16 public constant CLOPR_BOTTLES_MAX_SUPPLY = 10_000;
    uint16 public constant RESERVED_MARKETING_SUPPLY = 2_000;

    /// @dev minimum number of blocks before a delegate can transfer a token since its last time being emptied
    uint16 public constant NUMBER_OF_BLOCKS_AFTER_EMPTY = 20;

    /// @dev number of tokens reserved for marketing
    uint16 private remainingMarketingSupply;

    /// @dev timestamp of the current season's start
    uint48 private seasonStartTime;

    /// @dev ERC2981 royalty share per thousand unit of price
    uint16 private royaltyPerThousand;
    /// @dev ERC2981 royalty receiver address
    address private royaltyReceiver;

    /// @dev stores each potion's URI
    mapping(uint256 potionId => PotionBaseUri potionBaseUri) private potionUris;

    /// @dev stores bottles onchain metadata
    mapping(uint256 tokenId => BottleInformation bottleInformation)
        private bottles;

    /// @dev stores bottle fill and empty access for each potion type
    mapping(uint256 potionId => address grantee) private fillAccess;
    mapping(uint256 potionId => address grantee) private emptyAccess;

    /// @dev stores each mint phase information
    mapping(uint256 phaseId => MintPhase mintPhaseInformation)
        private mintPhases;

    /// @dev stores the number of tokens minted during a phase
    mapping(uint256 phaseId => mapping(address minter => uint256 numbMinted))
        private numbMintedForPhase;

    constructor() ERC721A("CloprBottles", "CB") {
        royaltyPerThousand = 25;
        royaltyReceiver = 0x89fe24F08ee13f284a9E3c1fE10964250Ad682a1;

        remainingMarketingSupply = RESERVED_MARKETING_SUPPLY;

        _grantRole(
            DEFAULT_ADMIN_ROLE,
            0x799B7627f972dcf97b00bBBC702b2AD1b7546519
        );
        _transferOwnership(0x799B7627f972dcf97b00bBBC702b2AD1b7546519);
    }

    /**
     * ----------- EXTERNAL -----------
     */

    /// @inheritdoc ICloprBottles
    function stake(uint256 tokenId, address vault) external {
        address requester = msg.sender;

        if (vault != address(0)) {
            bool isDelegateValid = DC.checkDelegateForERC721(
                msg.sender,
                vault,
                address(this),
                tokenId,
                ""
            );
            if (!isDelegateValid) revert InvalidDelegateVaultPairing();
            requester = vault;
        }

        if (ownerOf(tokenId) != requester) revert NotBottleOwner();

        // slither-disable-next-line timestamp
        bottles[tokenId].stakingTime = uint48(block.timestamp);

        emit BottleStaked(tokenId, true);
    }

    /// @inheritdoc ICloprBottles
    function unstake(uint256 tokenId, address vault) external {
        address requester = msg.sender;

        if (vault != address(0)) {
            bool isDelegateValid = DC.checkDelegateForERC721(
                msg.sender,
                vault,
                address(this),
                tokenId,
                ""
            );
            if (!isDelegateValid) revert InvalidDelegateVaultPairing();
            requester = vault;
        }

        if (ownerOf(tokenId) != requester) revert NotBottleOwner();

        delete bottles[tokenId].stakingTime;

        emit BottleStaked(tokenId, false);
    }

    /// @inheritdoc ICloprBottles
    function emergencyEmptyBottle(uint256 tokenId) external {
        if (ownerOf(tokenId) != msg.sender) revert NotBottleOwner();

        delete bottles[tokenId].filled;
        delete bottles[tokenId].delegatedFillLevel;

        // Don't increment numberDrinks
        emit EmptyBottle(tokenId);
        emit MetadataUpdate(tokenId);
    }

    /// @inheritdoc ICloprBottles
    function mintBottles(
        uint8 quantity,
        uint256 mintPhaseIndex,
        bytes calldata signature
    ) external payable {
        uint256 currentSupply = totalSupply();

        MintPhase memory currentMintPhase = mintPhases[mintPhaseIndex];

        // check if mint has ended
        // slither-disable-next-line timestamp
        if (currentMintPhase.endTimestamp < block.timestamp)
            revert MintPhaseEndedOrDoesntExist();

        // check if mint has started
        // slither-disable-next-line timestamp
        if (currentMintPhase.startTimestamp > block.timestamp)
            revert MintPhaseNotStarted();

        // minter can only mint a limited number of tokens
        bool isMaxMintReached;
        unchecked {
            isMaxMintReached =
                numbMintedForPhase[mintPhaseIndex][msg.sender] +
                    uint256(quantity) >
                currentMintPhase.maxMintPerWallet;
        }
        if (isMaxMintReached) revert MaxMintReached();

        // check if right price
        bool isBadMintPrice;
        unchecked {
            isBadMintPrice = msg.value != currentMintPhase.price * quantity;
        }
        if (isBadMintPrice) revert BadMintPrice();

        // check if mint is sold out
        bool isMintSoldOut;
        unchecked {
            isMintSoldOut =
                currentSupply + quantity >
                CLOPR_BOTTLES_MAX_SUPPLY - remainingMarketingSupply;
        }
        if (isMintSoldOut) revert MintSoldOut();

        // check if minter is whitelisted
        if (!_isWhitelisted(mintPhaseIndex, signature)) revert NotAuthorised();

        if (quantity > currentMintPhase.remainingSupply) revert PhaseSoldOut();

        unchecked {
            mintPhases[mintPhaseIndex].remainingSupply -= quantity;
        }

        unchecked {
            numbMintedForPhase[mintPhaseIndex][msg.sender] += quantity;
        }

        // fill up minted potions
        for (uint256 index = 1; index <= quantity; index++) {
            bottles[currentSupply + index] = BottleInformation({
                potionId: STORY_POTION_ID,
                delegatedFillLevel: false,
                filled: true,
                stakingTime: 0,
                numberDrinks: 0,
                lastEmptyBlock: 0
            });

            // No FillBottle event emission because this behaviour is predictable, all bottles
            // are minted filled up with SP
        }

        _mint(msg.sender, quantity);
    }

    /**
     * ----------- CLOPR PROTOCOL -----------
     */

    /// @inheritdoc ICloprBottles
    function fillBottle(
        uint256 tokenId,
        uint16 potionId,
        bool isDelegatedFillLevel,
        address potentialBottleOwner
    ) external {
        BottleInformation memory bottle = bottles[tokenId];

        if (fillAccess[potionId] != msg.sender) revert FillingUpNotAuthorised();

        if (_getBottleFillLevel(bottle, tokenId) > 0) revert BottleNotEmpty();
        if (ownerOf(tokenId) != potentialBottleOwner) revert NotBottleOwner();
        if (bottle.stakingTime == 0) revert BottleNotStaked();

        if (isDelegatedFillLevel) {
            bottles[tokenId] = BottleInformation({
                potionId: potionId,
                delegatedFillLevel: true,
                filled: false,
                stakingTime: bottle.stakingTime,
                numberDrinks: bottle.numberDrinks,
                lastEmptyBlock: 0
            });
        } else {
            bottles[tokenId] = BottleInformation({
                potionId: potionId,
                delegatedFillLevel: false,
                filled: true,
                stakingTime: bottle.stakingTime,
                numberDrinks: bottle.numberDrinks,
                lastEmptyBlock: 0
            });
        }

        emit FillBottle(tokenId, potionId);
        emit MetadataUpdate(tokenId);
    }

    /// @inheritdoc ICloprBottles
    function emptyBottle(
        uint256 tokenId,
        uint16 potionId,
        address potentialBottleOwner
    ) external {
        BottleInformation memory bottle = bottles[tokenId];

        if (emptyAccess[potionId] != msg.sender) revert EmptyingNotAuthorized();

        if (ownerOf(tokenId) != potentialBottleOwner) revert NotBottleOwner();
        if (bottle.stakingTime == 0) revert BottleNotStaked();

        if (bottle.potionId != potionId) revert InvalidPotion();
        if (_getBottleFillLevel(bottle, tokenId) < 100) revert BottleNotFull();

        unchecked {
            bottles[tokenId] = BottleInformation({
                potionId: potionId,
                delegatedFillLevel: false,
                filled: false,
                stakingTime: bottle.stakingTime,
                numberDrinks: bottle.numberDrinks + 1,
                lastEmptyBlock: uint64(block.number)
            });
        }

        emit EmptyBottle(tokenId);
        emit MetadataUpdate(tokenId);
    }

    /**
     * ----------- ADMIN -----------
     */

    /// @inheritdoc ICloprBottles
    function addNewPotion(
        uint256 potionId,
        address potionFillContract,
        address potionEmptyContract,
        string memory potionBaseUri
    ) external onlyOwner {
        if (potionUris[potionId].exists) revert PotionAlreadyExist();

        if (
            potionFillContract == address(0) ||
            potionEmptyContract == address(0)
        ) revert PotionContractCantBeNull();

        if (bytes(potionBaseUri).length == 0) revert PotionBaseUriCantBeNull();

        potionUris[potionId] = PotionBaseUri({
            baseUri: potionBaseUri,
            exists: true,
            isFrozen: false
        });

        fillAccess[potionId] = potionFillContract;
        emptyAccess[potionId] = potionEmptyContract;

        emit NewPotion(
            potionFillContract,
            potionEmptyContract,
            potionId,
            potionBaseUri
        );
    }

    /// @inheritdoc ICloprBottles
    function createNewMintPhase(
        uint256 phaseIndex,
        uint128 price,
        uint48 startTimestamp,
        uint48 endTimestamp,
        uint8 maxMintPerWallet,
        uint16 phaseSupply
    ) external onlyRole(MINT_PHASE_ROLE) {
        if (mintPhases[phaseIndex].endTimestamp != 0)
            revert MintPhaseAlreadyExist();

        if (startTimestamp == 0) revert InvalidStartTimestamp();

        // purposefully don't check that start < end as this is
        // already verified in the mint function

        mintPhases[phaseIndex].price = price;
        mintPhases[phaseIndex].startTimestamp = startTimestamp;
        mintPhases[phaseIndex].endTimestamp = endTimestamp;
        mintPhases[phaseIndex].maxMintPerWallet = maxMintPerWallet;
        mintPhases[phaseIndex].remainingSupply = phaseSupply;

        emit NewMintPhase(
            phaseIndex,
            price,
            startTimestamp,
            endTimestamp,
            maxMintPerWallet,
            phaseSupply
        );
    }

    /// @inheritdoc ICloprBottles
    function cancelMintPhase(uint256 phaseIndex) external onlyOwner {
        delete mintPhases[phaseIndex];

        emit CancelMintPhase(phaseIndex);
    }

    /// @inheritdoc ICloprBottles
    function marketingMint(
        uint16 quantity,
        address to
    ) external onlyRole(MARKETING_MINT_ROLE) {
        uint256 currentSupply = totalSupply();

        if (quantity > remainingMarketingSupply)
            revert MarketingSupplyReached();

        unchecked {
            remainingMarketingSupply -= quantity;
        }

        // fill up minted potions
        for (uint256 index = 1; index <= quantity; index++) {
            bottles[currentSupply + index] = BottleInformation({
                potionId: STORY_POTION_ID,
                delegatedFillLevel: false,
                filled: true,
                stakingTime: 0,
                numberDrinks: 0,
                lastEmptyBlock: 0
            });

            // No FillBottle event emission because this behaviour is predictable, all bottles
            // are minted filled up with SP
        }

        _mint(to, quantity);
    }

    /// @inheritdoc ICloprBottles
    function setRoyalty(
        uint16 newRoyaltyPerThousand,
        address newRoyaltyReceiver
    ) external onlyOwner {
        if (newRoyaltyReceiver == address(0))
            revert RoyaltyReceiverCantBeZero();
        if (newRoyaltyPerThousand > 1000) revert RoyaltyPerThousandTooHigh();

        royaltyPerThousand = newRoyaltyPerThousand;
        royaltyReceiver = newRoyaltyReceiver;

        emit RoyaltyChange(newRoyaltyPerThousand, newRoyaltyReceiver);
    }

    /// @inheritdoc ICloprBottles
    function changePotionBaseUri(
        uint256 potionId,
        string memory newPotionBaseUri
    ) external onlyOwner {
        if (bytes(newPotionBaseUri).length == 0)
            revert PotionBaseUriCantBeNull();

        PotionBaseUri memory uri = potionUris[potionId];

        if (!uri.exists) revert PotionDoesntExist();
        if (uri.isFrozen) revert PotionUriIsFrozen();

        potionUris[potionId].baseUri = newPotionBaseUri;

        emit NewPotionBaseUri(potionId, newPotionBaseUri);
        emit BatchMetadataUpdate(1, type(uint256).max);
    }

    /// @inheritdoc ICloprBottles
    function offchainMetadataUpdate() external onlyOwner {
        emit BatchMetadataUpdate(1, type(uint256).max);
    }

    /// @inheritdoc ICloprBottles
    function freezePotionUri(uint256 potionId) external onlyOwner {
        potionUris[potionId].isFrozen = true;

        emit FreezePotionUri(potionId);
    }

    /// @inheritdoc ICloprBottles
    function startSeason() external onlyOwner {
        // slither-disable-next-line timestamp
        seasonStartTime = uint48(block.timestamp);

        emit StartNewSeason(seasonStartTime);
    }

    /// @inheritdoc ICloprBottles
    function withdraw(address receiver) external onlyOwner {
        // slither-disable-next-line incorrect-equality
        if (address(this).balance == 0) revert NothingToWithdraw();
        if (receiver == address(0)) revert CantWithdrawToZeroAddress();

        // slither-disable-next-line low-level-calls
        (bool sent, ) = receiver.call{value: address(this).balance}("");
        if (!sent) revert FailedToWithdraw();
    }

    /**
     * ----------- ENUMERATIONS -----------
     */

    /// @inheritdoc ICloprBottles
    function getMintedMarketingSupply()
        external
        view
        returns (uint16 mintedMarketingSupply)
    {
        unchecked {
            mintedMarketingSupply =
                RESERVED_MARKETING_SUPPLY -
                remainingMarketingSupply;
        }
    }

    /// @inheritdoc ICloprBottles
    function getCurrentSeasonStartTime()
        external
        view
        returns (uint48 seasonStartTime_)
    {
        seasonStartTime_ = seasonStartTime;
    }

    /// @inheritdoc ICloprBottles
    function getMintPhaseInfos(
        uint256 mintPhaseIndex
    ) external view returns (MintPhase memory mintPhase) {
        mintPhase = mintPhases[mintPhaseIndex];
        if (mintPhase.endTimestamp == 0) revert MintPhaseDoesntExist();
    }

    /// @inheritdoc ICloprBottles
    function getMintPrice(
        uint256 mintPhaseIndex
    ) external view returns (uint128 mintPrice) {
        MintPhase memory mintPhase = mintPhases[mintPhaseIndex];

        if (mintPhase.endTimestamp == 0) revert MintPhaseDoesntExist();

        mintPrice = mintPhase.price;
    }

    /// @inheritdoc ICloprBottles
    function getBottleInformation(
        uint256 tokenId
    )
        external
        view
        returns (
            uint16 potionId,
            uint8 potionFill,
            uint48 stakingTime,
            uint48 stakingDuration,
            uint24 numberDrinks,
            uint64 lastEmptyBlock
        )
    {
        BottleInformation memory bottle = bottles[tokenId];

        potionId = bottle.potionId;
        potionFill = _getBottleFillLevel(bottle, tokenId);
        stakingTime = bottle.stakingTime;
        stakingDuration = _getStakingDuration(bottle.stakingTime);
        numberDrinks = bottle.numberDrinks;
        lastEmptyBlock = bottle.lastEmptyBlock;
    }

    /// @inheritdoc ICloprBottles
    function getStakingTime(
        uint256 tokenId
    ) external view returns (uint48 stakingTime) {
        stakingTime = bottles[tokenId].stakingTime;
    }

    /// @inheritdoc ICloprBottles
    function getStakingDuration(
        uint256 tokenId
    ) external view returns (uint48 stakingDuration) {
        stakingDuration = _getStakingDuration(bottles[tokenId].stakingTime);
    }

    /// @inheritdoc ICloprBottles
    function getBottleFillLevel(
        uint256 tokenId
    ) external view returns (uint8 fillLevel) {
        BottleInformation memory bottle = bottles[tokenId];

        fillLevel = _getBottleFillLevel(bottle, tokenId);
    }

    /// @inheritdoc ICloprBottles
    function getBottleLastEmptyBlock(
        uint256 tokenId
    ) external view returns (uint64 lastEmptyBlock) {
        lastEmptyBlock = bottles[tokenId].lastEmptyBlock;
    }

    /// @notice Get the price of royalties and the receiver's address for a given sale price
    /// @param salePrice the price of the sale in any unit of exchange
    /// @return receiver the return variables of a contract’s function state variable
    /// @return royaltyAmount the return variables of a contract’s function state variable
    /// @inheritdoc	ERC2981
    function royaltyInfo(
        uint256,
        uint256 salePrice
    ) public view override returns (address receiver, uint256 royaltyAmount) {
        receiver = royaltyReceiver;
        royaltyAmount = (royaltyPerThousand * salePrice) / 1000;
    }

    /// @notice Retrieves a token's URI
    /// @param tokenId token ID of the bottle
    /// @return uri the URI of the token
    /// @inheritdoc	ERC721A
    function tokenURI(
        uint256 tokenId
    ) public view override(ERC721A, IERC721A) returns (string memory uri) {
        require(_exists(tokenId), "ERC721: invalid token ID");

        BottleInformation memory bottle = bottles[tokenId];
        PotionBaseUri memory potionUri = potionUris[bottle.potionId];

        uint8 fill = _getBottleFillLevel(bottle, tokenId);

        uri = string(
            abi.encodePacked(
                potionUri.baseUri,
                _toString(tokenId),
                "/",
                _toString(fill)
            )
        );
    }

    /**
     * ----------- ERC165 -----------
     */

    /// @notice Know if a given interface ID is supported by this contract
    /// @dev This function overrides ERC721A, AccessControl, IERC721A, ERC2981
    /// @param interfaceId ID of the interface
    /// @return supports_ is the interface supported
    /// @inheritdoc	ERC165
    function supportsInterface(
        bytes4 interfaceId
    )
        public
        pure
        override(ERC721A, AccessControl, IERC721A, ERC2981)
        returns (bool supports_)
    {
        supports_ =
            interfaceId == type(IERC721).interfaceId || // ERC165 interface ID for ERC721
            interfaceId == type(IERC721Metadata).interfaceId || // ERC165 interface ID for ERC721Metadata
            interfaceId == type(IERC2981).interfaceId || // ERC165 interface ID for ERC2981
            interfaceId == type(IERC165).interfaceId || // ERC165 interface id for ERC165
            interfaceId == type(IAccessControl).interfaceId || // ERC165 interface id for AccessControl
            interfaceId == type(IERC4906).interfaceId; // ERC165 interface id for ERC4906
    }

    /**
     * ----------- INTERNAL -----------
     */

    /// @dev The staking duration can be affected by the current season's start time
    /// @param stakingTime stakingTime of the bottle
    /// @return stakingDuration the duration the bottle was staked for
    function _getStakingDuration(
        uint48 stakingTime
    ) internal view returns (uint48 stakingDuration) {
        if (stakingTime == 0) {
            stakingDuration = 0;
        }
        // slither-disable-next-line timestamp
        else if (stakingTime > seasonStartTime) {
            unchecked {
                // slither-disable-next-line timestamp
                stakingDuration = uint48(block.timestamp) - stakingTime;
            }
        }
        // slither-disable-next-line timestamp
        else {
            unchecked {
                // slither-disable-next-line timestamp
                stakingDuration = uint48(block.timestamp) - seasonStartTime;
            }
        }
    }

    /// @dev retrieves the bottle's fill level
    /// @param bottle bottle for which to retrieve the fill level
    /// @param tokenId tokenId of the bottle
    function _getBottleFillLevel(
        BottleInformation memory bottle,
        uint256 tokenId
    ) internal view returns (uint8 fillLevel) {
        if (bottle.delegatedFillLevel) {
            uint8 fill = IPotionDelegatedFillContract(
                fillAccess[bottle.potionId]
            ).getFillLevel(tokenId);

            fillLevel = fill;
        } else {
            fillLevel = bottles[tokenId].filled ? 100 : 0;
        }
    }

    /// @dev returns true is the signature grants a whitelist access
    /// @param mintPhaseIndex index of the mint phase
    /// @param signature signature to grant access to the mint phase
    function _isWhitelisted(
        uint256 mintPhaseIndex,
        bytes calldata signature
    ) internal view returns (bool) {
        bytes32 hash_ = ECDSA.toEthSignedMessageHash(
            keccak256(abi.encodePacked(msg.sender, mintPhaseIndex))
        );

        return hasRole(GRANT_WHITELIST_ROLE, ECDSA.recover(hash_, signature));
    }

    /// @dev ID of the first CloprBottles token (spoiler: it's 1)
    /// @return startId the ID of the first CloprBottles token
    /// @inheritdoc	ERC721A
    function _startTokenId() internal pure override returns (uint256 startId) {
        startId = 1;
    }

    /// @notice Prevent protocols from transfering bottles if they are staked
    /// @param from owner of the token
    /// @param startTokenId first ID of the transfered token
    /// @param quantity number of consecutive tokens being transfered
    /// @inheritdoc	ERC721A
    function _beforeTokenTransfers(
        address from,
        address,
        uint256 startTokenId,
        uint256 quantity
    ) internal view override {
        // quantity is >1 when minting tokens, in this case we don't need to check
        // if the bottle is staked or if it was recently emptied
        if (msg.sender == from || quantity != 1 || from == address(0)) return;

        // check if a protocol is trying to transfer the token
        if (bottles[startTokenId].stakingTime > 0)
            revert CantTransferStakedBottle();

        // bottles' value vary depending on their potion content. If a bottle is emptied, it should have
        // less value compared to if it's full. Observing this, there is a risk for someone to acquire, what
        // he thinks as a filled bottle, and receive an empty one as the owner could technically empty the bottle
        // right before the purchase transaction is added on-chain.
        // To mitigate this risk, we implement a cool down of NUMBER_OF_BLOCKS_AFTER_EMPTY blocks where tokens
        // can't be traded.
        if (
            block.number - bottles[startTokenId].lastEmptyBlock <
            NUMBER_OF_BLOCKS_AFTER_EMPTY
        ) revert CantTransferRecentlyEmptiedBottle();
    }
}
"
    },
    "contracts/interfaces/ICloprBottles.sol": {
      "content": "// SPDX-License-Identifier: MIT
// Clopr Contracts

pragma solidity 0.8.21;

import "../lib/standards/IERC721A.sol";

/**
 * @title ICloprBottles
 * @author Pybast.eth - Nefture
 * @custom:lead Antoine Bertin - Clopr
 * @notice This contract serves as the core of the Clopr protocol, managing the CloprBottles NFT collection, including potion-based assets, staking mechanisms, and dynamic metadata.
 */
interface ICloprBottles is IERC721A {
    /// @notice struct for base URI information
    /// @param baseUri base URI of the potion
    /// @param isFrozen if true, then the baseUri can't be modified
    /// @param exists used to know if a potionBaseUri exists
    struct PotionBaseUri {
        string baseUri;
        bool isFrozen;
        bool exists;
    }

    /// @notice struct for bottles information
    /// @param potionId is the ID of the potion inside the bottle or the ID of the last potion if the bottle is empty
    /// @param filled is the fill status of the bottle from 0 (empty) or 100 (full)
    /// @param delegatedFillLevel if true, then potionFill should be ingored and the fill level calculation should be delegated to the potion contract
    /// @param stakingTime timestamp of the bottle's staking or 0 if the bottle is unstaked
    /// @param numberDrinks number of times the bottle was drunk
    /// @param lastEmptyBlock block number at which the bottle was last emptied
    struct BottleInformation {
        uint16 potionId;
        bool filled;
        bool delegatedFillLevel;
        uint48 stakingTime;
        uint24 numberDrinks;
        uint64 lastEmptyBlock;
    }

    /// @notice struct for mint phase information
    /// @param price is the price of the mint phase
    /// @param startTimestamp is the start timestamp of the mint phase
    /// @param endTimestamp is the end timestamp of the mint phase
    /// @param maxMintPerWallet is the maximum token that can be minted per address
    /// @param remainingSupply is the remaining supply for a mint phase
    struct MintPhase {
        uint128 price;
        uint48 startTimestamp;
        uint48 endTimestamp;
        uint8 maxMintPerWallet;
        uint16 remainingSupply;
    }

    /// @notice thrown if the given vault is not delegated to the caller
    error InvalidDelegateVaultPairing();

    /// @notice thrown if a given potion doesn't exist
    error PotionDoesntExist();

    /// @notice thrown if a potion's URI is frozen
    error PotionUriIsFrozen();

    /// @notice thrown if a potion already exist
    error PotionAlreadyExist();

    /// @notice thrown if an address is not the bottle's owner
    error NotBottleOwner();

    /// @notice thrown if a bottle is not staked
    error BottleNotStaked();

    /// @notice thrown if the marketing supply has been reached
    error MarketingSupplyReached();

    /// @notice thrown if the mint phase already exist
    error MintPhaseAlreadyExist();

    /// @notice thrown if the mint phase doesn't exist
    error MintPhaseDoesntExist();

    /// @notice thrown if the mint phase has ended or if it doesn't exist
    error MintPhaseEndedOrDoesntExist();

    /// @notice thrown if the mint phase has not started yet
    error MintPhaseNotStarted();

    /// @notice thrown if the mint price is not correct
    error BadMintPrice();

    /// @notice thrown if the mint is sold out
    error MintSoldOut();

    /// @notice thrown if the mint phase is sold out
    error PhaseSoldOut();

    /// @notice thrown if the phase's maximum mint has been reached
    error MaxMintReached();

    /// @notice thrown if not authorised to mint
    error NotAuthorised();

    /// @notice thrown if trying to transfer a staked bottle
    error CantTransferStakedBottle();

    /// @notice thrown if trying to transferFrom a bottle that was just emptied
    error CantTransferRecentlyEmptiedBottle();

    /// @notice thrown if trying to fill up a bottle without the authorisation
    error FillingUpNotAuthorised();

    /// @notice thrown if trying to fill up a bottle that is not empty
    error BottleNotEmpty();

    /// @notice thrown if trying to empty a bottle without the authorisation
    error EmptyingNotAuthorized();

    /// @notice thrown if trying to empty a bottle from the wrong potion
    error InvalidPotion();

    /// @notice thrown if trying to empty a bottle that is not full
    error BottleNotFull();

    /// @notice thrown if trying to set the royalties receiver to the zero address
    error RoyaltyReceiverCantBeZero();

    /// @notice thrown if trying to set the royalties amount to a value higher than the maximum of 1000
    error RoyaltyPerThousandTooHigh();

    /// @notice thrown if there is no ether left to withdraw from the contract
    error NothingToWithdraw();

    /// @notice thrown if trying to withdraw to the zero address
    error CantWithdrawToZeroAddress();

    /// @notice thrown if withdrawing has failed
    error FailedToWithdraw();

    /// @notice thrown if trying to set the potionBaseUri as an empty string
    error PotionBaseUriCantBeNull();

    /// @notice thrown if trying to set potionFillContract or potionEmptyContract to address(0)
    error PotionContractCantBeNull();

    /// @notice thrown if trying to set the startTimestamp of a mint phase to zero
    error InvalidStartTimestamp();

    /// @notice emitted when a new potion type is added to the Clopr protocol
    /// @param potionFillContract contract allowed to fill up bottles
    /// @param potionEmptyContract contract allowed to empty bottles
    /// @param potionId new potion's unique ID
    /// @param potionMetadataUri nes potion's metadata URI
    event NewPotion(
        address indexed potionFillContract,
        address indexed potionEmptyContract,
        uint256 indexed potionId,
        string potionMetadataUri
    );

    /// @notice emitted when a potion's base URI is modified
    /// @param potionId potion's ID
    /// @param potionMetadataBaseUri new potion's base URI
    event NewPotionBaseUri(
        uint256 indexed potionId,
        string potionMetadataBaseUri
    );

    /// @notice emitted when a potion's URI is frozen so that it will never be modified
    /// @param potionId ID of the potion subject to a URI freeze
    event FreezePotionUri(uint256 indexed potionId);

    /// @notice emitted when a bottle is staked or unstaked
    /// @param tokenId ID of the bottle
    /// @param staked true if the bottle is being staked, false if it is being unstaked
    event BottleStaked(uint256 indexed tokenId, bool indexed staked);

    /// @notice emitted when a new staking season is started
    /// @param seasonStartTime timestamp of the start of the season
    event StartNewSeason(uint256 indexed seasonStartTime);

    /// @notice emitted when a bottle is filled up with potion
    /// @param tokenId ID of the bottle being filled up
    /// @param potionId ID of the potion filling up the bottle
    event FillBottle(uint256 indexed tokenId, uint256 indexed potionId);

    /// @notice emitted when a bottle is emptied
    /// @param tokenId ID of the bottle being emptied
    event EmptyBottle(uint256 indexed tokenId);

    /// @notice emitted when the ERC2981 royalties are modified
    /// @param royaltyPerThousand royalty amount per thousand
    /// @param royaltyReceiver royalties receiver address
    event RoyaltyChange(uint16 royaltyPerThousand, address royaltyReceiver);

    /// @notice emitted when a new mint phase is created
    /// @param phaseIndex index of the mint phase
    /// @param price price of the mint phase
    /// @param startTimestamp timestamp for the start of the mint phase
    /// @param endTimestamp timestamp for the end of the mint phase
    /// @param maxMintPerWallet maximum number of tokens mintable per wallet
    /// @param phaseSupply maximum token supply for the mint phase
    event NewMintPhase(
        uint256 indexed phaseIndex,
        uint128 price,
        uint48 indexed startTimestamp,
        uint48 indexed endTimestamp,
        uint8 maxMintPerWallet,
        uint16 phaseSupply
    );

    /// @notice emitted when a mint phase is cancelled
    /// @param phaseIndex index of the mint phase
    event CancelMintPhase(uint256 indexed phaseIndex);

    /**
     * ----------- EXTERNAL -----------
     */

    /// @notice Stake a CloprBottles
    /// @dev Emits an event to enable tracking bottle staking status
    /// @param tokenId token ID of the bottle
    function stake(uint256 tokenId, address vault) external;

    /// @notice Unstake a CloprBottles
    /// @dev Emits an event to enable tracking bottle staking status
    /// @param tokenId token ID of the bottle
    function unstake(uint256 tokenId, address vault) external;

    /// @notice Allows the owner of a bottle to empty the content of his bottle
    /// @dev Emits an event to enable tracking bottle contents
    /// @param tokenId token ID of the bottle
    function emergencyEmptyBottle(uint256 tokenId) external;

    /// @notice mint CloprBottless
    /// @param quantity number of tokens being minted
    /// @param mintPhaseIndex index of the mint phase
    /// @param signature ECDSA signature prooving that caller is whitelisted
    function mintBottles(
        uint8 quantity,
        uint256 mintPhaseIndex,
        bytes calldata signature
    ) external payable;

    /**
     * ----------- CLOPR PROTOCOL -----------
     */

    /// @notice Allows permited contracts to fill a CloprBottles with a specified Clopr Potion
    /// @dev Emits an event to enable tracking bottles' content
    /// @param tokenId token ID of the bottle
    /// @param potionId potion ID being filled up
    /// @param potentialBottleOwner said owner of the CloprBottles being filled up
    function fillBottle(
        uint256 tokenId,
        uint16 potionId,
        bool isDelegatedFillLevel,
        address potentialBottleOwner
    ) external;

    /// @notice Allows permited contracts to empty the content a CloprBottles
    /// @dev Emits an event to enable tracking bottles' content
    /// @param tokenId token ID of the bottle
    /// @param potionId potion ID being emptied
    /// @param bottleOwner owner of the CloprBottles being filled up
    function emptyBottle(
        uint256 tokenId,
        uint16 potionId,
        address bottleOwner
    ) external;

    /**
     * ----------- ADMIN -----------
     */

    /// @notice Allows owner to extend the protocol by adding a new potion, a contract allowed to fill bottles with this potion and a contract allowed to empty bottles with this potion
    /// @dev The new potion's ID must be unique
    ///      Emits an event to enable tracking new extensions of the protocol
    /// @param potionId the new potion's ID
    /// @param potionFillContract the contract now allowed to fill bottles with the new potion
    /// @param potionEmptyContract the contract now allowed to empty bottles filled up with the new potion
    /// @param potionBaseUri base URI of the new potion
    function addNewPotion(
        uint256 potionId,
        address potionFillContract,
        address potionEmptyContract,
        string memory potionBaseUri
    ) external;

    /// @notice Allows the creation of mint phases
    /// @dev Emits an event to enable tracking mint phases
    /// @param phaseIndex index of the mint phase
    /// @param price price of the mint phase to create
    /// @param startTimestamp timestamp for the start of the mint phase
    /// @param endTimestamp timestamp for the end of the mint phase
    /// @param maxMintPerWallet maximum number of tokens mintable per wallet
    /// @param phaseSupply maximum token supply for the mint phase
    function createNewMintPhase(
        uint256 phaseIndex,
        uint128 price,
        uint48 startTimestamp,
        uint48 endTimestamp,
        uint8 maxMintPerWallet,
        uint16 phaseSupply
    ) external;

    /// @notice Allows the cancellation of a mint phase
    /// @param phaseIndex index of the mint phase to cancel
    function cancelMintPhase(uint256 phaseIndex) external;

    /// @notice Allows wallets with MARKETING_MINT_ROLE to mint tokens for marketing purposes up to MARKETING_SUPPLY
    /// @param quantity number of tokens being minted
    /// @param to wallet receiving the tokens
    function marketingMint(uint16 quantity, address to) external;

    /// @notice set a new ERC2981 royalty for CloprBottless
    /// @param newRoyaltyPerThousand royalty amount per thousand
    /// @param newRoyaltyReceiver address of the royalties receiver
    function setRoyalty(
        uint16 newRoyaltyPerThousand,
        address newRoyaltyReceiver
    ) external;

    /// @notice Allows owner to change the base URI for a certain potion
    /// @dev Don't forget the trailing slash in the base URI as it will be concatenated with other information.
    ///      Emits an event to enable tracking base URI changes
    /// @param potionId the ID of the potion associated with the new base URI
    /// @param newPotionBaseUri the new base URI of the potion
    function changePotionBaseUri(
        uint256 potionId,
        string memory newPotionBaseUri
    ) external;

    /// @dev Used to emit an ERC-4906 event if the metadata are modified off chain
    function offchainMetadataUpdate() external;

    /// @notice Allows owner to freeze the URI of a particular potion making it immutable
    /// @dev Emits an event to enable tracking URI freezes
    /// @param potionId The potion ID which will have its URI frozen
    function freezePotionUri(uint256 potionId) external;

    /// @notice Allows owner to start a new season, this will reset all staking durations
    /// @dev Emits an event to enable tracking seasons
    function startSeason() external;

    /// @notice allows owner to withdraw contract's ether to an arbitrary account
    /// @param receiver receiver of the funds
    function withdraw(address receiver) external;

    /**
     * ----------- ENUMERATIONS -----------
     */

    /// @notice get the number of tokens which were minted for marketing
    /// @return mintedMarketingSupply the number of tokens which were minted for marketing
    function getMintedMarketingSupply()
        external
        view
        returns (uint16 mintedMarketingSupply);

    /// @notice Get the timestamp at which the current season was started
    /// @return seasonStartTime_ the timestamp at which the current season was started
    function getCurrentSeasonStartTime()
        external
        view
        returns (uint48 seasonStartTime_);

    /// @notice Get the information of a mint phase
    /// @param mintPhaseIndex index of the mint phase
    /// @return mintPhase mint phase object
    function getMintPhaseInfos(
        uint256 mintPhaseIndex
    ) external view returns (MintPhase memory mintPhase);

    /// @notice Get the mint price of CloprBottless for a specific mint phase
    /// @param mintPhaseIndex index of the mint phase
    /// @return mintPrice the mintPhase's price of CloprBottless
    function getMintPrice(
        uint256 mintPhaseIndex
    ) external view returns (uint128 mintPrice);

    /// @notice Retrieve the information of a bottle
    /// @param tokenId token ID of the bottle
    /// @return potionId the potion ID filling up the bottle
    /// @return potionFill the amount of potion filling up the bottle from 0 (empty) to 100 (full)
    /// @return stakingTime the timestamp at which the bottle was staked
    /// @return stakingDuration the timestamp at which the bottle was staked
    /// @return numberDrinks the number of times the bottle was staked
    function getBottleInformation(
        uint256 tokenId
    )
        external
        view
        returns (
            uint16 potionId,
            uint8 potionFill,
            uint48 stakingTime,
            uint48 stakingDuration,
            uint24 numberDrinks,
            uint64 lastEmptyBlock
        );

    /// @notice Get the time at which a bottle was staked
    /// @param tokenId token ID of the bottle
    /// @return stakingTime the timestamp at which the bottle was staked
    function getStakingTime(
        uint256 tokenId
    ) external view returns (uint48 stakingTime);

    /// @notice Get the staking duration of a bottle
    /// @dev The staking duration can be affected by the current season's start time
    /// @param tokenId token ID of the bottle
    /// @return stakingDuration the duration the bottle was staked for
    function getStakingDuration(
        uint256 tokenId
    ) external view returns (uint48 stakingDuration);

    /// @notice Get the fill level of a bottle
    /// @param tokenId token ID of the bottle
    /// @return fillLevel the bottle's fill level from 0 (empty) to 100 (full)
    function getBottleFillLevel(
        uint256 tokenId
    ) external view returns (uint8 fillLevel);

    /// @notice get the block number at which the bottle was emptied
    /// @param tokenId token ID of the bottle
    /// @return lastEmptyBlock block number at which the bottle was emptied
    function getBottleLastEmptyBlock(
        uint256 tokenId
    ) external view returns (uint64 lastEmptyBlock);
}
"
