    "contracts/ERC69_1155Fractional.sol": {
      "content": "// SPDX-License-Identifier: Unlicensed
pragma solidity 0.8.23;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721Receiver.sol";
import "./utils/ERC69Errors.sol";

abstract contract ERC69_1155Fractional is
    ERC69Errors,
    ERC1155,
    ERC1155Holder,
    Ownable,
    ReentrancyGuard,
    IERC721Receiver
{
    IERC20 public _erc20Token; // deposit token
    IERC721 public _assetNft; // asset NFT

    mapping(address => uint256) private _lastUnwrapTimestamp; // 5 minutes

    address private _closeProposer; // address that proposed closure
    address private _assetFeeRecipient; // address to receive tokens deposited for asset NFT
    address private _feeRecipient; // address to receive tax fees

    bool private _closed; // closed state of the contract
    bool private _closeProposed; // closure proposed state
    bool private _started; // started state of the contract

    string private _name; // name of the token
    string private _symbol; // symbol of the token
    string private _baseURI; // base URI for the token

    uint256 private _assetNftId; // id of the asset NFT
    uint256 private _maxSupply; // max supply of MFTs
    uint256 private constant MFT = 1; // MFT token id
    uint256 private _taxRate; // tax rate; 200 = 2.00%
    uint256 private _tokenAmount; // amount of deposit token to wrap
    uint256 private _totalTokenAmount; // total amount of tokens to wrap to receive asset NFT
    uint256 private _tokenAmountAfterTaxes; // amount of deposit token to wrap after taxes
    uint256 private _tokenTaxAmount; // amount of tax fees

    error AssetNotDeposited();
    error CannotClose();
    error CannotModifyWhileMFTsWrapped();
    error ContractClosed();
    error Unauthorized();

    event AssetDeposited(address indexed assetNft, uint256 assetNftId);
    event AssetEdited(address indexed assetNft, uint256 assetNftId);
    event AssetRedeemed(address indexed sender, uint256 assetNftId);
    event CloseProposed(address indexed proposer);
    event FeeRecipientClosed(address indexed feeRecipient);
    event Unwrap(address indexed sender, uint256 amount);
    event Wrap(address indexed sender, uint256 amount);

    modifier assetDeposited() {
        if (address(_assetNft) == address(0)) revert AssetNotDeposited();
        _;
    }

    modifier notClosed() {
        if (_closed) revert ContractClosed();
        _;
    }

    /**
     * @dev Sets the values for {name_}, {symbol_}, {baseURI_}, {erc20Token_}, {tokenAmount_}, and {maxSupply_}
     *
     * @param name_  immutable name of the token
     * @param symbol_  immutable symbol of the token
     * @param baseURI_  base URI for the token
     */
    constructor(
        string memory name_,
        string memory symbol_,
        string memory baseURI_,
        uint256 taxRate_,
        address feeRecipient_
    ) ERC1155("") {
        _name = name_;
        _symbol = symbol_;
        _baseURI = baseURI_;
        _taxRate = taxRate_;
        // precompute token amount after taxes to reduce gas fees when unwrapping
        _tokenAmountAfterTaxes = (_tokenAmount * (10000 - _taxRate)) / 10000;
        // precompute tax amount to reduce gas fees when wrapping
        _tokenTaxAmount = _tokenAmount - _tokenAmountAfterTaxes;
        _feeRecipient = feeRecipient_;
    }

    /**
     * @dev Returns the address of the asset fee recipient
     */
    function assetFeeRecipient() public view virtual returns (address) {
        return _assetFeeRecipient;
    }

    /**
     * @dev Returns the address of the asset NFT
     */
    function assetNft() public view virtual returns (address) {
        return address(_assetNft);
    }

    /**
     * @dev Returns the token ID of the asset NFT
     */
    function assetNftId() public view virtual returns (uint256) {
        return _assetNftId;
    }

    /**
     * @dev Returns the base URI
     */
    function baseURI() public view virtual returns (string memory) {
        return _baseURI;
    }

    /**
     * @dev Proposes or confirms the closure of the contract.
     *      Can be called by the contract owner or the fee recipient.
     *      Closure is executed only if both parties agree.
     */
    function close() public virtual notClosed assetDeposited {
        if (msg.sender != owner() && msg.sender != _assetFeeRecipient) {
            revert Unauthorized();
        }

        // Check if the closure has already been proposed by the other party
        if (_closeProposed && _closeProposer != msg.sender) {
            // Both parties have agreed to close the contract, proceed with closure
            _closed = true;
            _closeProposed = false; // Reset the proposal state
            _assetNft.safeTransferFrom(
                address(this),
                _assetFeeRecipient,
                _assetNftId
            );
            emit FeeRecipientClosed(_assetFeeRecipient);
        } else {
            // Proposal step: set the state to indicate closure has been proposed by the caller
            _closeProposed = true;
            _closeProposer = msg.sender;
            emit CloseProposed(msg.sender);
        }
    }

    /**
     * @dev Deposits the asset NFT
     *
     * @param assetNft_  address of the asset NFT
     * @param assetNftId_  id of the asset NFT
     * @param assetFeeRecipient_  address to receive tokens deposited for asset NFT
     */
    function depositAssetNft(
        address assetNft_,
        uint256 assetNftId_,
        address assetFeeRecipient_,
        address erc20Token_,
        uint256 tokenAmount_,
        uint256 maxSupply_
    ) public onlyOwner {
        IERC721(assetNft_).safeTransferFrom(
            msg.sender,
            address(this),
            assetNftId_
        );
        _assetNft = IERC721(assetNft_);
        _assetNftId = assetNftId_;
        _assetFeeRecipient = assetFeeRecipient_;
        _erc20Token = IERC20(erc20Token_);
        _tokenAmount = tokenAmount_;
        _maxSupply = maxSupply_;
        _mint(address(this), MFT, maxSupply_, "");
        emit AssetDeposited(assetNft_, assetNftId_);
    }

    function editAssetNft(
        address assetNft_,
        uint256 assetNftId_,
        address assetFeeRecipient_,
        address erc20Token_,
        uint256 tokenAmount_,
        uint256 maxSupply_
    ) public onlyOwner assetDeposited {
        if (balanceOf(address(this), MFT) != _maxSupply)
            revert CannotModifyWhileMFTsWrapped();
        // if already has an asset NFT, transfer it back to the owner
        if (address(_assetNft) != address(0)) {
            _assetNft.safeTransferFrom(address(this), msg.sender, _assetNftId);
        }
        _assetNft = IERC721(assetNft_);
        _assetNftId = assetNftId_;
        _assetFeeRecipient = assetFeeRecipient_;
        _assetNft.safeTransferFrom(msg.sender, address(this), assetNftId_);
        _erc20Token = IERC20(erc20Token_);
        _tokenAmount = tokenAmount_;
        _maxSupply = maxSupply_;
        if (maxSupply_ > _maxSupply) {
            _mint(address(this), MFT, maxSupply_ - _maxSupply, "");
        } else if (maxSupply_ < _maxSupply) {
            _burn(address(this), MFT, _maxSupply - maxSupply_);
        }
        _closed = false;
        emit AssetEdited(assetNft_, assetNftId_);
    }

    /**
     * @dev Returns the address of the deposit token
     */
    function erc20Token() public view virtual returns (address) {
        return address(_erc20Token);
    }

    /**
     * @dev Returns the address of the fee recipient
     */
    function feeRecipient() public view virtual returns (address) {
        return _feeRecipient;
    }

    /**
     * @dev Returns if the asset NFT is deposited
     */
    function isAssetNftDeposited() public view virtual returns (bool) {
        return address(_assetNft) != address(0);
    }

    /**
     * @dev Returns if the contract is closed
     *      If a user unwraps all MFTs, the contract is closed
     */
    function isClosed() public view virtual returns (bool) {
        return _closed;
    }

    /**
     * @dev Returns the max supply of MFTs
     */
    function maxSupply() public view virtual returns (uint256) {
        return _maxSupply;
    }

    /**
     * @dev Returns the name of the token
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * @dev Sets the base URI
     *
     * @param newURI_  new base URI
     */
    function setURI(string memory newURI_) public virtual onlyOwner {
        _baseURI = newURI_;
    }

    /**
     * @dev Returns the URI
     *
     * @param interfaceId_ interface id
     */
    function supportsInterface(
        bytes4 interfaceId_
    ) public view virtual override(ERC1155, ERC1155Receiver) returns (bool) {
        return super.supportsInterface(interfaceId_);
    }

    /**
     * @dev Returns the symbol of the token
     */
    function onERC721Received(
        address operator,
        address from,
        uint256 tokenId,
        bytes calldata data
    ) external returns (bytes4) {
        return 0x150b7a02;
    }

    /**
     * @dev Returns the symbol of the token
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the tax rate
     */
    function taxRate() public view virtual returns (uint256) {
        return _taxRate;
    }

    /**
     * @dev Returns the amount of deposit token to wrap
     */
    function tokenAmount() public view virtual returns (uint256) {
        return _tokenAmount;
    }

    /**
     * @dev Returns the total supply of MFTs
     */
    function totalSupply() public view virtual returns (uint256) {
        return _maxSupply;
    }

    /**
     * @dev Unwraps the MFTs to receive the deposit token
     *
     * @param amount_ amount of MFTs to unwrap
     */
    function unwrap(
        uint256 amount_
    ) public virtual assetDeposited nonReentrant {
        if (amount_ == 0) revert UnwrapZeroAmount();
        // if the user unwraps all MFTs, the contract is closed and user receives the asset NFT
        if (amount_ == maxSupply() && !_closed) {
            _closed = true;
            // transfer token balance to fee recipient
            _erc20Token.transfer(
                _assetFeeRecipient,
                _erc20Token.balanceOf(address(this))
            );
            // transfer asset NFT to user
            _assetNft.safeTransferFrom(address(this), msg.sender, _assetNftId);
            emit AssetRedeemed(msg.sender, _assetNftId);
        } else {
            // unwrap cooldown
            if (block.timestamp - _lastUnwrapTimestamp[msg.sender] < 5 minutes)
                revert WaitBeforeUnwrappingAgain();
            _lastUnwrapTimestamp[msg.sender] = block.timestamp;

            // send MFTs to contract
            safeTransferFrom(msg.sender, address(this), MFT, amount_, "");
            // computer after-tax token amount
            uint256 tokenAmt = _tokenAmountAfterTaxes * amount_;
            // ensure no issues with token amount
            tokenAmt = tokenAmt > _erc20Token.balanceOf(address(this))
                ? _erc20Token.balanceOf(address(this))
                : tokenAmt;
            // send deposit token to user
            _erc20Token.transfer(msg.sender, tokenAmt);
        }
        emit Unwrap(msg.sender, amount_);
    }

    /**
     * @dev Wraps the deposit token to receive MFTs
     *
     * @param amount_ amount of MFTs to wrap
     */
    function wrap(
        uint256 amount_
    ) public virtual assetDeposited notClosed nonReentrant {
        if (amount_ == 0) revert WrapZeroAmount();
        if (amount_ > balanceOf(address(this), MFT))
            revert InsufficientMFTsToWrap();
        // send after-tax deposit token to contract
        _erc20Token.transferFrom(
            msg.sender,
            address(this),
            amount_ * _tokenAmountAfterTaxes
        );
        // send tax fee to fee recipient
        _erc20Token.transferFrom(
            msg.sender,
            _feeRecipient,
            amount_ * _tokenTaxAmount
        );

        if (amount_ == _maxSupply) {
            _closed = true;
            _assetNft.safeTransferFrom(address(this), msg.sender, _assetNftId);
            emit AssetRedeemed(msg.sender, _assetNftId);
        } else {
            _safeTransferFrom(address(this), msg.sender, MFT, amount_, "");
        }
        emit Wrap(msg.sender, amount_);
    }

    /**
     * @dev Returns the URI
     *
     * @param tokenId_ token id
     */
    function uri(
        uint256 tokenId_
    ) public view override returns (string memory) {
        return _baseURI;
    }
}
"
    },
