abstract contract ERC69Errors {
    error ExceedsMaxSupply();
    error InsufficientMFTsToWrap();
    error InvalidTokenId();
    error UnwrapZeroAmount();
    error WaitBeforeUnwrappingAgain();
    error WrapZeroAmount();
}
"
