pragma solidity ^0.8.0;





contract MFLIXSale is Ownable,ReentrancyGuard {
    mapping(address => uint256) private _balances;
    address public admin;
    address private mcubeAddress;
    address private daiTokenAddress;
    address private usdtTokenAddress;
    uint256 private _totalSold;
    uint256 private _preTotalSold;
    uint256 private _icoTotalSold;
    uint256 private pricePerDaiToken;
    uint256 private pricePerUsdtToken;
    uint256 private pricePerETH;
    uint256 private presaleAmount;
    uint256 private icosaleAmount;

    mapping(address => bool) whitelistedAddresses;
    mapping(address => bool) adminAddresses;

    uint256 public presaleStartTimestamp;
    uint256 public presaleEndTimestamp;
    uint256 public presalePriceDAI;
    uint256 public presalePriceUSDT;
    uint256 public presalePriceETH;

    constructor(address _admin,address _MFLIXAddress,address _daiTokenAddress,address _usdtTokenAddress,uint256 _pricePerDaiToken,uint256 _pricePerUsdtToken,uint256 _pricePerETH, uint256 _presaleAmount, uint256 _icosaleAmount) {
        admin = _admin;
        mcubeAddress = _MFLIXAddress;
        daiTokenAddress = _daiTokenAddress;
        usdtTokenAddress =_usdtTokenAddress;
        pricePerDaiToken = _pricePerDaiToken;
        pricePerUsdtToken = _pricePerUsdtToken;
        pricePerETH = _pricePerETH;
        presaleAmount = _presaleAmount;
        icosaleAmount = _icosaleAmount;
    }

    function balanceOf(address account) public view returns (uint256) {
      return _balances[account];
    }

    function setPriceDaiToken(uint256 _price) public onlyAdmin(){
      require(_price >0,"price should be greater than Zero.");
      pricePerDaiToken = _price;
    }

    function setPriceUsdtToken(uint256 _price) public onlyAdmin{
      require(_price >0,"price should be greater than Zero.");
     pricePerUsdtToken = _price;
    }

    function getPriceDaiToken() public view virtual returns (uint256){
      return pricePerDaiToken;
    }
    
    function getPriceUsdtToken() public view virtual returns (uint256){
      return pricePerUsdtToken;
    }

    function getPriceETH() public view virtual returns (uint256){
      return pricePerETH;
    }

    function setPriceETH(uint256 _price) public onlyAdmin{
      require(_price >0,"price should be greater than Zero.");
      pricePerETH = _price;
    }

    function getpresaleAmount () public view virtual returns (uint256){
        return presaleAmount;
    }

    function geticosaleAmount () public view virtual returns (uint256){
        return icosaleAmount;
    }

    function setpresaleAmount (uint256 _setpresaleAmount) public onlyAdmin(){
        presaleAmount = _setpresaleAmount;
    }

    function seticosaleAmount (uint256 _seticosaleAmount) public onlyAdmin(){
        icosaleAmount = _seticosaleAmount;
    }

    function totalSold () public view virtual returns (uint256){
        return _totalSold;
    }   

    function preTotalSold () public view virtual returns (uint256){
        return _preTotalSold;
    }

    function icoTotalSold () public view virtual returns (uint256){
        return _icoTotalSold;
    }

    function addAdminUser(address[] memory _addressAdmin) public onlyOwner {
        uint size = _addressAdmin.length;
        for(uint256 i=0; i< size; i++){
            address adminUser = _addressAdmin[i];
            adminAddresses[adminUser] = true;
        }
    }

    function removeAdminUser(address[] memory _addressAdmin) external onlyOwner {
      uint size = _addressAdmin.length;
        for(uint256 i=0; i< size; i++){
            address adminUser = _addressAdmin[i];
            adminAddresses[adminUser] = false;
        }
    }

    function verifyAdmin(address _addressAdmin) public view returns(bool) {
      bool adminIsWhitelisted = adminAddresses[_addressAdmin];
      return adminIsWhitelisted;
    }

    modifier onlyAdmin() {
      require(adminAddresses[msg.sender], "You are not a Admin.");
      _;
    }

    function setPreSaleToken (uint256 _presaleStartTimestamp, uint256 _presaleEndTimestamp,uint256 _presalePriceDAI,uint256 _presalePriceUSDT,uint256 _presalePriceETH) public onlyAdmin(){  
      require(_presalePriceDAI > 0,"DAI token price should be greater than Zero.");
      require(_presalePriceUSDT > 0,"USDT token price should be greater than Zero.");
      require(_presalePriceETH > 0,"ETH price should be greater than Zero.");
      presaleStartTimestamp = _presaleStartTimestamp;
      presaleEndTimestamp = _presaleEndTimestamp;
      presalePriceDAI = _presalePriceDAI;
      presalePriceUSDT = _presalePriceUSDT;
      presalePriceETH = _presalePriceETH;
    }

    function presaleBuyTokenWithDAI(uint256 amount) public onlyWhitelisted{  
      require(presaleStartTimestamp <= block.timestamp, "Presale starttime is over");
      require(presaleEndTimestamp >= block.timestamp, "Presale endtime is over");
      require(amount >= presalePriceDAI,"Amount should be greater than Price.");
      SafeERC20.safeTransferFrom(IERC20(daiTokenAddress),msg.sender, admin, amount);
      uint256 tokenAmount = amount / presalePriceDAI;
      require(presaleAmount >= tokenAmount,"Presale amount token is over."); 
      _totalSold += tokenAmount;
      _preTotalSold += tokenAmount;
      SafeERC20.safeTransfer(IERC20(mcubeAddress),msg.sender, tokenAmount * 10**uint(18));
    }

    function presaleBuyTokenWithUsdt(uint256 amount) public onlyWhitelisted{  
      require(presaleStartTimestamp <= block.timestamp, "Presale starttime is over");
      require(presaleEndTimestamp >= block.timestamp, "Presale endtime is over");
      require(amount >= presalePriceUSDT,"Amount should be greater than Price.");
      SafeERC20.safeTransferFrom(IERC20(usdtTokenAddress),msg.sender, admin, amount);
      uint256 tokenAmount = amount / presalePriceUSDT;
      require(presaleAmount >= tokenAmount,"Presale amount token is over."); 
      _totalSold += tokenAmount;
      _preTotalSold += tokenAmount;
      SafeERC20.safeTransfer(IERC20(mcubeAddress),msg.sender, tokenAmount * 10**uint(18));
    }


    function preSaleBuyTokenWithETH() external payable onlyWhitelisted returns(uint256 amount){
      return preSaleTransferETH(msg.value,msg.sender);
    } 

    function preSaleTransferETH(uint256 _amount, address _sender) internal returns(uint256 _txAmount){
      require(presaleStartTimestamp <= block.timestamp, "Presale starttime is over");
      require(presaleEndTimestamp >= block.timestamp, "Presale endtime is over");
      require(_amount >0,"Amount should be greater than Zero.");
      require(_amount >= presalePriceETH,"Amount should be greater than minimum Amount.");
      uint256 tokenBuyAmount = _amount / presalePriceETH;

        // checking the net balance
      uint256 netBalance = IERC20(mcubeAddress).balanceOf(address(this));
      require(netBalance > tokenBuyAmount * 10**uint(18),"Insufficient token balance.");
      require(presaleAmount >= tokenBuyAmount * 10**uint(18),"Pre Sale amount token is over."); 

      _totalSold += tokenBuyAmount;
      _preTotalSold += tokenBuyAmount;
        //transferring the required amount to sender
      /* bool transactionStatus = IERC20(mcubeAddress).safeTransfer(_sender, tokenBuyAmount * 10**uint(18));
      require(transactionStatus,"Transaction failed."); */

      SafeERC20.safeTransfer(IERC20(mcubeAddress),_sender, tokenBuyAmount * 10**uint(18));
       
        // //transferring eth to admin
      require(address(this).balance >= _amount,"Transaction balace has not replicated yet.");        
      payable(admin).transfer(_amount);
      return tokenBuyAmount;
    }

    function buyTokenWithDAI (uint256 amount) public onlyWhitelisted{  
      require(amount>=pricePerDaiToken,"Amount should be greater than Price.");  
      SafeERC20.safeTransferFrom(IERC20(daiTokenAddress),msg.sender,admin,amount);
      uint256 tokenAmount = amount / pricePerDaiToken;
      require(icosaleAmount >= tokenAmount,"ICO sale amount token is over."); 
      _totalSold += tokenAmount;
      _icoTotalSold += tokenAmount;
      SafeERC20.safeTransfer(IERC20(mcubeAddress),msg.sender,tokenAmount * 10**uint(18));
    }

    function buyTokenWithUSDT (uint256 amount) public onlyWhitelisted{  
      require(amount>=pricePerUsdtToken,"Amount should be greater than Price.");  
      SafeERC20.safeTransferFrom(IERC20(usdtTokenAddress),msg.sender,admin,amount);
      uint256 tokenAmount = amount / pricePerUsdtToken;
      require(icosaleAmount >= tokenAmount,"ICO sale amount token is over."); 
      _totalSold += tokenAmount;
      _icoTotalSold += tokenAmount;
      SafeERC20.safeTransfer(IERC20(mcubeAddress),msg.sender,tokenAmount * 10**uint(18));
    }
    
    function buyTokenWithETH() external payable onlyWhitelisted returns(uint256 amount){
      return transferETH(msg.value,msg.sender);
    }

    function transferETH(uint256 _amount, address _sender) internal returns(uint256 _txAmount){
      require(_amount >0,"Amount should be greater than Zero.");
      require(_amount >= pricePerETH,"Amount should be greater than minimum Amount.");
      uint tokenBuyAmount = _amount / pricePerETH;

        // checking the net balance
      uint netBalance = IERC20(mcubeAddress).balanceOf(address(this));
      require(netBalance > tokenBuyAmount,"Insufficient token balance.");
      require(icosaleAmount >= tokenBuyAmount * 10**18,"ICO sale amount token is over."); 

      _totalSold += tokenBuyAmount;
      _icoTotalSold += tokenBuyAmount;
      //transferring the required amount to sender
      /* bool transactionStatus = IERC20(mcubeAddress).safeTransfer(_sender, tokenBuyAmount * 10**uint(18));
      require(transactionStatus,"Transaction failed."); */
      SafeERC20.safeTransfer(IERC20(mcubeAddress),_sender, tokenBuyAmount * 10**uint(18));
       
        // //transferring eth to masterAccountAddess
      require(address(this).balance >= _amount,"Transaction balace has not replicated yet.");        
      payable(admin).transfer(_amount);

      return tokenBuyAmount;
    }


    function withdrawToken (uint256 tokenAmount) external onlyOwner{   
        SafeERC20.safeTransfer(IERC20(mcubeAddress),msg.sender,tokenAmount);  
    }

    modifier isWhitelisted(address _address) {
      require(whitelistedAddresses[_address], "Whitelist: You need to be whitelisted");
      _;
    }

    modifier onlyWhitelisted() {
      require(whitelistedAddresses[msg.sender], "Whitelist: You need to be whitelisted");
      _;
    }

    function addWhitelistUser(address _addressToWhitelist) public onlyAdmin {
      whitelistedAddresses[_addressToWhitelist] = true;
    }
    
    function removeWhitelistUser(address whiteListedAddress) public onlyAdmin {
      whitelistedAddresses[whiteListedAddress] = false;
    }

    function verifyUser(address _whitelistedAddress) public view returns(bool) {
      bool userIsWhitelisted = whitelistedAddresses[_whitelistedAddress];
      return userIsWhitelisted;
    }

    function batchWhitelist(address[] memory _addressToWhitelist) public onlyAdmin {
      uint size = _addressToWhitelist.length;
    
      for(uint256 i=0; i< size; i++){
          address user = _addressToWhitelist[i];
          whitelistedAddresses[user] = true;
      }
 }

}
