    "contracts/EthscriptionCreator.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract EthscriptionCreator {
    uint256 public totalSupply;
    uint public constant esip6StartBlock = 18320000;
    uint public constant mintEndBlock = esip6StartBlock + (24 hours / 12);
    uint private nonce = 0;

    event ethscriptions_protocol_CreateEthscription(
        address indexed initialOwner,
        string contentURI
    );

    function random() internal view returns (uint) {
        return uint(keccak256(abi.encodePacked(block.timestamp, msg.sender, totalSupply, blockhash(block.number - 1)))) % 1000;
    }

    function createEthscription() public {
        require(block.number >= esip6StartBlock, "Not yet started");
        require(block.number <= mintEndBlock, "It's over");

        totalSupply++;

        emit ethscriptions_protocol_CreateEthscription(
            msg.sender,
            string.concat(
                'data:image/svg+xml;rule=esip6,<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" viewBox="0 0 1000 1000" style="background-color:%23',
                random() == 1 ? "EABD00" : "FFFFFF",
                '"><image height="1000" width="1000" xlink:href="data:image/svg+xml;base64," /></svg>'
            )
        );
    }
}"
    }
