pragma solidity ^0.8.20;
pragma experimental ABIEncoderV2;

import '../external/IUniswapV2Pair.sol';
import "../token/IToken.sol";
import "../token/ITickets.sol";
import "../external/Decimal.sol";
import "../Constants.sol";

contract Account {
    enum Status {
        Frozen,
        Fluid,
        Locked
    }

    struct State {
        uint256 staged;
        uint256 balance;
        uint256 fluidUntil;
        uint256 lockedUntil;
    }
}

contract Epoch {
    struct Global {
        uint256 current;
    }

    struct TicketRage {
        uint256 start;
        uint256 end;
    }

    struct State {
        uint256 bonded;

        uint256 prizePerTicket;
        uint256[] winningTickets;

        mapping(address => TicketRage) userTicketRange;
        mapping(address => bool) userPrizeClaimed;

        uint chainLinkRequestId;
        bool drawExecuted;
    }
}

contract Candidate {
    enum Vote {
        UNDECIDED,
        APPROVE,
        REJECT
    }

    struct State {
        uint256 start;
        uint256 period;
        uint256 approve;
        uint256 reject;
        mapping(address => Vote) votes;
        bool initialized;
    }
}

contract Storage {
    struct Provider {
        IToken token;
        ITickets tickets;
    }

    struct Balance {
        uint256 supply;
        uint256 bonded;
        uint256 staged;
        uint256 userUSDCClaims;
    }

    struct State {
        Epoch.Global epoch;
        Balance balance;
        Provider provider;

        mapping(address => Account.State) accounts;
        mapping(uint256 => Epoch.State) epochs;
        mapping(uint256 => uint256) chainlinkRequestIds; // req id to epoch mapping
        mapping(address => Candidate.State) candidates;
    }
}

contract State is Constants {
    Storage.State _state;
}
"
