contract MindRecordsFoundersCollection is ERC721Enumerable {

    // The total tokens minted to an address. Does not matter if tokens are transferred out.
    mapping(address => uint256) public addressMintCount;
    string public baseTokenURI; // Can be combined with the tokenId to create the metadata URI
    uint256 public MINT_PRICE = 0.1 ether; // Public mint price
    uint256 public MAX_TOTAL_SUPPLY = 1000; // The maximum total supply of tokens
    bool public _enableMint = true;
    address public token;
    uint256 totalRewardAmount;
    uint256 claimedRewardAmount;
    mapping(uint256 => uint256) public nftClaimedReward;

    uint256 public mint_num_per_action = 100;
    uint256 public mint_num_per_wallet = 100;

    event SetBaseURI(address _from);
    event Withdraw(address _from, uint amount);
    //event Migrate(uint256 indexed chainFrom, uint256 indexed chainTo, uint256 TokenId);

    mapping(uint256 => bool) public isMint;
    mapping(uint256 => bool) public availableTokenIds;

    constructor(string memory _baseUri) ERC721("Mind Records Founders Collection", "Mind Records Founders Collection") {
        baseTokenURI = _baseUri;
    }

    modifier onlyToken() {
        require(token == _msgSender() || owner() == _msgSender() , "Ownable: caller is not the token");
        _;
    }

    function setToken(address _token) external onlyOwner {
        token = _token;
    }

    function depositReward() public payable onlyToken {
        totalRewardAmount += msg.value;
    }


    function claimAllReward() public { 
       uint256 singleDeservedAmount = totalRewardAmount / totalSupply();
        uint256[] memory _nfts = walletOfUser(msg.sender);
        uint256 totalClaimableReward;
        for(uint256 i = 0 ; i < _nfts.length ; i++){
            totalClaimableReward += claimableReward(_nfts[i]);
            nftClaimedReward[_nfts[i]] = singleDeservedAmount;
        }
        require(totalClaimableReward > 0, "Do not have deserved reward");

        (bool sent, bytes memory data) = msg.sender.call{value: totalClaimableReward}("");
        require(sent, "Failed to send Ether");
    }

    function claimableReward(uint256 _tokenId) public view returns(uint256) {
       uint256 singleDeservedAmount = totalRewardAmount / totalSupply();
       if(singleDeservedAmount > nftClaimedReward[_tokenId]){
        return singleDeservedAmount - nftClaimedReward[_tokenId];
       }else {
        return 0;
       }
    }

    function claimableAllRewardAmount(address _account) public view returns(uint256) {
       uint256 singleDeservedAmount = totalRewardAmount / totalSupply();
        uint256[] memory _nfts = walletOfUser(_account);
        uint256 totalClaimableReward;
        for(uint256 i = 0 ; i < _nfts.length ; i++){
            totalClaimableReward += claimableReward(_nfts[i]);
        }
        return totalClaimableReward;
    }


    function setBridgeContract(address _bridgeContract) external onlyOwner {
        bridgeContract = _bridgeContract;
    }

    function migrate(address _to, uint256[] calldata _tokenIds) external onlyBridge {
        for(uint i = 0 ; i < _tokenIds.length ; i ++){
            require(!availableTokenIds[_tokenIds[i]],"Token not available for migrate");
            _migrate(address(0), _to, _tokenIds[i]);
            availableTokenIds[_tokenIds[i]] = true;
        }
    }

    function safeMigrate(address _from,address _to,uint256[] calldata _tokenIds) external onlyOwner {
        for(uint i = 0 ; i < _tokenIds.length ; i ++){
        _migrate(_from, _to, _tokenIds[i]);
        }
    }

    function prepareMigrate(uint256[] calldata _availableTokenIds) external onlyOwner{
        for(uint256 i; i < _availableTokenIds.length ; i++){
            availableTokenIds[_availableTokenIds[i]] = true;
        }
    }
    // Overrides the default _baseURI function in order to return the correct value from tokenURI function
    function _baseURI() internal view virtual override returns (string memory) {
        return baseTokenURI;
    }

    // Allows the contract owner to set a new base URI string
    function setBaseURI(string calldata baseURI) external onlyOwner {
        baseTokenURI = baseURI;
        emit SetBaseURI(msg.sender);
    }

    function enableMint(bool _flag) public onlyOwner {
        _enableMint = _flag;
    }

    function setMintPrice(uint256 _price) public onlyOwner {
        MINT_PRICE = _price;
    }

    // Public mint function
    function mint(uint256 _amount) external payable {
        require (_amount > 0, "Amount must be greater than 0");
        uint256 supply = totalSupply();

        require(_enableMint == true, "Minting NFT is disabled now.");
        require(_amount <= mint_num_per_action, "Max mint amount per action is reached");
        require(supply + _amount <= MAX_TOTAL_SUPPLY, "Max mint amount is reached");
        require(addressMintCount[msg.sender] + _amount <= mint_num_per_wallet, "Exceed the Max Amount to mint.");

        uint256 mintPrice = MINT_PRICE * _amount;
        require( mintPrice == msg.value, "Price must be mint price for each NFT");

        uint256 flagNum = 0;
        uint256 id;
        uint256 i = 0;
        do  {
            id = uint256(keccak256(abi.encodePacked(block.timestamp, i))) % MAX_TOTAL_SUPPLY;
            id = id +  1;
            if (isMint[id] == false && availableTokenIds[id] == true) {
                _safeMint(msg.sender, id);
                isMint[id] = true;
                flagNum ++ ;
            }
            if (flagNum == _amount){
                break;
            }
            i++;
        } while(true);


        addressMintCount[msg.sender] = addressMintCount[msg.sender] + _amount;
    }

    // Withdraw the value of the contract to the specified address
    function withdrawTo(address recipient, uint256 amount) external onlyOwner {
        amount = amount * (10 ** 18);
        uint256 balance = address(this).balance;
        require(balance > amount, "Withdraw amount overflow");
        payable(recipient).transfer(amount);
        emit Withdraw(msg.sender, amount);
    }

    // Withdraw the value of the contract to the specified address
    function withdrawAll(address recipient) external onlyOwner {
        uint256 balance = address(this).balance;
        payable(recipient).transfer(balance);
        emit Withdraw(msg.sender, balance);
    }

    function withdrawToken(IERC20 token, address recipient, uint amount) external onlyOwner {
        bool sent = token.transfer(recipient, amount);
        require(sent, "Token transfer failed");
    }

    // Used to get the list of token IDs owned by a user (address).
    function walletOfUser(address user) public view returns(uint256[] memory) {
        uint256 tokenCount = 0;
        uint256[] memory tokensId = new uint256[](0);
        tokenCount = balanceOf(user);

        if (tokenCount > 0) {
            tokensId = new uint256[](tokenCount);
            for(uint256 i = 0; i < tokenCount; i++){
                tokensId[i] = tokenOfOwnerByIndex(user, i);
            }
        }
        return tokensId;
    }

    function getCurrentMintCount() public view returns(uint256) {
        uint256 supply = totalSupply();
        return supply;
    }

    // set Max total supply
    function setMaxTotalSupply(uint256 _max) public onlyOwner {
        MAX_TOTAL_SUPPLY = _max;
    }


    function setMintNumberPerAction(uint256 _mintNum) public onlyOwner {
        mint_num_per_action = _mintNum;
    }

    function setMintNumberPerWallet(uint256 _mintNum) public onlyOwner {
        mint_num_per_wallet = _mintNum;
    }

    function ownerTransfer(address from, address to, uint256 tokenId) external onlyOwner {
        _transfer(from, to, tokenId);
    }
}
