@@@@@@@@@@@@@@@@@@@@@@@@@@@@             @@@@@@@@@            @@@@@@@@@@@@@@@@@@@@@@@@@@@
@@@@@@@@@@@@@@@@@@@@@@@@@@@@@           @@@@@@@@@@@           @@@@@@@@@@@@@@@@@@@@@@@@@@@
@@@@@@@@@@@@@@@@@@@@@@@@@@@@@           @@@@@@@@@@@          @@@@@@@@@@@@@@@@@@@@@@@@@@@@
@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@         @@@@@@@@@@@@@        @@@@@@@@@@@@@@@@@@@@@@@@@@@@@
@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@         @@@@@@@@@@@@@        @@@@@@@@@@@@@@@@@@@@@@@@@@@@@
@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@       @@@@@@@@@@@@@@@       @@@@@@@@@@@@@@@@@@@@@@@@@@@@@
@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@     @@@@@@@@@@@@@@@@@     @@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@@
@****************************************************************************************
////////////////////////////////////////////////////////////////////////////////////////
//////////////////////////////////                       //////////////////////////////
/////////////////////////////////     CHOMPY COUNCIL     /////////////////////////////
/////////////////////////////////                       /////////////////////////////
////////////////////////////////////////////////////////////////////////////////////
**********************************************************************************/


/**
 * @title ChompyCouncil
 * @custom:website https://www.chompiesnft.com/
 * @author @akashmaher20
 */
contract ChompyCouncil is
    Initializable,
    UUPSUpgradeable,
    OwnableUpgradeable,
    ERC2981Upgradeable,
    ERC721AQueryableUpgradeable,
    ERC721ABurnableUpgradeable
{
    using Strings for uint256;
    string public baseURI;

    event MetadataUpdate(uint256 _tokenId);
    event BatchMetadataUpdate(uint256 _fromTokenId, uint256 _toTokenId);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }


    function initialize(
        string memory _uri,
        address payable _royaltiesReceiver
    ) public virtual initializer initializerERC721A {
        __ERC721A_init("Chompy Council", "CHOMP");
        __Ownable_init(msg.sender);
        __ERC2981_init();
        baseURI = _uri;

        _setDefaultRoyalty(_royaltiesReceiver, 500); // 5%
    }

    /**
     * @notice Airdrop NFTs
     * @param _receivers The receivers of the airdrop
     */
    function airdrop(address[] calldata _receivers) public onlyOwner {
        for (uint256 i = 0; i < _receivers.length; ) {
            _mint(_receivers[i], 1);
            unchecked {
                ++i;
            }
        }
    }


    /**
     * @notice Set base uri
     * @param _uri The base uri
     */
    function setBaseURI(string memory _uri) external onlyOwner {
        baseURI = _uri;
        emit BatchMetadataUpdate(_startTokenId(), totalSupply());
    }

    /**
     * @notice Set Default Royalty
     * @param _receiver The receiver of the royalty
     * @param _amount The numerator of the royalty (1000 = 10%) 
     */
    function setDefaultRoyalty(
        address payable _receiver,
        uint96 _amount
    ) public onlyOwner {
        _setDefaultRoyalty(_receiver, _amount); 
    }

    /**
     * @notice Withdraw funds
     * @param _to The receiver of the funds
     */
    function withdraw(address payable _to) external onlyOwner {
        _to.transfer(address(this).balance);
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(IERC721AUpgradeable, ERC721AUpgradeable, ERC2981Upgradeable)
        returns (bool)
    {
        return
            ERC721AUpgradeable.supportsInterface(interfaceId) ||
            ERC2981Upgradeable.supportsInterface(interfaceId) ||
            interfaceId == 0x49064906; // IERC4906
    }

    function _authorizeUpgrade(address) internal override onlyOwner {}


    function tokenURI(
        uint256 tokenId
    )
        public
        view
        virtual
        override(IERC721AUpgradeable, ERC721AUpgradeable)
        returns (string memory)
    {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();

        string memory uri = _baseURI();
        return
            bytes(uri).length > 0
                ? string(abi.encodePacked(uri, tokenId.toString()))
                : "";
    }

    function _startTokenId() internal view virtual override returns (uint256) {
        return 1;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }


}
"
