{
      using SafeMath for uint256;
      event SwapTokensForETH(uint256 amountIn, address[] path);
      event SwapAndLiquifyEnabledUpdated(bool enabled);
      bool inSwapAndLiquify;
      bool public swapAndLiquifyEnabled = true;
   
      modifier lockTheSwap {
         inSwapAndLiquify = true;
         _;
         inSwapAndLiquify = false;
      }

      mapping (address => uint256) private _balances;
      mapping (address => mapping (address => uint256)) private _allowances;
      mapping (address => bool) private _isExcludedFromFee;
      mapping (address => bool) private _isExcludedFromWhale;

      uint256 private _totalSupply;
      string private _name;
      string private _symbol;
      uint8 private _decimals;
      address payable public marketingAddress; 
      IUniswapV2Router02 public immutable uniswapV2Router;
      address public uniswapV2Pair;
      uint256 public sellMarketingFee;
      uint256 public buyMarketingFee;
      uint256 public _maxTxAmount;
      uint256 public minimumTokensBeforeSwap;


    constructor() 
    { 
      _name = "ShibariumX";
      _symbol = "SHIBX";
      _decimals = 9;

      _mint(msg.sender, 1000_000_000 * 10**9);

      IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
      uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
      .createPair(address(this), _uniswapV2Router.WETH());
      uniswapV2Router = _uniswapV2Router;  

      _maxTxAmount = 1000_000_0 * 10**9;
      minimumTokensBeforeSwap = 1000_0 * 10**9;  

      sellMarketingFee = 85;
      buyMarketingFee = 0;
      
      _isExcludedFromFee[owner()] = true;
      _isExcludedFromFee[address(this)] = true;

      marketingAddress = payable(0x0f9cAf351015b5a926a08e5ffAA0c145B1B2b511);
    }


    function isExcludedFromFee(address account) external view returns(bool) {
        return _isExcludedFromFee[account];
    }
    
    function excludeFromFee(address account) external onlyOwner {
        _isExcludedFromFee[account] = true;
    }
    
    function includeInFee(address account) external onlyOwner {
        _isExcludedFromFee[account] = false;
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless {_setupDecimals} is
     * called.
