contract Owned {
    address payable public owner;

    event OwnershipTransferred(address indexed _from, address indexed _to);

    constructor(address _OwnerAddress) {
        owner = payable(_OwnerAddress);
    }

    modifier onlyOwner() {
        require(msg.sender == owner);
        _;
    }

    function transferOwnership(address payable _newOwner) public onlyOwner {
        owner = _newOwner;
        emit OwnershipTransferred(msg.sender, _newOwner);
    }
}
interface XDSeaTrade {
    function getTokenID(uint256 count) external returns (uint256);
}

contract XDSea1155V1 is Owned, ERC1155 {
    using SafeMath for uint256;
    event Create(
        address indexed _to,
        uint256 indexed tokenId
    );
    uint256 public totalmint = 0;
    uint256 public _tid = 1;
    mapping(uint256 => address) public _creator;
    mapping(uint256 => uint256) public _royal;

    constructor(
        string memory name_,
        string memory symbol_,
        address _ownerAddress
    ) ERC1155("", name_, symbol_) Owned(_ownerAddress) {}
    
    function mint(string memory ipfsmetadata, address to, uint total, uint royal, uint256 id_) public {
        _mint(to, id_, total, "");
        _setTokenURI(id_, ipfsmetadata);
        _setTokenRoyalty(id_, to, uint96(royal.div(1e16)));
        _creator[id_] = to;
        _royal[id_]=royal;
    }
    
    function bulkMint(string[] memory ipfs, uint royal,uint256 id_, uint total) public {
        _tid = XDSeaTrade(owner).getTokenID(id_).sub(id_);
        for(uint i = 0; i < id_; i++){
            _tid = _tid.add(1);
            uint256 tokenId = _tid.add(block.timestamp);
            _mint(msg.sender, tokenId, total, "");
            _setTokenURI(tokenId, ipfs[i]);
            _setTokenRoyalty(tokenId, msg.sender, uint96(royal.div(1e16)));
            emit Create(msg.sender, tokenId);
        }
    }

    function getCreatorsAndRoyalty(uint256 tokenid) public view returns(address, uint256) {
        return (_creator[tokenid], _royal[tokenid]);
    } 
    function burnNFT(uint256 tokenId, uint256 amount) public{
        require(balanceOf(msg.sender, tokenId) == amount, "Not a Owner or balance Mismatch");
        _burn(msg.sender, tokenId, amount);
    }
    function changeCollectionOwner(address to) public {
        transferOwnership(payable(to));
    }
    
}
