pragma solidity ^0.8.0;

contract TreatBridge  {
    using SafeMath for uint256;
    
    address public owner;
    address public pendingOwner;
    address public controller;


    TokenBase public token;
    uint256 public _balance;
    uint256 public _tax;
    uint256 public _taxMaxValue;
    uint128 public _fee;


    enum Step { Burn, Mint }

    event Transfer(
        address from,
        address to,
        uint amount,
        uint date,
        Step indexed step
    );

    event Deposit(
        address indexed account,
        uint256 amount
    );

    event Withdraw(
        address indexed account,
        uint256 amount
    );

    event ControllerChanged(address indexed previousBridge, address indexed newBridge);
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    
    modifier onlyOwner() { require(msg.sender == owner, "BridgeBase: caller is not the owner"); _; }
    modifier onlyController() { require(msg.sender == controller, "TreatBridge: caller is not the bridge"); _; }

    constructor(address _token) {
        // Ensure that the _token address is not zero
        require(_token != address(0), "Bridge: token address cannot be 0");
        token = TokenBase(_token);
        
        owner = msg.sender;
        _tax = 0;
        _taxMaxValue = 0;
        
        // set _fee to 0.005 ether
        _fee = 5000000000000000;
    }

    function setFee(uint128 fee) external onlyOwner {
        _fee = fee;
    }

     function setController(address _bridge) external onlyOwner {
        controller = _bridge;
        emit ControllerChanged(controller, _bridge);
    }

    function transferOwnership(address _pendingOwner) external onlyOwner {
        pendingOwner = _pendingOwner;
    }

    function acceptOwnership() external {
        require(msg.sender == pendingOwner, "BridgeBase: caller is not the pending owner");
        emit OwnershipTransferred(owner, pendingOwner);
        owner = pendingOwner;
        pendingOwner = address(0);
    }

    function setToken (address _token) external onlyOwner {
        token = TokenBase(_token);
        
    }

    function setTax (uint256 tax) external onlyOwner {
        _tax = tax;
    }

    function setTaxMaxValue (uint256 taxMaxValue) external onlyOwner {
        _taxMaxValue = taxMaxValue;
    }

