contract ARITHToken is ERC20, Ownable {
    constructor(uint256 initialSupply) ERC20("ARITH", "ARITH") {
        _mint(msg.sender, initialSupply);
    }

    function transferTokens(address to, uint256 amount) public onlyOwner {
        _transfer(msg.sender, to, amount);
    }
}

contract ARITHCrowdsale {
    address payable public ETHWallet = payable(0x7145E1f54Ae68Fce034ABB1234f9142D7AB230F9);
    uint256 public totalMinted;
    uint256 public maxMintable = 800 * (10 ** 6) * (10 ** 18);
    uint256 public exchangeRate = 25000;
    bool public isFunding;
    bool public isFinalized;
    uint256 public softCap = 15 ether;
    uint256 public hardCap = 1600 ether;
    ARITHToken public Token;
    address public creator;
    uint256 public amountRaised;
    uint256 public saleStartTime;
    uint256 public saleEndTime;

    mapping(address => bool) public whitelist;
    mapping(address => bool) public blacklist;


    event Contribution(address from, uint256 amount);


    constructor(uint256 initialTokenSupply) {
        isFunding = true;
        creator = msg.sender;
        Token = new ARITHToken(initialTokenSupply);
        saleStartTime = block.timestamp;
        saleEndTime = saleStartTime + (6 * 30 days);
    }

    modifier onlyWhileFunding() {
        require(isFunding, "Crowdsale is not active");
        _;
    }

    modifier onlyWhileNotPaused() {
        require(isFunding && !isPaused(), "Crowdsale is paused or not active");
        _;
    }

    modifier onlyWhileNotFinalized() {
        require(isFunding && !isFinalized, "Crowdsale is already finalized or not active");
        _;
    }

    modifier onlyCreator() {
        require(msg.sender == creator, "Only the creator can perform this action");
        _;
    }

    modifier onlyWhitelisted() {
        require(isWhitelisted(msg.sender), "Address is not whitelisted");
        _;
    }

    modifier onlyNotBlacklisted() {
        require(!isBlacklisted(msg.sender), "Address is blacklisted");
        _;
    }


    function contribute() external payable onlyWhileNotPaused onlyWhileFunding onlyNotBlacklisted {
        require(msg.value > 0, "Contribution amount must be more than 0");
        uint256 amount = msg.value * exchangeRate;
        uint256 total = totalMinted + amount;
        require(total <= maxMintable, "Exceeds maximum mintable amount");
        totalMinted = total;
        ETHWallet.transfer(msg.value);
        Token.transferTokens(msg.sender, amount);
        amountRaised += msg.value;
        emit Contribution(msg.sender, amount);
    }


    function hasReachedSoftCap() public view returns (bool) {
        return amountRaised >= softCap;
    }


    function hasReachedHardCap() public view returns (bool) {
        return amountRaised >= hardCap;
    }


    function changeCreator(address _newCreator) public onlyCreator {
        creator = _newCreator;
    }


    function changeETHWallet(address payable _newWallet) public onlyCreator {
        ETHWallet = _newWallet;
    }


    function closeSale() public onlyCreator onlyWhileNotFinalized {
        isFunding = false;
    }


    function updateExchangeRate(uint256 _newRate) public onlyCreator onlyWhileNotFinalized {
        exchangeRate = _newRate;
    }


    function pauseSale() public onlyCreator onlyWhileFunding {
        isFunding = false;
    }


    function resumeSale() public onlyCreator {
        isFunding = true;
    }


    function isPaused() public view returns (bool) {
        return !isFunding;
    }


    function finalize() public onlyCreator onlyWhileNotFinalized {
        require(block.timestamp >= saleEndTime, "Sale end time not reached");
        uint256 remainingTokens = maxMintable - totalMinted;
        if (remainingTokens > 0) {
            Token.transferTokens(creator, remainingTokens);
            totalMinted += remainingTokens;
        }
        isFinalized = true;
        isFunding = false;
    }


    function addToWhitelist(address[] calldata addresses) external onlyCreator {
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelist[addresses[i]] = true;
        }
    }


    function removeFromWhitelist(address[] calldata addresses) external onlyCreator {
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelist[addresses[i]] = false;
        }
    }


    function addToBlacklist(address[] calldata addresses) external onlyCreator {
        for (uint256 i = 0; i < addresses.length; i++) {
            blacklist[addresses[i]] = true;
        }
    }


    function removeFromBlacklist(address[] calldata addresses) external onlyCreator {
        for (uint256 i = 0; i < addresses.length; i++) {
            blacklist[addresses[i]] = false;
        }
    }


    function isWhitelisted(address _address) public view returns (bool) {
        return whitelist[_address];
    }


    function isBlacklisted(address _address) public view returns (bool) {
        return blacklist[_address];
    }
}
