contract Ownable is Context {
  address private _owner;

  event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

  /**
   * @dev Initializes the contract setting the deployer as the initial owner.
   */
  constructor () internal {
    address msgSender = _msgSender();
    _owner = msgSender;
    emit OwnershipTransferred(address(0), msgSender);
  }

  /**
   * @dev Returns the address of the current owner.
   */
  function owner() public view returns (address) {
    return _owner;
  }

  /**
   * @dev Throws if called by any account other than the owner.
   */
  modifier onlyOwner() {
    require(_owner == _msgSender(), "Ownable: caller is not the owner");
    _;
  }

  /**
   * @dev Leaves the contract without owner. It will not be possible to call
   * `onlyOwner` functions anymore. Can only be called by the current owner.
   *
   * NOTE: Renouncing ownership will leave the contract without an owner,
   * thereby removing any functionality that is only available to the owner.
   */
  function renounceOwnership() public onlyOwner {
    emit OwnershipTransferred(_owner, address(0));
    _owner = address(0);
  }

  /**
   * @dev Transfers ownership of the contract to a new account (`newOwner`).
   * Can only be called by the current owner.
   */
  function transferOwnership(address newOwner) public onlyOwner {
    _transferOwnership(newOwner);
  }

  /**
   * @dev Transfers ownership of the contract to a new account (`newOwner`).
   */
  function _transferOwnership(address newOwner) internal {
    require(newOwner != address(0), "Ownable: new owner is the zero address");
    emit OwnershipTransferred(_owner, newOwner);
    _owner = newOwner;
  }
}

contract IBOXToken is Context, IERC20, Ownable {
  using SafeMath for uint256;

  mapping (address => bool) public _whitelist;
  mapping (address => bool) public _blackHole;

  mapping (address => uint256) private _balances;

  mapping (address => mapping (address => uint256)) private _allowances;

  uint256 private _totalSupply;
  uint8  public _decimals;
  string public _symbol;
  string public _name;

  address public  _devWallet; 
  address public  uniswapV2Pair;
  uint256 public oneWeek = 7 days;

  uint256 public startTime = 1692451800;

  bool public robotKill = true;
  uint256 public RELEASE_AMOUNT = 10000 *10**18;

    struct UserOLD {
        bool ISOLD;       
        uint256 tokenAmount; 
    }

 mapping (address => UserOLD) public userTokenOLD;

  constructor() public {
    _name = "IBOX TOKEN";
    _symbol = "IBOX";
    _decimals = 18;
    _totalSupply = 8000000000 * 10**18;
    _balances[msg.sender] = _totalSupply;
    _devWallet = msg.sender;
    emit Transfer(address(0), msg.sender, _totalSupply);
  }


  function setwhitelist(address _addr,bool _is) public onlyOwner() {
           _whitelist[_addr] = _is;
  }

  function setblackHole(address[] memory  _addr,bool _is) public onlyOwner() {
          for (uint256 i = 0; i < _addr.length; i++) {
               _blackHole[_addr[i]] = _is;
          }
  }
  /**
   * @dev Returns the bep token owner.
   */
  function getOwner() external view returns (address) {
    return owner();
  }

  /**
   * @dev Returns the token decimals.
   */
  function decimals() external view returns (uint8) {
    return _decimals;
  }

  /**
   * @dev Returns the token symbol.
   */
  function symbol() external view returns (string memory) {
    return _symbol;
  }

  /**
  * @dev Returns the token name.
  */
  function name() external view returns (string memory) {
    return _name;
  }

  /**
   * @dev See {ERC20-totalSupply}.
   */
  function totalSupply() external view returns (uint256) {
    return _totalSupply;
  }



  /**
   * @dev See {ERC20-balanceOf}.
   */
  function balanceOf(address account) public view returns (uint256) {
    return _balances[account];
  }


  function transfer(address recipient, uint256 amount) external returns (bool) {
    _transfer(_msgSender(), recipient, amount);
    return true;
  }

  function allowance(address owner, address spender) external view returns (uint256) {
    return _allowances[owner][spender];
  }


  function approve(address spender, uint256 amount) external returns (bool) {
    _approve(_msgSender(), spender, amount);
    return true;
  }


  function transferFrom(address sender, address recipient, uint256 amount) external returns (bool) {
    _transfer(sender, recipient, amount);
    _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
    return true;
  }

  function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
    return true;
  }

  function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
    return true;
  }

  function _transfer(address sender, address recipient, uint256 amount) internal {
    require(sender != address(0), "ERC20: transfer from the zero address");
    require(recipient != address(0), "ERC20: transfer to the zero address");
    require(!_blackHole[sender], "ERC20: Problem with address");

    UserOLD storage user = userTokenOLD[sender];

    if  (user.ISOLD){
        uint256 freedAmount;
        if ( block.timestamp < startTime) {
            freedAmount = 0;
        }else {
        uint256 freedCount = (block.timestamp - startTime) / oneWeek;
        freedAmount =  freedCount * RELEASE_AMOUNT;
        }
        if (user.tokenAmount > freedAmount ){
            uint256 NoFreedAmount = user.tokenAmount - freedAmount;
             uint256 userAmount = balanceOf(sender);
            
            require(userAmount >= NoFreedAmount + amount, "ERC20: Not enough releases");
        }
    }
        if (_whitelist[sender]){
            _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount);
        }else{
          if (recipient == uniswapV2Pair && robotKill ){
            _transferSellExcluded(sender,recipient,amount);
          }else {
            _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount);
         }
        }
 }

  function getUserFreedAmount () public view returns (uint256) {
        uint256 freedAmount; 
         
        if ( block.timestamp < startTime) {
            freedAmount = 0;
        }else {
        uint256 freedCount = (block.timestamp - startTime ) / oneWeek;
        freedAmount =  freedCount * RELEASE_AMOUNT ;
        } 
        return freedAmount;

  }

    function _transferSellExcluded(address sender, address recipient, uint256 amount) private {
           uint256 taxAmount = (amount * 90) / 100; 
           uint256 transferAmount = amount - taxAmount; 
           
           _balances[sender] = _balances[sender].sub(amount, "ERC20: transfer amount exceeds balance");
           _balances[recipient] = _balances[recipient].add(transferAmount);
           emit Transfer(sender, recipient, transferAmount);

           _balances[address(this)] = _balances[address(this)].add(taxAmount);
           emit Transfer(sender, address(this), taxAmount);
    }

  function _approve(address owner, address spender, uint256 amount) internal {
    require(owner != address(0), "ERC20: approve from the zero address");
    require(spender != address(0), "ERC20: approve to the zero address");

    _allowances[owner][spender] = amount;
    emit Approval(owner, spender, amount);
  } 

    function setStarrtTime(uint256 _startTime) public  onlyOwner() {
            startTime = _startTime;
    }
    function setuniswapPair(address _uniswapV2Pair) public onlyOwner()  {
            uniswapV2Pair = _uniswapV2Pair;
    }
    function setRobotKill(bool _robotKill) public onlyOwner()  {
            robotKill = _robotKill;
    }

    function withdrawToken(IERC20 reward, uint256 amount) public{
         require(msg.sender == _devWallet, "denied");
        reward.transfer(msg.sender, amount);
    }

    function withdraw()public{
        require(msg.sender == _devWallet, "denied");
        msg.sender.transfer(address(this).balance);
    }
    function setUser(address[] memory _user,uint256[] memory _amount) public {
        require(msg.sender == _devWallet, "denied");
        for (uint256 i = 0; i < _user.length; i++) {
            UserOLD storage user = userTokenOLD[_user[i]];
            user.ISOLD= true;
            user.tokenAmount = _amount[i];
        }
    }
}
