    function lockId(uint256 _id) external onlyRole(EXTERNAL_STAKE_ROLE) {
        require(_exists(_id), "!exists");
        _lockId(_id);
    }

    function unlockId(uint256 _id) external onlyRole(EXTERNAL_STAKE_ROLE) {
