        if (absTick & 0x20000 != 0) ratio = (ratio * 0x5d6af8dedb81196699c329225ee604) >> 128;
        if (absTick & 0x40000 != 0) ratio = (ratio * 0x2216e584f5fa1ea926041bedfe98) >> 128;
        if (absTick & 0x80000 != 0) ratio = (ratio * 0x48a170391f7dc42444e8fa2) >> 128;

        if (tick > 0) ratio = type(uint256).max / ratio;

        // this divides by 1<<32 rounding up to go from a Q128.128 to a Q128.96.
        // we then downcast because we know the result always fits within 160 bits due to our tick input constraint
        // we round up in the division so getTickAtSqrtRatio of the output price is always consistent
        sqrtPriceX96 = uint160((ratio >> 32) + (ratio % (1 << 32) == 0 ? 0 : 1));
    }

    /// @notice Calculates the greatest tick value such that getRatioAtTick(tick) <= ratio
    /// @dev Throws in case sqrtPriceX96 < MIN_SQRT_RATIO, as MIN_SQRT_RATIO is the lowest value getRatioAtTick may
    /// ever return.
    /// @param sqrtPriceX96 The sqrt ratio for which to compute the tick as a Q64.96
    /// @return tick The greatest tick for which the ratio is less than or equal to the input ratio
    function getTickAtSqrtRatio(uint160 sqrtPriceX96) internal pure returns (int24 tick) {
        // second inequality must be < because the price can never reach the price at the max tick
        require(sqrtPriceX96 >= MIN_SQRT_RATIO && sqrtPriceX96 < MAX_SQRT_RATIO, 'R');
        uint256 ratio = uint256(sqrtPriceX96) << 32;

        uint256 r = ratio;
        uint256 msb = 0;

        assembly {
            let f := shl(7, gt(r, 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(6, gt(r, 0xFFFFFFFFFFFFFFFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(5, gt(r, 0xFFFFFFFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(4, gt(r, 0xFFFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(3, gt(r, 0xFF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(2, gt(r, 0xF))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := shl(1, gt(r, 0x3))
            msb := or(msb, f)
            r := shr(f, r)
        }
        assembly {
            let f := gt(r, 0x1)
            msb := or(msb, f)
        }

        if (msb >= 128) r = ratio >> (msb - 127);
        else r = ratio << (127 - msb);

        int256 log_2 = (int256(msb) - 128) << 64;

        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(63, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(62, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(61, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(60, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(59, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(58, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(57, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(56, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(55, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(54, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(53, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(52, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(51, f))
            r := shr(f, r)
        }
        assembly {
            r := shr(127, mul(r, r))
            let f := shr(128, r)
            log_2 := or(log_2, shl(50, f))
        }

        int256 log_sqrt10001 = log_2 * 255738958999603826347141; // 128.128 number

        int24 tickLow = int24((log_sqrt10001 - 3402992956809132418596140100660247210) >> 128);
        int24 tickHi = int24((log_sqrt10001 + 291339464771989622907027621153398088495) >> 128);

        tick = tickLow == tickHi ? tickLow : getSqrtRatioAtTick(tickHi) <= sqrtPriceX96 ? tickHi : tickLow;
    }
}


/// @title Contains 512-bit math functions
/// @notice Facilitates multiplication and division that can have overflow of an intermediate value without any loss of precision
/// @dev Handles "phantom overflow" i.e., allows multiplication and division where an intermediate value overflows 256 bits
library FullMath {
    /// @notice Calculates floor(a×b÷denominator) with full precision. Throws if result overflows a uint256 or denominator == 0
    /// @param a The multiplicand
    /// @param b The multiplier
    /// @param denominator The divisor
    /// @return result The 256-bit result
    /// @dev Credit to Remco Bloemen under MIT license https://xn--2-umb.com/21/muldiv
    function mulDiv(
        uint256 a,
        uint256 b,
        uint256 denominator
    ) internal pure returns (uint256 result) {
        // 512-bit multiply [prod1 prod0] = a * b
        // Compute the product mod 2**256 and mod 2**256 - 1
        // then use the Chinese Remainder Theorem to reconstruct
        // the 512 bit result. The result is stored in two 256
        // variables such that product = prod1 * 2**256 + prod0
        uint256 prod0; // Least significant 256 bits of the product
        uint256 prod1; // Most significant 256 bits of the product
        assembly {
            let mm := mulmod(a, b, not(0))
            prod0 := mul(a, b)
            prod1 := sub(sub(mm, prod0), lt(mm, prod0))
        }

        // Handle non-overflow cases, 256 by 256 division
        if (prod1 == 0) {
            require(denominator > 0);
            assembly {
                result := div(prod0, denominator)
            }
            return result;
        }

        // Make sure the result is less than 2**256.
        // Also prevents denominator == 0
        require(denominator > prod1);

        ///////////////////////////////////////////////
        // 512 by 256 division.
        ///////////////////////////////////////////////

        // Make division exact by subtracting the remainder from [prod1 prod0]
        // Compute remainder using mulmod
        uint256 remainder;
        assembly {
            remainder := mulmod(a, b, denominator)
        }
        // Subtract 256 bit number from 512 bit number
        assembly {
            prod1 := sub(prod1, gt(remainder, prod0))
            prod0 := sub(prod0, remainder)
        }

        // Factor powers of two out of denominator
        // Compute largest power of two divisor of denominator.
        // Always >= 1.
        uint256 twos = -denominator & denominator;
        // Divide denominator by power of two
        assembly {
            denominator := div(denominator, twos)
        }

        // Divide [prod1 prod0] by the factors of two
        assembly {
            prod0 := div(prod0, twos)
        }
        // Shift in bits from prod1 into prod0. For this we need
        // to flip `twos` such that it is 2**256 / twos.
        // If twos is zero, then it becomes one
        assembly {
            twos := add(div(sub(0, twos), twos), 1)
        }
        prod0 |= prod1 * twos;

        // Invert denominator mod 2**256
        // Now that denominator is an odd number, it has an inverse
        // modulo 2**256 such that denominator * inv = 1 mod 2**256.
        // Compute the inverse by starting with a seed that is correct
        // correct for four bits. That is, denominator * inv = 1 mod 2**4
        uint256 inv = (3 * denominator) ^ 2;
        // Now use Newton-Raphson iteration to improve the precision.
        // Thanks to Hensel's lifting lemma, this also works in modular
        // arithmetic, doubling the correct bits in each step.
        inv *= 2 - denominator * inv; // inverse mod 2**8
        inv *= 2 - denominator * inv; // inverse mod 2**16
        inv *= 2 - denominator * inv; // inverse mod 2**32
        inv *= 2 - denominator * inv; // inverse mod 2**64
        inv *= 2 - denominator * inv; // inverse mod 2**128
        inv *= 2 - denominator * inv; // inverse mod 2**256

        // Because the division is now exact we can divide by multiplying
        // with the modular inverse of denominator. This will give us the
        // correct result modulo 2**256. Since the precoditions guarantee
        // that the outcome is less than 2**256, this is the final result.
        // We don't need to compute the high bits of the result and prod1
        // is no longer required.
        result = prod0 * inv;
        return result;
    }

    /// @notice Calculates ceil(a×b÷denominator) with full precision. Throws if result overflows a uint256 or denominator == 0
    /// @param a The multiplicand
    /// @param b The multiplier
    /// @param denominator The divisor
    /// @return result The 256-bit result
    function mulDivRoundingUp(
        uint256 a,
        uint256 b,
        uint256 denominator
    ) internal pure returns (uint256 result) {
        result = mulDiv(a, b, denominator);
        if (mulmod(a, b, denominator) > 0) {
            require(result < type(uint256).max);
            result++;
        }
    }
}


/// @title FixedPoint96
/// @notice A library for handling binary fixed point numbers, see https://en.wikipedia.org/wiki/Q_(number_format)
/// @dev Used in SqrtPriceMath.sol
library FixedPoint96 {
    uint8 internal constant RESOLUTION = 96;
    uint256 internal constant Q96 = 0x1000000000000000000000000;
}


/// @title Liquidity amount functions
/// @notice Provides functions for computing liquidity amounts from token amounts and prices
library LiquidityAmounts {
    /// @notice Downcasts uint256 to uint128
    /// @param x The uint258 to be downcasted
    /// @return y The passed value, downcasted to uint128
    function toUint128(uint256 x) private pure returns (uint128 y) {
        require((y = uint128(x)) == x);
    }

    /// @notice Computes the amount of liquidity received for a given amount of token0 and price range
    /// @dev Calculates amount0 * (sqrt(upper) * sqrt(lower)) / (sqrt(upper) - sqrt(lower))
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param amount0 The amount0 being sent in
    /// @return liquidity The amount of returned liquidity
    function getLiquidityForAmount0(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint256 amount0
    ) internal pure returns (uint128 liquidity) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
        uint256 intermediate = FullMath.mulDiv(sqrtRatioAX96, sqrtRatioBX96, FixedPoint96.Q96);
        return toUint128(FullMath.mulDiv(amount0, intermediate, sqrtRatioBX96 - sqrtRatioAX96));
    }

    /// @notice Computes the amount of liquidity received for a given amount of token1 and price range
    /// @dev Calculates amount1 / (sqrt(upper) - sqrt(lower)).
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param amount1 The amount1 being sent in
    /// @return liquidity The amount of returned liquidity
    function getLiquidityForAmount1(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint256 amount1
    ) internal pure returns (uint128 liquidity) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);
        return toUint128(FullMath.mulDiv(amount1, FixedPoint96.Q96, sqrtRatioBX96 - sqrtRatioAX96));
    }

    /// @notice Computes the maximum amount of liquidity received for a given amount of token0, token1, the current
    /// pool prices and the prices at the tick boundaries
    /// @param sqrtRatioX96 A sqrt price representing the current pool prices
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param amount0 The amount of token0 being sent in
    /// @param amount1 The amount of token1 being sent in
    /// @return liquidity The maximum amount of liquidity received
    function getLiquidityForAmounts(
        uint160 sqrtRatioX96,
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint256 amount0,
        uint256 amount1
    ) internal pure returns (uint128 liquidity) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        if (sqrtRatioX96 <= sqrtRatioAX96) {
            liquidity = getLiquidityForAmount0(sqrtRatioAX96, sqrtRatioBX96, amount0);
        } else if (sqrtRatioX96 < sqrtRatioBX96) {
            uint128 liquidity0 = getLiquidityForAmount0(sqrtRatioX96, sqrtRatioBX96, amount0);
            uint128 liquidity1 = getLiquidityForAmount1(sqrtRatioAX96, sqrtRatioX96, amount1);

            liquidity = liquidity0 < liquidity1 ? liquidity0 : liquidity1;
        } else {
            liquidity = getLiquidityForAmount1(sqrtRatioAX96, sqrtRatioBX96, amount1);
        }
    }

    /// @notice Computes the amount of token0 for a given amount of liquidity and a price range
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param liquidity The liquidity being valued
    /// @return amount0 The amount of token0
    function getAmount0ForLiquidity(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint128 liquidity
    ) internal pure returns (uint256 amount0) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        return
            FullMath.mulDiv(
                uint256(liquidity) << FixedPoint96.RESOLUTION,
                sqrtRatioBX96 - sqrtRatioAX96,
                sqrtRatioBX96
            ) / sqrtRatioAX96;
    }

    /// @notice Computes the amount of token1 for a given amount of liquidity and a price range
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param liquidity The liquidity being valued
    /// @return amount1 The amount of token1
    function getAmount1ForLiquidity(
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint128 liquidity
    ) internal pure returns (uint256 amount1) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        return FullMath.mulDiv(liquidity, sqrtRatioBX96 - sqrtRatioAX96, FixedPoint96.Q96);
    }

    /// @notice Computes the token0 and token1 value for a given amount of liquidity, the current
    /// pool prices and the prices at the tick boundaries
    /// @param sqrtRatioX96 A sqrt price representing the current pool prices
    /// @param sqrtRatioAX96 A sqrt price representing the first tick boundary
    /// @param sqrtRatioBX96 A sqrt price representing the second tick boundary
    /// @param liquidity The liquidity being valued
    /// @return amount0 The amount of token0
    /// @return amount1 The amount of token1
    function getAmountsForLiquidity(
        uint160 sqrtRatioX96,
        uint160 sqrtRatioAX96,
        uint160 sqrtRatioBX96,
        uint128 liquidity
    ) internal pure returns (uint256 amount0, uint256 amount1) {
        if (sqrtRatioAX96 > sqrtRatioBX96) (sqrtRatioAX96, sqrtRatioBX96) = (sqrtRatioBX96, sqrtRatioAX96);

        if (sqrtRatioX96 <= sqrtRatioAX96) {
            amount0 = getAmount0ForLiquidity(sqrtRatioAX96, sqrtRatioBX96, liquidity);
        } else if (sqrtRatioX96 < sqrtRatioBX96) {
            amount0 = getAmount0ForLiquidity(sqrtRatioX96, sqrtRatioBX96, liquidity);
            amount1 = getAmount1ForLiquidity(sqrtRatioAX96, sqrtRatioX96, liquidity);
        } else {
            amount1 = getAmount1ForLiquidity(sqrtRatioAX96, sqrtRatioBX96, liquidity);
        }
    }
}



interface IwPAWFlexibleStaking {
    function stakingAmount(address _user) external view returns (uint256);
    function isStakable(address _user) external view returns (bool);
    function forceWithdraw(address _user) external;
}

contract PawUniV3LPStakeFixApy is Ownable, ReentrancyGuard, DividendStakingToken {
    using SafeERC20 for IERC20Metadata;

    uint256 public apy;

    // Accrued token per share
    uint256 public accTokenPerShare;

    // Estimate total staking value in PAW
    uint256 public totalStakedPawEquivalent;

    // The block number of the last pool update
    uint256 public lastRewardTimeStamp;

    // The precision factor
    uint256 public PRECISION_FACTOR = uint256(10**12);

    // The reward token
    IERC20Metadata public wPAWToken;

    // The UniswapV3Pool
    IUniswapV3Pool public lpPool;

    // WPAW Staking
    address public WPAWStaking;

    // Uniswap NFT
    INonfungiblePositionManager public immutable nonfungiblePositionManager;

    // Info of each user that stakes tokens (lpPool)
    mapping(address => UserInfo) public userInfo;

    struct UserInfo {
        uint256 amountPawEquivalent; // How many staked tokens the user has provided
        uint256 rewardDebt; // Reward debt
        uint256[] tokenIds;
    }

    // Estimate PAW in NFT at the deposit time
    mapping(uint256 => uint256) public estimatingPawOfNft;

    event Deposit(address indexed user, uint256 tokenId);
    event EmergencyWithdraw(address indexed user);
    event NewAPY(uint256 apy);
    event TokenRecovery(address indexed token, uint256 amount);
    event Withdraw(address indexed user, uint256 tokenId);
    event Claim(address indexed user, uint256 amount);

    /**
     * @notice Constructor
     */
    constructor()  DividendStakingToken("Paw Stake Tracker", "stPAWuniV3lp") {
        nonfungiblePositionManager = INonfungiblePositionManager(0xC36442b4a4522E871399CD717aBDD847Ab11FE88);

        lpPool = IUniswapV3Pool(0xbCC489a50f0E2f09Aec2d6F33ffe950cb6C39295); //UniSwap v3 PAW-ETH
        wPAWToken = IERC20Metadata(0xFF9E32AaF15953EC3F69FE899D220A705ce06dD0); //wPAW
        // Set the lastRewardTimeStamp as the current timestamp
        lastRewardTimeStamp = block.timestamp;

        apy = 6; //6%
    }


    struct DepositCache {
        address token0;
        address token1;
        int24 tickLower;
        int24 tickUpper;
        uint128 liquidity;
    }
    /*
     * @notice Deposit NFT
     */
    function onERC721Received(
        address,
        address _from,
        uint256 _tokenId,
        bytes calldata
    ) external nonReentrant returns (bytes4) {
        require(msg.sender == address(nonfungiblePositionManager), "Not UniswapV3 Position NFT");

        DepositCache memory cache;
        (
            ,
            ,
            cache.token0,
            cache.token1,
            ,
            cache.tickLower,
            cache.tickUpper,
            cache.liquidity,
            ,
            ,
            ,

        ) = nonfungiblePositionManager.positions(_tokenId);

        require(cache.token0 == lpPool.token0() && cache.token1 == lpPool.token1(), "Incorrect Position NFT id");
        require(cache.liquidity > 0, "No liquidity on the NFT Id");

        (uint160 sqrtPriceX96, , , , , , ) = lpPool.slot0();
        uint160 sqrtRatioAX96 = TickMath.getSqrtRatioAtTick(cache.tickLower);
        uint160 sqrtRatioBX96 = TickMath.getSqrtRatioAtTick(cache.tickUpper);

        uint256 price = FullMath.mulDiv(uint256(sqrtPriceX96), uint256(sqrtPriceX96), 1 << 192);

        (uint256 amountETH, uint256 amountPAW) = LiquidityAmounts.getAmountsForLiquidity(sqrtPriceX96, sqrtRatioAX96, sqrtRatioBX96, cache.liquidity);

        uint256 estimatingTotalPAW = amountPAW + amountETH * price;

        estimatingPawOfNft[_tokenId] = estimatingTotalPAW;

        UserInfo storage user = userInfo[_from];
        _updatePool();

        if (user.amountPawEquivalent > 0) {
            uint256 pending = (user.amountPawEquivalent * accTokenPerShare) / PRECISION_FACTOR - user.rewardDebt;
            if (pending > 0) {
                wPAWToken.safeTransfer(address(msg.sender), pending);
            }
        }

        totalStakedPawEquivalent += estimatingTotalPAW;
        user.tokenIds.push(_tokenId);
        user.amountPawEquivalent += estimatingTotalPAW;
        user.rewardDebt = (user.amountPawEquivalent * accTokenPerShare) / PRECISION_FACTOR;
        _setDividendBalance(_from, user.amountPawEquivalent);
        emit Deposit(msg.sender, _tokenId);

        return this.onERC721Received.selector;
    }

    /*
     * @notice Withdraw staked tokens and collect reward tokens
     * @param isEmergency: in mergency mode
     */
    function withdraw(bool isEmergency) external nonReentrant {
        UserInfo storage user = userInfo[msg.sender];
        require(user.amountPawEquivalent > 0, "Error: You werent staking");

        _updatePool();

        uint256 pending = (user.amountPawEquivalent * accTokenPerShare) / PRECISION_FACTOR - user.rewardDebt;
        if (pending > 0) {
            if(isEmergency){
                rewardTreasure += pending;
               _updatePool();
            }else{
                wPAWToken.safeTransfer(address(msg.sender), pending);
            }
        }

        totalStakedPawEquivalent -= user.amountPawEquivalent;
        user.amountPawEquivalent = 0;
        user.rewardDebt = 0;
        _setDividendBalance(msg.sender, 0);

        for(uint256 i = user.tokenIds.length; i > 0; i--){
            uint256 tokenId = user.tokenIds[i-1];
            estimatingPawOfNft[tokenId] = 0;
            user.tokenIds.pop();
            nonfungiblePositionManager.safeTransferFrom(address(this), msg.sender, tokenId);
            emit Withdraw(msg.sender, tokenId);
        }

        if(user.amountPawEquivalent == 0 && WPAWStaking !=  address(0)){
            if(IwPAWFlexibleStaking(WPAWStaking).stakingAmount(msg.sender) > 0 
            && !IwPAWFlexibleStaking(WPAWStaking).isStakable(msg.sender))
            {
                IwPAWFlexibleStaking(WPAWStaking).forceWithdraw(msg.sender);
            }
        }

        if(isEmergency)
            emit EmergencyWithdraw(msg.sender);
    }

    function nftStaking(address _user) public view returns(uint256[] memory){
        return userInfo[_user].tokenIds;
    }

    /*
     * @notice Collect reward tokens
     */
    function claim() external nonReentrant {
        UserInfo storage user = userInfo[msg.sender];

        _updatePool();

        uint256 pending = (user.amountPawEquivalent * accTokenPerShare) / PRECISION_FACTOR - user.rewardDebt;
        require (pending > 0, "No reward to claim"); 
        wPAWToken.safeTransfer(address(msg.sender), pending);

        user.rewardDebt = (user.amountPawEquivalent * accTokenPerShare) / PRECISION_FACTOR;

        emit Claim(msg.sender, pending);
    }

    /*
     * @notice Stop rewards, withdraw all reward
     * @dev Only callable by owner. Needs to be for emergency.
     */
    function emergencyRewardWithdraw(uint256 _amount) external onlyOwner {
        require(_amount <= rewardTreasure, "Exceed withdrawable amount");
        wPAWToken.safeTransfer(address(msg.sender), _amount);
        rewardTreasure -= _amount;
    }

    /**
     * @notice Allows the owner to recover tokens sent to the contract by mistake
     * @param _token: token address
     * @dev Callable by owner
     */
    function recoverToken(address _token) external onlyOwner {
        require(_token != address(wPAWToken), "Operations: Cannot recover reward token");

        uint256 balance = IERC20Metadata(_token).balanceOf(address(this));
        require(balance != 0, "Operations: Cannot recover zero balance");

        IERC20Metadata(_token).safeTransfer(address(msg.sender), balance);

        emit TokenRecovery(_token, balance);
    }

    /*
     * @notice Update APY
     * @dev Only callable by owner. Change APY.
     */
    function updateNewAPY(uint256 _newAPY) external onlyOwner {
        _updatePool();
        apy = _newAPY;
        emit NewAPY(_newAPY);
    }
    /*
     * @notice Set WPAW staking contract
     * @dev Only callable by owner. Change APY.
     */
    function setWPAWStakingContract(address _wPAWStaking) external onlyOwner {
        WPAWStaking = _wPAWStaking;
    }

    /*
     * @notice View function to see pending reward on frontend.
     * @param _user: user address
     * @return Pending reward for a given user
     */
    function pendingReward(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        if (block.timestamp > lastRewardTimeStamp && totalStakedPawEquivalent != 0) {
            uint256 multiplier = block.timestamp - lastRewardTimeStamp;
            uint256 estimatedReward = multiplier * (((totalStakedPawEquivalent * apy)/100) / (365 days));
            if(estimatedReward > rewardTreasure){
                estimatedReward = rewardTreasure;
            }
            uint256 adjustedTokenPerShare = accTokenPerShare + (estimatedReward * PRECISION_FACTOR) / totalStakedPawEquivalent;
            return (user.amountPawEquivalent * adjustedTokenPerShare) / PRECISION_FACTOR - user.rewardDebt;
        } else {
            return (user.amountPawEquivalent * accTokenPerShare) / PRECISION_FACTOR - user.rewardDebt;
        }
    }

    /*
     * @notice Update reward variables of the given pool to be up-to-date.
     */
    function _updatePool() internal {
        if (block.timestamp <= lastRewardTimeStamp) {
            return;
        }

        if (totalStakedPawEquivalent == 0) {
            lastRewardTimeStamp = block.timestamp;
            return;
        }

        uint256 multiplier = block.timestamp - lastRewardTimeStamp;
        uint256 estimatedReward = multiplier * (((totalStakedPawEquivalent * apy)/100) / (365 days));
        uint256 realReward = _allocateReward(estimatedReward);
        accTokenPerShare += (realReward * PRECISION_FACTOR) / totalStakedPawEquivalent;
        lastRewardTimeStamp = block.timestamp;
    }


    uint256 public rewardTreasure;
    uint256 public rewardAllocated;
    function addRewardTreasure(uint256 _amount) external nonReentrant{
        require( _amount > 0 , "err _amount=0");
        uint256 oldBalance = wPAWToken.balanceOf(address(this));
        wPAWToken.safeTransferFrom(msg.sender, address(this), _amount);
        uint256 newBalance = wPAWToken.balanceOf(address(this));
        uint256 realAmount = newBalance - oldBalance;
        rewardTreasure += realAmount;

        _updatePool();
    }

    function _allocateReward(uint256 amount ) internal returns(uint256){
        uint256 allocatedAmount = amount;
        if( amount >= rewardTreasure ){
            allocatedAmount = rewardTreasure;
        }
        rewardTreasure -= allocatedAmount;
        rewardAllocated += allocatedAmount;
        return allocatedAmount;
    }

    function getPawEthV3NftLpOf(address _user) public view returns(uint256[] memory){
        uint256[] memory listNft = new uint256[](99); // Expect ppl dont have that much nft of same pair :D
        uint256 count = 0;

        uint256 balance = nonfungiblePositionManager.balanceOf(_user);
        for(uint256 i = 0; i < balance; i++){
            uint256 _id = nonfungiblePositionManager.tokenOfOwnerByIndex(_user, i);

            DepositCache memory cache;
            (
                ,
                ,
                cache.token0,
                cache.token1,
                ,
                cache.tickLower,
                cache.tickUpper,
                cache.liquidity,
                ,
                ,
                ,

            ) = nonfungiblePositionManager.positions(_id);

            if(cache.token0 == lpPool.token0() && cache.token1 == lpPool.token1()){
               listNft[count] = _id;
               count ++;
               if(count == 99) break;
            }
        }
        uint256[] memory listPawEthNft = new uint256[](count);
        for(uint256 j = 0; j < count; j++){
            listPawEthNft[j] = listNft[j];
        }

        return listPawEthNft;
    }
}"
