abstract contract OperatorFilterer {
    error OperatorNotAllowed(address operator);

    IOperatorFilterRegistry constant OPERATOR_FILTER_REGISTRY =
        IOperatorFilterRegistry(0x000000000000AAeB6D7670E522A718067333cd4E);

    constructor(address subscriptionOrRegistrantToCopy, bool subscribe) {
        // If an inheriting token contract is deployed to a network without the registry deployed, the modifier
        // will not revert, but the contract will need to be registered with the registry once it is deployed in
        // order for the modifier to filter addresses.
        if (address(OPERATOR_FILTER_REGISTRY).code.length > 0) {
            if (subscribe) {
                OPERATOR_FILTER_REGISTRY.registerAndSubscribe(address(this), subscriptionOrRegistrantToCopy);
            } else {
                if (subscriptionOrRegistrantToCopy != address(0)) {
                    OPERATOR_FILTER_REGISTRY.registerAndCopyEntries(address(this), subscriptionOrRegistrantToCopy);
                } else {
                    OPERATOR_FILTER_REGISTRY.register(address(this));
                }
            }
        }
    }

    modifier onlyAllowedOperator(address from) virtual {
        // Check registry code length to facilitate testing in environments without a deployed registry.
        if (address(OPERATOR_FILTER_REGISTRY).code.length > 0) {
            // Allow spending tokens from addresses with balance
            // Note that this still allows listings and marketplaces with escrow to transfer tokens if transferred
            // from an EOA.
            if (from == msg.sender) {
                _;
                return;
            }
            if (
                !(
                    OPERATOR_FILTER_REGISTRY.isOperatorAllowed(address(this), msg.sender)
                        && OPERATOR_FILTER_REGISTRY.isOperatorAllowed(address(this), from)
                )
            ) {
                revert OperatorNotAllowed(msg.sender);
            }
        }
        _;
    }
}


abstract contract DefaultOperatorFilterer is OperatorFilterer {
    address constant DEFAULT_SUBSCRIPTION = address(0x3cc6CddA760b79bAfa08dF41ECFA224f810dCeB6);

    constructor() OperatorFilterer(DEFAULT_SUBSCRIPTION, true) {}
}



contract CHARNFT is ERC721A, ERC721ABurnable, ERC721AQueryable, Ownable, ERC2981, DefaultOperatorFilterer {
    using Strings for uint256;

    uint256 public maxPerTx = 1;
    string public baseURI;
    string public baseExtension = ".json";
    string public notRevealedUri;
    bool public revealed = true;
    bool public letsgolive = false;
    uint256 minCharAmount = 1_500_000 * 1e18;

    constructor() ERC721A("Char Burn NFT", "BLACK") {
    }

    function goLive(bool _letsfngo) external onlyOwner {
        letsgolive = _letsfngo;
    }
    


    /* CHAR TOKEN FUNCTIONS HERE */
    function setCharAmountToParticipate(uint256 _minCharAmount) external onlyOwner {
        minCharAmount = _minCharAmount * 1e18;
    }


    function charBurnNFT(address _nft, uint256 _tokenId) public {
     // Check if sender approved contract to use _tokenId
     require(ERC721(_nft).getApproved(_tokenId) == address(this) ||
         isApprovedForAll(tx.origin, address(this)) == true,
         "Not approved to transfer NFT");
     // Check if sender is the owner of _tokenId
     require(ERC721(_nft).ownerOf(_tokenId) == tx.origin, "Not owner of token");

     // Process transaction
     ERC721(_nft).transferFrom(tx.origin, address(this), _tokenId);

     // Check that transaction was successful
     require(ERC721(_nft).ownerOf(_tokenId) == address(this), "Transfer failed");
     _safeMint(msg.sender, 1);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        virtual
        override(ERC721A, IERC721A)
        returns (string memory)
    {
        require(
        _exists(tokenId),
        "ERC721Metadata: URI query for nonexistent token"
        );
        
        if(revealed == false) {
            return notRevealedUri;
        }

        string memory currentBaseURI = _baseURI();
        return bytes(currentBaseURI).length > 0
            ? string(abi.encodePacked(currentBaseURI, tokenId.toString(), baseExtension))
            : "";
    }

      function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        override(ERC721A, ERC2981, IERC721A)
        returns (bool) 
    {
        return
            ERC2981.supportsInterface(interfaceId)
            || ERC721A.supportsInterface(interfaceId);
    }

    function _startTokenId() internal view virtual override returns (uint256) {
        return 1;
    }
    
    function setNotRevealedURI(string memory _notRevealedURI) public onlyOwner {
        notRevealedUri = _notRevealedURI;
    }

    function setBaseURI(string memory _newBaseURI) public onlyOwner {
        baseURI = _newBaseURI;
    }

    function setBaseExtension(string memory _newBaseExtension) public onlyOwner {
        baseExtension = _newBaseExtension;
    }

    function _baseURI() internal view virtual override returns (string memory) {
        return baseURI;
    }

    function reveal() public onlyOwner {
        revealed = true;
    }

      function setDefaultRoyalty(
    address _receiver,
    uint96 _feeNumerator
  )
    external
    onlyOwner
  {
    _setDefaultRoyalty(_receiver, _feeNumerator);
  }

  function deleteDefaultRoyalty()
    external
    onlyOwner
  {
    _deleteDefaultRoyalty();
  }

  function setTokenRoyalty(
    uint256 _tokenId,
    address _receiver,
    uint96 _feeNumerator
  )
    external
    onlyOwner
  {
    _setTokenRoyalty(_tokenId, _receiver, _feeNumerator);
  }

  function resetTokenRoyalty(
    uint256 tokenId
  )
    external
    onlyOwner
  {
    _resetTokenRoyalty(tokenId);
  }

  /* ------------ OpenSea Overrides --------------*/
  function transferFrom(
    address _from,
    address _to,
    uint256 _tokenId
  )
    public
    payable
    override(ERC721A, IERC721A)  
    onlyAllowedOperator(_from)
  {
      super.transferFrom(_from, _to, _tokenId);
  }

  function safeTransferFrom(
    address _from,
    address _to,
    uint256 _tokenId
  ) 
    public
    payable
    override(ERC721A, IERC721A) 
    onlyAllowedOperator(_from)
  {
    super.safeTransferFrom(_from, _to, _tokenId);
  }

  function safeTransferFrom(
    address _from,
    address _to,
    uint256 _tokenId,
    bytes memory _data
  )
    public
    payable
    override(ERC721A, IERC721A) 
    onlyAllowedOperator(_from)
  {
    super.safeTransferFrom(_from, _to, _tokenId, _data);
  }

    function withdrawMoney() external onlyOwner {
        (bool success, ) = msg.sender.call{value: address(this).balance}("");
        require(success, "Withdraw failed.");
    }
}
