        uint256 toSend = (total * percent) / 1000;

        (bool success, ) = payable(to).call{value: toSend}("");
        require(success);
    }

    function getAmountMintedPerType(
        MintType mintType,
        address _address
    ) public view returns (uint256) {
        return typeToWalletMinted[mintType][_address];
