pragma solidity ^0.8.23;

import "./ERC404.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract DragonToken is ERC404 {
    string public dataURI;
    string public baseTokenURI;

    constructor(
        address _owner
    ) ERC404("Lucky Red Dragon", "LUCK", 18, 8000, _owner) {
        balanceOf[_owner] = 8000 * 10 ** 18;
    }

    function setDataURI(string memory _dataURI) public onlyOwner {
        dataURI = _dataURI;
    }

    function setTokenURI(string memory _tokenURI) public onlyOwner {
        baseTokenURI = _tokenURI;
    }

    function setNameSymbol(
        string memory _name,
        string memory _symbol
    ) public onlyOwner {
        _setNameSymbol(_name, _symbol);
    }

    function tokenURI(uint256 id) public view override returns (string memory) {
        if (bytes(baseTokenURI).length > 0) {
            return baseTokenURI;
        } else {
            string memory jsonPreImage = string.concat(
                string.concat(
                    string.concat(
                        '{"name":"Lucky Red Dragon #',
                        Strings.toString(id)
                    ),
                    '","description":"**Blessings of the Celestial Dragon** \\n\\nIn the auspicious cycle of the Year of the Dragon, the Lucky Red Dragon NFT emerges as a celestial blessing. This radiant and powerful dragon, steeped in the ancient spirit of Chinese New Year, is a digital talisman of immense luck and overflowing prosperity. Hold this token and let the dragon`s timeless fortune guide your path to abundant wealth and success in the unfolding year. It is not merely an NFT; it is a sacred charm for harnessing the enduring blessings of prosperity. \\n\\n **\u5929\u9f99\u7684\u795d\u798f** \\n\\n\u5728\u9f99\u5e74\u7684\u5409\u7965\u5468\u671f\u4e2d\u002c\u0020\u5e78\u8fd0\u7ea2\u9f99\u0020\u004e\u0046\u0054\u0020\u663e\u73b0\u4e3a\u5929\u754c\u7684\u795d\u798f\u3002\u8fd9\u4e2a\u5149\u8292\u56db\u5c04\u3001\u5f3a\u5927\u7684\u9f99\uff0c\u6d78\u6da6\u7740\u4e2d\u56fd\u65b0\u5e74\u7684\u53e4\u8001\u7cbe\u795e\uff0c\u662f\u4e00\u679a\u5e26\u6765\u5de8\u5927\u8fd0\u6c14\u548c\u6ee1\u6ea2\u7e41\u8363\u7684\u6570\u5b57\u62a4\u7b26\u3002\u6301\u6709\u6b64\u4ee4\u724c\uff0c\u8ba9\u9f99\u7684\u6c38\u6052\u8d22\u5bcc\u5f15\u9886\u4f60\u8d70\u5411\u4e00\u5e74\u4e2d\u7684\u4e30\u76db\u8d22\u5bcc\u548c\u6210\u529f\u3002\u8fd9\u4e0d\u4ec5\u4ec5\u662f\u4e00\u4e2a\u0020\u004e\u0046\u0054\u003a\u0020\u5b83\u662f\u4e00\u4e2a\u7528\u4e8e\u6c72\u53d6\u6301\u4e45\u7e41\u8363\u795d\u798f\u7684\u795e\u5723\u9b45\u529b\u002e \\n\\nArtwork by Joyce Liu X AOI","external_url":"https://aoi.com", "image":"'
                ),
                string.concat(
                    dataURI,
                    'bafybeido3hdxmoo73icpndkpwp2ajm6xc4jn2yuqnocf75wvimfynp4ia4",'
                )
            );
            string memory jsonPostImage = string.concat(
                string.concat(
                    '"animation_url":"',
                    string.concat(
                        dataURI,
                        'bafybeibasfxazq4bswfzn5ov6va6qemuv6p4wjxrqyg33n2uv6wf767yau",'
                    )
                ),
                '"attributes":[]}'
            );

            return
                string.concat(
                    "data:application/json;utf8,",
                    string.concat(jsonPreImage, jsonPostImage)
                );
        }
    }
}
