// D4A constants, structs, enums && errors
import { BASIS_POINT, SIGNER_ROLE, BASIC_DAO_RESERVE_NFT_NUMBER } from "contracts/interface/D4AConstants.sol";
import {
    UpdateRewardParam,
    DaoMintInfo,
