    "contracts/GNUSERC1155MaxSupply.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

import "@gnus.ai/contracts-upgradeable-diamond/token/ERC1155/extensions/ERC1155SupplyUpgradeable.sol";
import "@gnus.ai/contracts-upgradeable-diamond/security/PausableUpgradeable.sol";
import "@gnus.ai/contracts-upgradeable-diamond/token/ERC1155/extensions/ERC1155BurnableUpgradeable.sol";
import "./GNUSNFTFactoryStorage.sol";

contract GNUSERC1155MaxSupply is ERC1155SupplyUpgradeable, PausableUpgradeable, ERC1155BurnableUpgradeable {

    using GNUSNFTFactoryStorage for GNUSNFTFactoryStorage.Layout;

    function _beforeTokenTransfer(address operator, address from, address to, uint256[] memory ids,
        uint256[] memory amounts, bytes memory data) internal whenNotPaused
        override(ERC1155Upgradeable, ERC1155SupplyUpgradeable) {

        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);

        if (from == address(0)) {
            for (uint256 i = 0; i < ids.length; ++i) {
                uint256 id = ids[i];
                require(totalSupply(id) <= GNUSNFTFactoryStorage.layout().NFTs[id].maxSupply, "Max Supply for NFT would be exceeded");
            }
        }
    }

    function asSingletonArray(uint256 element) internal pure returns (uint256[] memory) {
        uint256[] memory array = new uint256[](1);
        array[0] = element;

        return array;
    }

    function asSingletonArray(string memory element) internal pure returns (string[] memory) {
        string[] memory array = new string[](1);
        array[0] = element;

        return array;
    }
}
"
    },
    "contracts/GNUSNFTFactoryStorage.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

struct NFT {
    string name;            // Token/MFT Name
    string symbol;          // Token/MFT Symbol
    string uri;             // Token/NFT uri for metadata
    uint256 exchangeRate;   // only for withdrawing to GNUS
    uint256 maxSupply;      // maximum supply of NFTs
    address creator;        // the creator of the token
    uint128 childCurIndex;  // the current childNFT count created
    bool nftCreated;        // if there is a mapping/token created
}

/// @custom:security-contact support@gnus.ai
library  GNUSNFTFactoryStorage
{
    struct Layout {
        // token ID to NFT information
        mapping(uint256 => NFT) NFTs;
    }

    bytes32 constant GNUS_NFT_FACTORY_STORAGE_POSITION = keccak256("gnus.ai.nft.factory.storage");

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = GNUS_NFT_FACTORY_STORAGE_POSITION;
        assembly {
            l.slot := slot
        }
    }

}

"
    },
    "contracts/GNUSConstants.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.2;

// Reserve Currency/Token Name
string constant GNUS_NAME = "Genius Tokens";
// Reserve Currency/Token Symbol
string constant GNUS_SYMBOL = "GNUS";
uint256 constant GNUS_DECIMALS = 10 ** 18;
uint256 constant GNUS_MAX_SUPPLY = 50000000 * GNUS_DECIMALS;  // 50 million tokens
string constant GNUS_URI = "https://nft.gnus.ai/{id}";
uint256 constant GNUS_TOKEN_ID = 0;

uint128 constant MAX_UINT128 = uint128(0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF);
uint256 constant PARENT_MASK = uint256(MAX_UINT128) << 128; // the token type mask in the upper 128 bits
uint256 constant CHILD_MASK = MAX_UINT128; // the non-fungible index mask in the lower 128
"
