pragma solidity ^0.8.9;

abstract contract Operable is Context {
    mapping(address => bool) _operators;
    modifier onlyOperator() {
        _checkOperatorRole(_msgSender());
        _;
    }

    function isOperator(address _operator) public view returns (bool) {
        return _operators[_operator];
    }

    function _grantOperatorRole(address _candidate) internal {
        require(
            !_operators[_candidate],
            string(
                abi.encodePacked(
                    "account ",
                    Strings.toHexString(uint160(_msgSender()), 20),
                    " is already has an operator role"
                )
            )
        );
        _operators[_candidate] = true;
    }

    function _revokeOperatorRole(address _candidate) internal {
        _checkOperatorRole(_candidate);
        delete _operators[_candidate];
    }

    function _checkOperatorRole(address _operator) internal view {
        require(
            _operators[_operator],
            string(
                abi.encodePacked(
                    "account ",
                    Strings.toHexString(uint160(_msgSender()), 20),
                    " is not an operator"
                )
            )
        );
    }
}

interface IStatus {
    // TOKEN IDごとのステータスを更新
    function setStatus(uint256 tokenId, uint256 status) external;

    function setLevel(uint256 tokenId, uint256 _level) external;

    function getStatus(uint256 tokenId) external view returns (uint256);

    function getLevel(uint256 tokenId) external view returns (uint256);

    function upLevel(uint256 tokenId) external;

    function getAtribute(uint256 tokenId) external view returns (uint256);

    function getMinStatus() external view returns (uint256);
}

/*
𝒎𝒐𝒓𝒐𝒈𝒂𝒎𝒊 𝑺𝑻𝑨𝑲𝑬
*/

contract MorogamiStaking is ERC721Holder, Ownable, Operable {
    event NFTStaked(address owner, uint256 tokenId);
    event NFTUnstaked(address owner, uint256 tokenId);
    event Claimed(address owner, uint256 amount);

    IERC721BASE parentNFT;
    IStatus statusContract;

    mapping(uint256 => uint256) public expTable;
    address public parentAddress;
    address public statusAddress;
    struct Stake {
        address owner;
        uint256 timestamp;
    }
    // map owner address to stake details
    mapping(uint256 => Stake) public stakes;
    mapping(address => uint256) public stakingBalance;
    // map owner total staking time
    mapping(uint256 => uint256) public stakingTime;
    // map ownerOfToken
    mapping(uint256 => address) public ownerOfToken;

    constructor(address _parentNFT, address _statusAdd) {
        _grantOperatorRole(msg.sender);
        parentAddress = _parentNFT;
        parentNFT = IERC721BASE(_parentNFT);
        statusAddress = _statusAdd;
        statusContract = IStatus(statusAddress);
        expTable[1] = 60 * 60 * 24 * 3;
        expTable[2] = 60 * 60 * 24 * 7;
        expTable[3] = 60 * 60 * 24 * 7;
        expTable[4] = 60 * 60 * 24 * 14;
    }

    function setParentNFT(address _contractAddress) public onlyOperator {
        parentAddress = _contractAddress;
        parentNFT = IERC721BASE(_contractAddress);
    }

    function setSTatusContract(address _contractAddress) public onlyOperator {
        statusAddress = _contractAddress;
        statusContract = IStatus(statusAddress);
    }

    function safeTransferFromMulti(
        address _from,
        address _to,
        uint256[] memory _tokenIds
    ) internal {
        for (uint256 i = 0; i < _tokenIds.length; i++) {
            parentNFT.safeTransferFrom(_from, _to, _tokenIds[i]);
        }
    }

    function stakeMulti(uint256[] memory _tokenIds) external {
        uint256 length = _tokenIds.length;
        for (uint256 i = 0; i < length; i++) {
            require(
                parentNFT.ownerOf(_tokenIds[i]) == msg.sender,
                "Not the owner"
            );
            require(stakes[_tokenIds[i]].owner == address(0), "Already staked");

            stakes[_tokenIds[i]] = Stake(msg.sender, block.timestamp);
            stakingBalance[msg.sender]++;
            emit NFTStaked(msg.sender, _tokenIds[i]);

            parentNFT.safeTransferFrom(msg.sender, address(this), _tokenIds[i]);
        }
    }

    function unstakeMulti(uint256[] memory _tokenIds) external {
        uint256 length = _tokenIds.length;
        for (uint256 i = 0; i < length; i++) {
            require(stakes[_tokenIds[i]].owner == msg.sender, "Not the owner");

            stakingTime[_tokenIds[i]] += (block.timestamp -
                stakes[_tokenIds[i]].timestamp);
            delete stakes[_tokenIds[i]];
            stakingBalance[msg.sender]--;
            emit NFTUnstaked(msg.sender, _tokenIds[i]);

            parentNFT.safeTransferFrom(address(this), msg.sender, _tokenIds[i]);
        }
    }

    function balanceOf(address owner) public view returns (uint) {
        require(
            owner != address(0),
            "ERC721: balance query for the zero address"
        );
        return stakingBalance[owner];
    }

    function ownerOf(uint256 _tokenId) public view returns (address) {
        return stakes[_tokenId].owner;
    }

    function getStakeTimestamp(
        uint256 _tokenId
    ) external view virtual returns (uint256) {
        return stakes[_tokenId].timestamp;
    }

    function setStakeTimestamp(
        uint256 _tokenId,
        uint256 _timestamp
    ) external onlyOperator {
        stakes[_tokenId].timestamp = _timestamp;
    }

    function setRetroStakeTimestamp(
        uint256 _tokenId,
        uint256 _days
    ) external onlyOperator {
        stakes[_tokenId].timestamp =
            stakes[_tokenId].timestamp -
            60 *
            60 *
            24 *
            _days;
    }

    function setExpTable(uint256 _id, uint256 _time) external onlyOperator {
        expTable[_id] = _time;
    }

    function setExpTableDays(uint256 _id, uint256 _days) external onlyOperator {
        expTable[_id] = 60 * 60 * 24 * _days;
    }

    function levelUp(uint256 _tokenId) external {
        require(parentNFT.ownerOf(_tokenId) == msg.sender, "is no owner");
        uint256 nowLevel = statusContract.getLevel(_tokenId);
        if (nowLevel < 1) {
            nowLevel = 1;
        }
        require(expTable[nowLevel] > 0, "out of level table.");
        if (stakingTime[_tokenId] >= expTable[nowLevel]) {
            statusContract.upLevel(_tokenId);
            stakingTime[_tokenId] = stakingTime[_tokenId] - expTable[nowLevel];
        }
        emit Claimed(msg.sender, nowLevel);
    }

    function getLevel(
        uint256 _tokenId
    ) external view virtual returns (uint256) {
        if (stakes[_tokenId].owner == address(0)) {
            return 0;
        }
        uint256 nowLevel = statusContract.getLevel(_tokenId);
        if (nowLevel < 1) {
            nowLevel = 1;
        }
        uint256 tmpStakeTime = block.timestamp - stakes[_tokenId].timestamp;
        while (tmpStakeTime >= expTable[nowLevel]) {
            if (expTable[nowLevel] > 0) {
                tmpStakeTime = tmpStakeTime - expTable[nowLevel];
                nowLevel += 1;
            } else {
                break;
            }
        }
        return nowLevel;
    }

    function getStatus(
        uint256 _tokenId
    ) external view virtual returns (uint256) {
        if (stakes[_tokenId].owner == address(0)) {
            return 0;
        }
        uint256 nowLevel = statusContract.getLevel(_tokenId);
        if (nowLevel < 1) {
            nowLevel = 1;
        }
        uint256 nowStatus = statusContract.getStatus(_tokenId);
        uint256 tmpStakeTime = block.timestamp - stakes[_tokenId].timestamp;
        while (tmpStakeTime >= expTable[nowLevel]) {
            if (expTable[nowLevel] > 0) {
                tmpStakeTime = tmpStakeTime - expTable[nowLevel];
                nowLevel += 1;
                nowStatus += statusContract.getMinStatus();
            } else {
                break;
            }
        }
        return nowStatus;
    }

    function getAttribute(
        uint256 _tokenId
    ) external view virtual returns (uint256) {
        return statusContract.getAtribute(_tokenId);
    }

    //return wallet owned tokenids.
    function walletOfOwner(
        address _address
    ) external view virtual returns (uint256[] memory) {
        unchecked {
            uint256 totalSupply = parentNFT.totalSupply();
            uint256[] memory tokenIds = new uint256[](stakingBalance[_address]);
            uint256 tokenindex = 0;
            for (uint256 i = 0; i < totalSupply+1; i++) {
                if (stakes[i].owner == _address) tokenIds[tokenindex++] = i;
            }
            return tokenIds;
        }
    }

    /**
        @dev Operable.Role.ADD
     */
    function grantOperatorRole(address _candidate) external onlyOwner {
        _grantOperatorRole(_candidate);
    }

    /**
        @dev Operable.Role.REMOVE
     */
    function revokeOperatorRole(address _candidate) external onlyOwner {
        _revokeOperatorRole(_candidate);
    }
}
