pragma solidity ^0.8.4;


contract XCZ is Ownable, ERC20 {
    uint256 public maxWallet;
    address public uniswapV2Pair;
    IUniswapV2Router02 immutable router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);

    uint256 SUPPLY = 999000000000 * 10**18;

    uint256 firstBlockFee = 30; // 20% 
    uint256 totalFee = 8; // 3% 
    uint256 nftFee = 1; // 1%


    bool private inSwap = false;
    address public marketingWallet;
    address public nftWallet;

    uint256 public openTradingBlock;

    uint256 public collectedNftFees;

    mapping (address => uint256) public receiveBlock;

    uint256 public swapAt = SUPPLY / 1000; //0.1%

    constructor() ERC20("XCZ", "XCZ") payable {
        _mint(msg.sender, SUPPLY * 7 / 100);
        _mint(address(this), SUPPLY * 93 / 100);
        maxWallet = SUPPLY * 1 / 100;
        marketingWallet = owner();
        nftWallet = 0x00F3c3aC40525326Bcd95ec79A37f91F78bF3Bb7; 
    }

    receive() external payable {}

    function updateFee(uint256 _totalFee, uint256 _nftFee) external onlyOwner {
        require(_totalFee <= 30 && _nftFee <= _totalFee);
        totalFee = _totalFee;
        nftFee = _nftFee;
    }

    function updateMaxHoldingPercent(uint256 percent) external onlyOwner {
        require(percent >= 1 && percent <= 100, "invalid percent");
        maxWallet = SUPPLY * percent / 100;
    }

    function updateSwapAt(uint256 value) external onlyOwner() {
        require(value <= SUPPLY / 50);
        swapAt = value;
    }


    function openTrading() external onlyOwner {
        address pair = IUniswapV2Factory(router.factory()).createPair(address(this), router.WETH());
        _approve(address(this), address(router), balanceOf(address(this)));
        router.addLiquidityETH{
            value: address(this).balance
        } (
            address(this),
            balanceOf(address(this)),
            0,
            0,
            owner(),
            block.timestamp
        );

        uniswapV2Pair = pair;
        openTradingBlock = block.number;
    }

    function _transfer(address from, address to, uint256 amount) internal override {
        if(uniswapV2Pair == address(0)) {
            require(from == address(this) || from == address(0) || from == owner() || to == owner(), "Not started");
            super._transfer(from, to, amount);
            return;
        }

        if(from == uniswapV2Pair && to != address(this) && to != owner() && to != address(router)) {
            require(super.balanceOf(to) + amount <= maxWallet, "max wallet");
        }

        uint256 swapAmount = balanceOf(address(this));

        if(swapAmount > swapAt) {
            swapAmount = swapAt;
        }

        if(
            swapAt > 0 &&
            swapAmount == swapAt &&
            !inSwap &&
            from != uniswapV2Pair) {

            inSwap = true;

            uint256 nftShare = swapTokensForEth(swapAmount);

            uint256 balance = address(this).balance;

            if(balance > 0) {
                withdraw(balance, nftShare);
            }

            inSwap = false;
        }


        uint256 fee;

        if(block.number < openTradingBlock + 1) {
            fee = firstBlockFee;
        }
        else if(totalFee > 0) {
            fee = totalFee;
        }

        if(
            fee > 0 &&
            from != address(this) &&
            from != owner() &&
            from != address(router)
        ) {
            uint256 feeTokens = amount * fee / 100;
            amount -= feeTokens;

            super._transfer(from, address(this), feeTokens);

            if(nftFee > 0) {
                collectedNftFees += feeTokens * nftFee / fee;
            }
        }

        //

        super._transfer(from, to, amount);
    }


    function swapTokensForEth(uint256 tokenAmount) private returns (uint256) {
        uint256 balance = balanceOf(address(this));
        uint256 nftShare = collectedNftFees * 10000 / balance;

        uint256 lower = nftShare * tokenAmount / 10000;

        if(collectedNftFees > lower) {
            collectedNftFees -= lower;
        }
        else {
            collectedNftFees = 0;
        }

        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = router.WETH();
        _approve(address(this), address(router), tokenAmount);

        router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );

        return nftShare;
    }

    function withdraw(uint256 amount, uint256 nftShare) private {
        if(nftShare > 10000) {
            nftShare = 10000;
        }

        uint256 toNft = amount * nftShare / 10000;
        uint256 toMarketing = amount - toNft;

        (bool success,) = marketingWallet.call{value: toMarketing}("");
        success;

        (success,) = nftWallet.call{value: toNft}("");
        success;
    }
}
