      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity  ^0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "solidity-bytes-utils/contracts/BytesLib.sol";

import "./interfaces/IBatchDepositWithKeyRegistry.sol";
import "./interfaces/IDepositContract.sol";
import "./interfaces/IKeyRegistry.sol";

contract BatchDepositWithKeyRegistry is IBatchDepositWithKeyRegistry, Ownable, ReentrancyGuard{
    using SafeMath for uint256;

    /// @dev asset manager address
    address public assetManager;

    /// @dev ETH2 depositContract address
    address public immutable depositContract;

    /// @dev KeyRegistry contract address
    address public keyRegistry;

    uint256 constant public DEPOSIT_AMOUNT = 32 ether;
    uint256 internal constant DEPOSIT_AMOUNT_UNIT = 1000000000 wei;
    uint256 constant public SIGNATURE_LENGTH = 96;

    /// @dev default value for maximum number of Ethereum 2.0 validators registered in a single  call 
    uint256 public maxDepositsPerCall;

    /// @dev deposit whitelist
    mapping(address => bool) public whitelist;

    /// @dev depositETH method switch
    bool public depositEnabled;

    constructor(
        address _assetManager,
        address _depositContract,
        address _keyRegistry,
        uint256 _initMaxDepositCount
    ) {
        require(address(_assetManager) != address(0), "invalid init asset manager address");
        require(address(_depositContract) != address(0), "invalid init ETH2 depositContract address");
        require(address(_keyRegistry) != address(0), "invalid init KeyRegistry address");
        require(_initMaxDepositCount != 0, "invalid init max deposit per call");
        assetManager = _assetManager;
        depositContract = _depositContract;
        keyRegistry = _keyRegistry;
        maxDepositsPerCall = _initMaxDepositCount;

        emit Deployed(msg.sender,_assetManager, _depositContract, _keyRegistry, _initMaxDepositCount);
    }

    /// @dev receive ETH and deposit
    receive() external payable {
        depositETH();
    }

    /// @dev asset manager authority
    modifier onlyAssetManager() {
        require(msg.sender == assetManager, "not asset manager");
        _;
    }

    /// @dev whitelist users` authority
    modifier onlyWhitelisted() {
        require(whitelist[msg.sender] == true, "not in the whitelist");
        _;
    }

    /// @dev depositETH method switch
    modifier onlyDepositEnabled() {
        require(depositEnabled == true, "not enabled");
        _;
    }

    /// @dev Turn on the depositETH switch
    function enable() public onlyOwner {
        require(depositEnabled != true, "already enabled");
        depositEnabled = true;
        emit Enable();
    }

    /// @dev Turn off the depositETH switch
    function pause() public onlyOwner {
        require(depositEnabled != false, "already paused");
        depositEnabled = false;
        emit Pause();
    }

    /// @dev update asset manager address, only owner can operator
    /// @param _newAssetManager new asset manager address
    function updateAssetManager(address _newAssetManager) public onlyOwner {
        require(address(_newAssetManager) != address(0), "invalid new asset manager address");
        require(address(_newAssetManager) != address(assetManager), "asset manager address repeat");
        assetManager = _newAssetManager;
        emit UpdateAssetManager(_newAssetManager);
    }

    /// @dev update maxDepositsPerCall, only asset manager can operator
    /// @param _newMaxDepositCount new maxDepositsPerCall
    function updateMaxDepositCount(uint256 _newMaxDepositCount) public onlyAssetManager {
        require(_newMaxDepositCount != 0, "zero max deposit count set");
        require(_newMaxDepositCount != maxDepositsPerCall, "repeat setting max deposit count");                                                                                  
        maxDepositsPerCall = _newMaxDepositCount;
        emit UpdateMaxDeposit(_newMaxDepositCount);
    }

    /// @dev update KeyRegistry contract address, only owner can operator
    /// @param _newKeyRegistry new KeyRegistry contract address
    function updateKeyRegistry(address _newKeyRegistry) public onlyOwner {
        require(address(_newKeyRegistry) != address(0), "invalid new KeyRegistry address");
        require(address(_newKeyRegistry) != address(keyRegistry), "KeyRegistry address repeat");
        keyRegistry = _newKeyRegistry;
        emit UpdateKeyRegistry(_newKeyRegistry);
    }

    // @dev add single account or multiple accounts to whitelist
    /// @param _addrs accounts addresses
    function addAddressesToWhitelist(address[] memory _addrs) public onlyAssetManager {
        for (uint256 i = 0; i < _addrs.length; i++) {
            require(_addrs[i] != address(0),"invalid address");
            require(whitelist[_addrs[i]] != true, "already in the whitelist");
            whitelist[_addrs[i]] = true;
            emit WhitelistAddressAdded(_addrs[i]);
        }
    }

    /// @dev remove single accounts or multiple accounts from whitelist
    /// @param _addrs accounts addresses
    function removeAddressesFromWhitelist(address[] memory _addrs) public onlyAssetManager {
        for (uint256 i = 0; i < _addrs.length; i++) {
            require(_addrs[i] != address(0),"invalid address");
            require(whitelist[_addrs[i]] == true, "not in the whitelist");
            delete whitelist[_addrs[i]];
            emit WhitelistAddressRemoved(_addrs[i]);
        }
    }

    /// @dev deposit ETH to ETH2 depositContract, only in the whitelist can use this method
    function depositETH() public payable nonReentrant onlyWhitelisted onlyDepositEnabled {
        uint256 ethAmount = msg.value;
        uint256 numKeys = ethAmount.div(DEPOSIT_AMOUNT);
        require(ethAmount != 0, "zero ETH deposited");
        require(ethAmount.mod(DEPOSIT_AMOUNT) == 0, "not a multiple of 32");
        require(numKeys <= maxDepositsPerCall, "over max keys");

        eth2Deposit(numKeys);

        emit ETHDeposited(msg.sender, numKeys, ethAmount);
    }

    function eth2Deposit(uint256 numDeposit) internal {
        (
            bytes[] memory pubKeys,
            bytes[] memory withdrawalCredentials,
            bytes[] memory signatures
        ) = IKeyRegistry(keyRegistry).assignKeys(msg.sender, numDeposit);

        uint256 keyAmount = pubKeys.length;
        require(keyAmount != 0, "error pubKeys amounts");
        require(withdrawalCredentials.length == keyAmount, "error withdrawalCredentials amounts");
        require(signatures.length == keyAmount, "error signatures amounts");

        for (uint256 i = 0; i < keyAmount; i++) {
            _deposit(pubKeys[i], withdrawalCredentials[i], signatures[i]);
        }
    }

    function _deposit(bytes memory pubKey, bytes memory withdrawalCredential, bytes memory signature) internal {
        uint256 valueAmount = DEPOSIT_AMOUNT;
        uint256 depositAmounts = valueAmount.div(DEPOSIT_AMOUNT_UNIT);
        require(depositAmounts.mul(DEPOSIT_AMOUNT_UNIT) == valueAmount, "depositAmounts error");

        bytes32 pubkeyRoot = sha256(_pad64(pubKey));
        bytes32 signatureRoot = sha256(
            abi.encodePacked(
                sha256(BytesLib.slice(signature, 0, 64)),
                sha256(_pad64(BytesLib.slice(signature, 64, SIGNATURE_LENGTH.sub(64))))
                )
            );

        bytes32 depositDataRoot = sha256(
          abi.encodePacked(
              sha256(abi.encodePacked(pubkeyRoot,withdrawalCredential)),
              sha256(abi.encodePacked(_toLittleEndian64(depositAmounts),signatureRoot))
          )
        );
        uint256 targetBalance = address(this).balance.sub(valueAmount);

        IDepositContract(depositContract).deposit{value : valueAmount}(pubKey, abi.encodePacked(withdrawalCredential), signature, depositDataRoot);
        require(address(this).balance == targetBalance, "expecting deposit to happen");
        emit Deposited(msg.sender, pubKey, withdrawalCredential, signature, depositDataRoot, valueAmount);
    }

    /// @dev sweep ETH in this contract
    /// @param _receiver ETH receiver address
    function sweep(address _receiver) public onlyOwner nonReentrant {
        uint256 currentBalance = address(this).balance;
        require(currentBalance != 0, "invalid balance");
        require(_receiver != address(0), "invalid receiver address");
        (bool sent,) = payable(_receiver).call{value: currentBalance}("");
        require(sent, "failed sweep");

        emit Swept(msg.sender, _receiver, currentBalance);
    }

    function _pad64(bytes memory _b) internal pure returns (bytes memory) {
        assert(_b.length >= 32 && _b.length <= 64);
        if (64 == _b.length)
            return _b;

        bytes memory zero32 = new bytes(32);
        assembly { mstore(add(zero32, 0x20), 0) }

        if (32 == _b.length)
            return BytesLib.concat(_b, zero32);
        else
            return BytesLib.concat(_b, BytesLib.slice(zero32, 0, uint256(64).sub(_b.length)));
    }

    function _toLittleEndian64(uint256 _value) internal pure returns (uint256 result) {
        result = 0;
        uint256 temp_value = _value;
        for (uint256 i = 0; i < 8; ++i) {
            result = (result << 8) | (temp_value & 0xFF);
            temp_value >>= 8;
        }

        assert(0 == temp_value);    // fully converted
        result <<= (24 * 8);
    }
}"
