      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity  ^0.8.13;

interface IBatchDepositWithKeyRegistry {

    event Deployed(
        address indexed deployer,
        address indexed assetManager, 
        address indexed depositContract,
        address keyRegistry,
        uint256 initMaxDepositCount
    );

    event ETHDeposited(address from, uint256 keysNumber, uint256 ethAmount);

    event Enable();

    event Pause();

    event UpdateAssetManager(address newAssetManager);

    event UpdateMaxDeposit(uint256 newMaxDepositCount);

    event UpdateKeyRegistry(address newKeyRegistry);

    event WhitelistAddressAdded(address userAddress);

    event WhitelistAddressRemoved(address userAddress);

    event Deposited(
        address indexed from, 
        bytes pubkey, 
        bytes withdrawalCredentials, 
        bytes signature,
        bytes32 depositDataRoot,
        uint256 ethAmount
    );

    event Swept(address from, address to, uint256 amount);

    function whitelist(address) external view returns(bool);

    function enable() external;

    function pause() external;

    function updateAssetManager(address _newAssetManager) external;

    function updateKeyRegistry(address _newKeyRegistry) external;

    function addAddressesToWhitelist(address[] memory _addrs) external;

    function removeAddressesFromWhitelist(address[] memory _addrs) external;

    function depositETH() external payable;

    function sweep(address _receiver) external;
}"
