pragma solidity ^0.8.0;

import "./Ownable.sol";
import "./Strings.sol";
import "./Address.sol";
import "./Pausable.sol";
import "./ReentrancyGuard.sol";
import "./IMintables.sol";

contract WotmSeller is  Ownable , Pausable, ReentrancyGuard{

    using Address for address payable;

    using Strings for uint256;    

    address public fundsReceiver;

    address public fundsReceiver2;

    uint256 public maxNftPerWallet = 3;

    uint256 public tokenPrice = 2 ether;

    uint256 public fundsLockingEndDate = 1704088800;

    uint256 public fundsLockingLimit = 67;

    IMintable public collection;

    constructor(address _collectionAddress, address _fundsReceiver, address _fundsReceiver2){
        collection = IMintable(_collectionAddress);
        fundsReceiver = _fundsReceiver;
        fundsReceiver2 = _fundsReceiver2;
    }

    function mint(address to, uint256[] memory tokenIds) external payable nonReentrant {
        require(!paused(), "is on pause !");
        require(tokenIds.length>0,"Bad parameter");
        require(to!=address(0),"Bad parameter");
        require(collection.balanceOf(to) + tokenIds.length <= maxNftPerWallet, "Max token par wallet limit");                                
        require(msg.value == tokenPrice * tokenIds.length ,"bad price");
        collection.mints(to,tokenIds);
    }

    function setMaxNftPerWallet(uint256 _maxNftPerWallet) external onlyOwner {
        require(_maxNftPerWallet>0,"Bad parameter");
        maxNftPerWallet = _maxNftPerWallet;
    }

    function retrieveFunds(uint256 _amount) external {
        require(
            msg.sender == fundsReceiver ||
            msg.sender == fundsReceiver2,
            "Not allowed"
        );
        require(_amount > 0 && _amount <= address(this).balance,"Bad eth amount");
        if(block.timestamp < fundsLockingEndDate){
            require(collection.totalSupply() >= fundsLockingLimit,"Funds are locked");
        }
        payable(msg.sender).sendValue(_amount);
    }

    function getContractData() public view returns (uint256,uint256) {        
        return (collection.totalSupply(),tokenPrice);
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

}"}}
