      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.23;

// Uncomment this line to use console.log
// import "hardhat/console.sol";

// Inherited
import "@openzeppelin/contracts/access/Ownable.sol";

// Inherited
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract MigrationLock is Ownable {
    uint256 public expireTime;
    bool public migrationEnabled;
    IERC20 public sourceToken;
    IERC20 public destinationToken;

    mapping(address => uint256) public bank;

    constructor(address _sourceToken, address _initialOwner) Ownable(_initialOwner) {
        sourceToken = IERC20(_sourceToken);
    }

	/*
	 * Lock source token in the contract.
	 * NOTE:
	 *  User must first approve transfer of tokens.
	 *  _numTokens is denominated in *wei*.
	 */
	function lockTokens(uint256 _numTokens) external {
        require(block.timestamp < expireTime, "Locking time has expired.");
        /*
         * Don't perform any check on the token amount, in order to save on gas.
         * The following function will revert if any issue.
         */
        sourceToken.transferFrom(msg.sender, address(this), _numTokens);
		bank[msg.sender] += _numTokens;
	}

	/*
	 * Migrate source token to the destination token.
	 */
    function migrateTokens() public {
        require(migrationEnabled, "Migration is not enabled.");
        uint256 amount = bank[msg.sender];
        bank[msg.sender] = 0;
        destinationToken.transfer(msg.sender, amount);
    }

    /*
    * Get expire time for the lock.
    */
    function getExpireTime() external view returns (uint256) {
        return expireTime;
    }

    /*
    * Get locked balance for an address.
    */
    function getLockedBalance(address _address) external view returns (uint256) {
        return bank[_address];
    }

	/*
	 * Set expire time for the lock.
	 */
    function setExpireTime(uint256 _expireTime) external onlyOwner {
        expireTime = _expireTime;
    }

	/*
	 * Migrate source token to the destination token.
	 */
	function setMigrationEnabled(bool _status) external onlyOwner {
		migrationEnabled = _status;
	}

	/*
	 * Set the destination token.
	 */
    function setDestinationToken(address _destinationToken) external onlyOwner {
        destinationToken = IERC20(_destinationToken);
    }

	/*
	 * Drain tokens from the contract.
	 */
	function drainTokens(address _token, address _to) external onlyOwner {
		IERC20(_token).transfer(_to, IERC20(_token).balanceOf(address(this)));
	}
}
