contract BBB is ERC721A, Ownable {
  using Strings for uint256;

  string private uriPrefix = "ipfs://Qmew98RVUMXBHVXdTg17ySPPN2rgfdMAm1dewATLgTeSr5/";
  string private uriSuffix = ".json";
  
  uint256 public price = 0.01 ether;
  uint256 public maxSupply = 1000; 
  uint256 public maxMintAmountPerTx = 5;
  uint256 public maxMintPerWallet = 5; 
  
  mapping(address => uint256) private addressMintedBalance;
  mapping(address => uint) private whitelistedAddresses;

  constructor() ERC721A("Bulls Bulls Bulls", "BBB", maxMintAmountPerTx) {
  }

  modifier mintCompliance(uint256 _mintAmount) {
    require(_mintAmount > 0 && _mintAmount <= maxMintAmountPerTx, "Invalid mint amount!");
    require(_mintAmount + balanceOf(msg.sender) <= maxMintPerWallet, "Unable to mint that many NFTs!");
    require(currentIndex + _mintAmount <= maxSupply, "Max supply exceeded!");
    _;
  }

  modifier onlyOwner() override {
    require(owner() == _msgSender(), "Ownable: caller is not the owner/dev");
    _;
  }
 
  function mint(uint256 _mintAmount) public payable mintCompliance(_mintAmount)
  {
    require(msg.value >= price * _mintAmount, "Insufficient funds!");
    _safeMint(msg.sender, _mintAmount);
  }
 
  function walletOfOwner(address _owner)
    public
    view
    returns (uint256[] memory)
  {
    uint256 ownerTokenCount = balanceOf(_owner);
    uint256[] memory ownedTokenIds = new uint256[](ownerTokenCount);
    uint256 currentTokenId = 0;
    uint256 ownedTokenIndex = 0;

    while (ownedTokenIndex < ownerTokenCount && currentTokenId <= maxSupply) {
      address currentTokenOwner = ownerOf(currentTokenId);

      if (currentTokenOwner == _owner) {
        ownedTokenIds[ownedTokenIndex] = currentTokenId;

        ownedTokenIndex++;
      }

      currentTokenId++;
    }

    return ownedTokenIds;
  }

  function tokenURI(uint256 _tokenId)
    public
    view
    virtual
    override
    returns (string memory)
  {
    require(
      _exists(_tokenId),
      "ERC721Metadata: URI query for nonexistent token"
    );

    string memory currentBaseURI = _baseURI();
    return bytes(currentBaseURI).length > 0
        ? string(abi.encodePacked(currentBaseURI, (_tokenId+1).toString(), uriSuffix))
        : "";
  }

  function setPrice(uint256 _price) public onlyOwner {
    price = _price;
  }

  function _baseURI() internal view virtual override returns (string memory) {
    return uriPrefix;
  }

  function withdraw() public onlyOwner {
    (bool ownerPayout, ) = payable(owner()).call{value: address(this).balance}("");
    require(ownerPayout); 
