/**
 * @title Ownable
 * @dev The Ownable contract has an owner address, and provides basic authorization control
 * functions, this simplifies the implementation of "user permissions".
 */
contract Ownable {
    address private _owner;

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    /**
     * @dev The Ownable constructor sets the original `owner` of the contract to the sender
     * account.
     */
    constructor () internal {
        _owner = msg.sender;
        emit OwnershipTransferred(address(0), _owner);
    }

    /**
     * @return the address of the owner.
     */
    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(isOwner(), "Ownable: caller is not the owner");
        _;
    }

    /**
     * @return true if `msg.sender` is the owner of the contract.
     */
    function isOwner() public view returns (bool) {
        return msg.sender == _owner;
    }

    /**
     * @dev Allows the current owner to relinquish control of the contract.
     * It will not be possible to call the functions with the `onlyOwner`
     * modifier anymore.
     * @notice Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    /**
     * @dev Allows the current owner to transfer control of the contract to a newOwner.
     * @param newOwner The address to transfer ownership to.
     */
    function transferOwnership(address newOwner) public onlyOwner {
        _transferOwnership(newOwner);
    }

    /**
     * @dev Transfers control of the contract to a newOwner.
     * @param newOwner The address to transfer ownership to.
     */
    function _transferOwnership(address newOwner) internal {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        emit OwnershipTransferred(_owner, newOwner);
        _owner = newOwner;
    }
}

// File: contracts/SatoshiGeneric.sol

contract SatoshiGeneric is ERC721, Ownable {
    string public constant name = "$STCL Brand Swag";
    string public constant symbol = "STCL";

    struct Item {
        string itemName;
        string itemType;
        string color;
        string size;
        string year;
    }

    uint128 MAX_ITEMS = 9999;
    // array of items
    Item[] items;

    function implementsERC721() public pure returns (bool) {
        return true;
    }

    function baseTokenURI() internal pure returns (string memory) {
        return "https://satoshiscloset.com/SatoshiGeneric_";
    }

    function tokenURI(uint256 _tokenId) external pure returns (string memory) {
        return Strings.strConcat(
            baseTokenURI(),
            Strings.uint2str(_tokenId),
            ".json"
        );
    }

    function getItem( uint _itemId ) public view returns(string memory itemName, string memory itemType, string memory size, string memory year, string memory color) {
        Item memory _item = items[_itemId];

        itemName = _item.itemName;
        itemType = _item.itemType;
        size = _item.size;
        year = _item.year;
        color = _item.color;
    }

    function totalSupply() public view returns (uint) {
        return items.length;
    }

    function createItem( string calldata _name, string calldata _type, string calldata _size, string calldata _year, string calldata _color) external onlyOwner returns (uint) {
        require(MAX_ITEMS > totalSupply());

        Item memory _item = Item({
            itemName: _name,
            itemType: _type,
            size: _size,
            year: _year,
            color: _color
        });
        uint itemId = items.push(_item);

        _mint(owner(), itemId);

        return itemId;
    }
}
