contract EXZO is Context, IERC20, IERC20Metadata, Ownable {
    using SafeMath for uint256;
    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) private _isAutomaticMarketMaker;
    mapping(address => bool) public isBlackListed; 
    address payable public donationWallet = payable(0x625b32050059E571E3E833454Baa3f88A8ba7981);

    uint256 public MaxSupply;
    uint256 private _totalSupply;
    string private _name;
    string private _symbol;
    uint8 private _decimals;

    uint256 public _liquidityFee = 0;
    uint256 public _donationFee = 0;
    uint256 public _burnFee = 0;
    /**
     * @dev Sets the values for {name}, {symbol} and {decimals}.
     * All two of these values are immutable: they can only be set once during
     * construction.
     */

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    uint256 public _maxTxAmount = 10 * 10**6 * 10**18;

    event TaxFeeUpdated(uint256 totalFee);
    event MaxTxAmountUpdated(uint256 updatingTxAmount);
    event MinTokensBeforeSwapUpdated(uint256 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    constructor() {
        _name = "Exzo";
        _symbol = "XZO";
        _decimals = 18;
        MaxSupply = 135_000_000 * 10**_decimals;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D // uniswap v2 router
        );

        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());
        uniswapV2Router = _uniswapV2Router;

        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[donationWallet] = true;
        _isAutomaticMarketMaker[uniswapV2Pair] = true;

        _mint(_msgSender(), 10);
    }

    function name() public view virtual override returns (string memory) {
        return _name;
    }

    function symbol() public view virtual override returns (string memory) {
        return _symbol;
    }

    function decimals() public view virtual override returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view virtual override returns (uint256) {
        return _totalSupply;
    }

    function setDonationWallet(address payable _donationWallet) external onlyOwner() {
        donationWallet = _donationWallet;
    }

    function balanceOf(address account)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        virtual
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        virtual
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external override returns (bool) {
        require(to != address(0) || to != address(this));
        if (from != msg.sender) {
            uint256 allowed = _allowances[from][msg.sender];
            if (allowed != type(uint256).max) {
                require(
                    allowed >= value,
                    "Exzo: request exceeds allowance"
                );
                uint256 reduced = allowed - value;
                _allowances[from][msg.sender] = reduced;
                emit Approval(from, msg.sender, reduced);
            }
        }
        _transfer(from, to, value);
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender] + addedValue
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        uint256 currentAllowance = _allowances[_msgSender()][spender];
        require(
            currentAllowance >= subtractedValue,
            "Exzo: Decreased allowance below zero"
        );
        _approve(_msgSender(), spender, currentAllowance - subtractedValue);

        return true;
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "Exzo: Transfer from the zero address");
        require(recipient != address(0), "Exzo: Transfer to the zero address");
        require(isBlackListed[sender] == false, "Exzo: Cannot transfer tokens from this address");
        require(isBlackListed[recipient] == false, "Exzo: Cannot transfer tokens to this address");

        uint256 _DonationAmt;
        uint256 _liquidityAmt;
        uint256 _burnAmt;
        if (
            _isAutomaticMarketMaker[sender] ||
            _isAutomaticMarketMaker[recipient]
        ) {
            if (_isExcludedFromFee[sender] || _isExcludedFromFee[recipient]) {
                _DonationAmt = 0;
                _liquidityAmt = 0;
                _burnAmt = 0;
            } else {
                require(amount <= _maxTxAmount, "Exzo: Transaction limit Exceeded!");
                _DonationAmt = amount.mul(_donationFee).div(10000);
                _liquidityAmt = amount.mul(_liquidityFee).div(10000);
                _burnAmt = amount.mul(_burnFee).div(10000);
            }
        } else {
            _DonationAmt = 0;
            _liquidityAmt = 0;
            _burnAmt = 0;
        }

        uint256 senderBalance = _balances[sender];
        require(
            senderBalance >= amount,
            "Exzo: Transfer amount exceeds balance"
        );
        _balances[sender] = senderBalance - amount;
        _balances[donationWallet] += _DonationAmt;
        _balances[address(this)] += _liquidityAmt;
        if (_burnAmt > 0) {
            _totalSupply -= _burnAmt;
            emit Transfer(sender, address(0), _burnAmt);
        }
        _balances[recipient] += amount.sub(_DonationAmt).sub(_liquidityAmt).sub(
            _burnAmt
        );
        if (_DonationAmt > 0) {
            emit Transfer(sender, donationWallet, _DonationAmt);
        }
        emit Transfer(
            sender,
            recipient,
            amount.sub(_DonationAmt).sub(_liquidityAmt).sub(_burnAmt)
        );
    }

    function addToBlacklist(
        address[] calldata _address, 
        bool[] calldata _value
        ) external onlyOwner {
        require(_address.length == _value.length, "Exzo: Arrays must be the same length");
        for(uint i = 0; i < _address.length; i++) {
