    function pluck(
        uint256 tokenId,
        string memory keyPrefix,
        string[] memory sourceArray
    ) internal view returns (string memory, uint8) {
        uint256 rand = random(string(abi.encodePacked(keyPrefix, toString(tokenId))));

        // Create weighted distribution so items decrease in rarity in descending order of the list
        uint8[] memory weights = new uint8[](sourceArray.length);
        uint256 weightSum = (sourceArray.length * (sourceArray.length + 1)) / 2;
        for (uint8 i = 0; i < sourceArray.length; i++) {
            weights[i] = i + 1;
        }

        uint16 sum = 0;
        uint256 randIdx = rand % weightSum;
        for (uint8 i = 0; i < weights.length; i++) {
            sum += weights[i];
            if (randIdx < sum) {
                // Return item string and rarityColor index of item in SVG
                return (sourceArray[i], i / 2 > 4 ? 4 : i / 2);
            }
        }

        return (sourceArray[rand % sourceArray.length], 4);
    }

