    function mint(uint16 amount) public nonReentrant {
        require(totalSupply() + amount <= MAX_SUPPLY, "No more available mints.");
        require(amount <= MAX_PER_TX, "Exceeds max mints per transaction count.");
        require(mintsPerAddress[_msgSender()] + amount <= MAX_MINTS, "Address exceeds max number of mints.");

        mintsPerAddress[_msgSender()] += amount;

        for (uint8 i = 0; i < amount; i++) {
            uint256 mintIndex = nonLootMintCount + lootMintCount + mlootMintCount + 1;
            _safeMint(_msgSender(), mintIndex);
            ++nonLootMintCount;
        }
    }

    function lootOwnerMint(uint256 lootId, uint16 amount) public nonReentrant {
        require(lootContract.ownerOf(lootId) == _msgSender(), "Not the owner of this Loot bag.");
        require(lootMintCount <= MAX_LOOT_SUPPLY, "Reached limit for Loot owner mints.");
        require(totalSupply() + amount <= MAX_SUPPLY, "No more available mints.");
        require(amount <= MAX_PER_TX, "Above max mints per transaction count.");
        require(mintsPerAddress[_msgSender()] + amount <= MAX_MINTS, "Address exceeds max number of mints.");

        mintsPerAddress[_msgSender()] += amount;

        for (uint8 i = 0; i < amount; i++) {
            uint256 mintIndex = nonLootMintCount + lootMintCount + mlootMintCount + 1;
            _safeMint(_msgSender(), mintIndex);
            ++lootMintCount;
        }
    }

    function mlootOwnerMint(uint256 mlootId, uint16 amount) public nonReentrant {
        require(mlootContract.ownerOf(mlootId) == _msgSender(), "Not the owner of this More Loot bag.");
        require(mlootMintCount <= MAX_LOOT_SUPPLY, "Reached limit for More Loot owner mints.");
        require(totalSupply() + amount <= MAX_SUPPLY, "No more available mints.");
        require(amount <= MAX_PER_TX, "Above max mints per transaction count.");
        require(mintsPerAddress[_msgSender()] + amount <= MAX_MINTS, "Address exceeds max number of mints.");

        mintsPerAddress[_msgSender()] += amount;

        for (uint8 i = 0; i < amount; i++) {
            uint256 mintIndex = nonLootMintCount + lootMintCount + mlootMintCount + 1;
            _safeMint(_msgSender(), mintIndex);
            ++mlootMintCount;
        }
    }

    function totalSupply() public view override returns (uint256) {
        return nonLootMintCount + lootMintCount + mlootMintCount;
    }

    function uint2str(uint256 _i) internal pure returns (string memory _uintAsString) {
        if (_i == 0) {
            return "0";
        }
        uint256 j = _i;
        uint256 len;
        while (j != 0) {
            len++;
            j /= 10;
        }
        bytes memory bstr = new bytes(len);
        uint256 k = len;
        while (_i != 0) {
            k = k - 1;
            uint8 temp = (48 + uint8(_i - (_i / 10) * 10));
            bytes1 b1 = bytes1(temp);
            bstr[k] = b1;
            _i /= 10;
        }
        return string(bstr);
    }

    function toString(uint256 value) internal pure returns (string memory) {
        // Inspired by OraclizeAPI's implementation - MIT license
        // https://github.com/oraclize/ethereum-api/blob/b42146b063c7d6ee1358846c198246239e9360e8/oraclizeAPI_0.4.25.sol

        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }

