    "src/BoringSecurityLedger.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.8.20;

import {ERC721} from "lib/solady/src/tokens/ERC721.sol";
import {OwnableRoles} from "lib/solady/src/auth/OwnableRoles.sol";

contract BoringSecurityLedger is ERC721, OwnableRoles {
    event MetadataUpdate(uint256 _tokenId);

    string public baseURI = "ipfs://QmZJr19b53nGRC328vMYVfjY274yrotEe43GJ5oYVAEwmc/";
    uint256 currentTokenId;

    mapping(uint256 => bool) public isClaimed;

    constructor() {
        _initializeOwner(tx.origin);
    }

    function mintTo(address to) external onlyRolesOrOwner(_ROLE_69) {
        _mint(to, ++currentTokenId);
    }

    function setClaimed(uint256 tokenId, bool _isClaimed) external onlyRolesOrOwner(_ROLE_69) {
        isClaimed[tokenId] = _isClaimed;

        emit MetadataUpdate(tokenId);
    }

    function setbaseURI(string calldata newUri) external onlyOwner {
        baseURI = newUri;
    }

    function setAdmin(address user) external onlyOwner() {
        _grantRoles(user, _ROLE_69);
    }

    function revokeAdmin(address user) external onlyOwner() {
        _removeRoles(user, _ROLE_69);
    }

    function name() public pure override returns (string memory) {
        return "Boring Security Ledger";
    }

    function symbol() public pure override returns (string memory) {
        return "LEDGER";
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        if (!_exists(tokenId)) revert ERC721.TokenDoesNotExist();

        if (isClaimed[tokenId]) {
            return string(abi.encodePacked(baseURI, "claimed"));
        } else {
            return string(abi.encodePacked(baseURI, "unclaimed"));
        }
    }

    function totalSupply() external view returns (uint256) {
        return currentTokenId;
    }
}"
