// File: ElleriumToken.sol

contract ELLERIUM is ERC20, Ownable {
    uint256 internal constant ELLE_UNIT = 10**18; // Number of decimals
    uint256 internal constant MAX_SUPPLY = 10**7 * ELLE_UNIT; // Maximum token supply
    uint256 private _maxWithdraw = 10**4 * ELLE_UNIT; // Maximum withdraw amount
    uint256 private _withdrawCooldown = 60 * 60 * 1; // 1 Hour - Duration between each withdraw

    uint256 private constant TREASURY_ALLOCATION = 2 * 10**6 * ELLE_UNIT; // Treasury Allocation
    uint256 private constant HOLDERS_ALLOCATION = 2 * 10**6 * ELLE_UNIT; // Token Holders Allocation

    address private constant TREASURY_ADDR =
        0xEE4C020A688dE02EaABb3dc1Afc57539447370cB; // Wallet Address of the treasury

    mapping(address => uint256) private _withdrawCounter; // Mapping for Withdraw counter for holder
    mapping(address => uint256) private _lastWithdraw; // Mapping for last Withdraw for holder

    address private _signerAddress; // Address to validate signature

    /**
     * @dev Creates a ellerium ERC20 token contract.
     * @param signerAddress_ address of the signer who will sign the withdraw request.
     */
    constructor(address signerAddress_) ERC20("ELLERIUM", "ELLE") {
        setSignerAddress(signerAddress_);
        _mint(TREASURY_ADDR, TREASURY_ALLOCATION);
        _mint(address(this), HOLDERS_ALLOCATION);
    }

    /**
     * @dev Returns the amount of tokens allocated to treasury pool.
     */
    function treasuryAllocation() public pure returns (uint256) {
        return TREASURY_ALLOCATION;
    }

    /**
     * @dev Returns the treasury pool allocation address.
     */
    function treasuryAddr() public pure returns (address) {
        return TREASURY_ADDR;
    }

    /**
     * @dev Returns the amount of maximum tokens that can be minted.
     */
    function maxSupply() public pure returns (uint256) {
        return MAX_SUPPLY;
    }

    /**
     * @dev Returns the address of the current signer.
     */
    function signerAddress() public view returns (address) {
        return _signerAddress;
    }

    /**
     * @dev set the signer address with the new address (only owner).
     * @param address_ address of the signer who will sign the withdraw request.
     */
    function setSignerAddress(address address_) public onlyOwner {
        _signerAddress = address_;
    }

    /** @dev Creates `amount_` tokens and assigns them to `to_`, increasing the total supply (only owner).
     *
     * Emits a {Transfer} event with `from` set to the zero address.
     *
     * Requirements:
     *
     * - `to_` cannot be the zero address.
     */
    function mint(address to_, uint256 amount_) external onlyOwner {
        require(
            totalSupply() + amount_ <= MAX_SUPPLY,
            "can't mint more then max supply"
        );
        _mint(to_, amount_);
    }

    /**
     * @dev withdraw the specified amount from smart contract balance.
     *
     * Emits a {Transfer} event.
     *
     * @param timestamp_ time at which request was signed
     * @param amount_ total amount of tokens to be transferred.
     * @param sig_ ECDSA signature of message signed by platform.
     */
    function withdraw(
        uint256 timestamp_,
        uint256 amount_,
        bytes memory sig_
    ) external {
        require(block.timestamp <= timestamp_ + (15 * 60), "stale request");
        require(
            amount_ < _maxWithdraw,
            string.concat(
                "Cannot withdraw more than ",
                Strings.toString(_maxWithdraw)
            )
        );
        uint256 lastWithdrawDuration = block.timestamp -
            _lastWithdraw[msg.sender];
        require(
            lastWithdrawDuration > _withdrawCooldown,
            string.concat(
                "Please wait for cooldown: ",
                Strings.toString(_withdrawCooldown),
                ". Time since last withdraw: ",
                Strings.toString(lastWithdrawDuration)
            )
        );
        require(
            isValidSignature(
                timestamp_,
                msg.sender,
                amount_,
                _withdrawCounter[msg.sender],
                sig_
            ),
            "signature validation failed"
        );
        require(balanceOf(address(this)) >= amount_, "insufficient balance");
        _withdrawCounter[msg.sender] += 1;
        _lastWithdraw[msg.sender] = block.timestamp;
        _transfer(address(this), msg.sender, amount_);
    }

    /**
     * @dev Returns the withdraw counter for a specific user
     *
     * Get the value of _withdrawCounter for validation
     *
     * @param walletAddress_ wallet address of the user
     */
    function getWithdrawCounter(address walletAddress_)
        public
        view
        onlyOwner
        returns (uint256)
    {
        return _withdrawCounter[walletAddress_];
    }

    /**
     * @dev withdraw the specified amount from smart contract balance (only owner).
     *
     * Emits a {Transfer} event.
     *
     * @param to_ address of the beneficiary to whom tokens are transferred.
     * @param amount_ total amount of tokens to be transferred.
     */
    function withdrawForAdmin(address to_, uint256 amount_) external onlyOwner {
        require(balanceOf(address(this)) >= amount_, "insufficient balance");
        _transfer(address(this), to_, amount_);
    }

    /**
     * @dev Set cooldown time and maximum amount for Withdraw
     *
     * @param withdrawCooldownInSec_ Duration for withdraw cooldown (in seconds)
     * @param maxWithdraw_ Maximum amount that can be withdrawn at a time
     */
    function setWithdrawLimits(
        uint256 withdrawCooldownInSec_,
        uint256 maxWithdraw_
    ) public onlyOwner {
        _withdrawCooldown = withdrawCooldownInSec_;
        _maxWithdraw = maxWithdraw_ + 1;
    }

    function getWithdrawCooldown() public view returns (uint256) {
        return _withdrawCooldown;
    }

    function getMaxWithdraw() public view returns (uint256) {
        return _maxWithdraw;
    }

    /**
     * @dev Returns the last withdraw time for a specific user
     *
     * Get the value of _lastWithdraw for verification
     *
     * @param walletAddress_ wallet address of the user
     */
    function getLastWithdraw(address walletAddress_)
        public
        view
        returns (uint256)
    {
        return _lastWithdraw[walletAddress_];
    }

    function isValidSignature(
        uint256 timestamp_,
        address walletAddress_,
        uint256 amount_,
        uint256 counter_,
        bytes memory sig_
    ) internal view returns (bool) {
        bytes32 message = keccak256(
            abi.encodePacked(timestamp_, walletAddress_, amount_, counter_)
        );
        return (recoverSigner(message, sig_) == _signerAddress);
    }

    function recoverSigner(bytes32 message_, bytes memory sig_)
        internal
        pure
        returns (address)
    {
        uint8 v;
        bytes32 r;
        bytes32 s;
        (v, r, s) = splitSignature(sig_);
        return ecrecover(message_, v, r, s);
    }

    function splitSignature(bytes memory sig_)
