        pure
        returns (
            uint8,
            bytes32,
            bytes32
        )
    {
        require(sig_.length == 65);
        bytes32 r;
        bytes32 s;
        uint8 v;
        assembly {
            // First 32 bytes, after the length prefix
            r := mload(add(sig_, 32))

            // Second 32 bytes
            s := mload(add(sig_, 64))

            // Sinal byte (first byte of the next 32 bytes)
            v := byte(0, mload(add(sig_, 96)))
        }
        return (v, r, s);
    }
