    error Blacklisted();

    /// @notice Thrown when updating an address with zero address
    error ZeroAddress();

    /// @notice Thrown when buy is disabled
    error BuyNotEnable();

    /// @notice Thrown when claim is disabled
    error ClaimNotEnable();

    /// @notice Thrown when signature is invalid
