pragma solidity 0.8.17;






contract DiMoStaking is BaseStaking {
    using SafeERC20 for IERC20;
    
    IERC20 public dmoToken;

    struct PoolLimit {
        uint256 minDeposit;
        uint256 lockPeriod;
    }

    mapping(uint256 => PoolLimit) public poolLimits;

    // user address => poolId => deposit timestamp
    mapping(address => mapping(uint256 => uint256)) public depositTimestamp;

    uint256 public constant ACC_REWARD_PRECISION = 1e12;

    function initialize(
        IERC20 _dmoToken,
        IERC20 _rewardToken,
        address _devaddr,
        uint256 _rewardPerSecond,
        uint256 _startTime,
        uint256 _allEndTime
    ) public {
        initialize(_rewardToken, _devaddr, _rewardPerSecond, _startTime, _allEndTime);
        dmoToken = _dmoToken;
    }

    function addPool(uint256 _allocPoint, bool _withUpdate, uint256 minDeposit, uint256 lockPeriod) public {
        StDMO newToken = new StDMO();
        uint pid = poolInfo.length;
        poolLimits[pid] = PoolLimit(minDeposit, lockPeriod);
        // check admin in this function
        add(_allocPoint, IERC20(newToken), _withUpdate); 
    }

    function setPool(uint256 _pid, uint _allocPoint, bool _withUpdate, uint256 minDeposit, uint256 lockPeriod) public {
        poolLimits[_pid] = PoolLimit(minDeposit, lockPeriod);
        // check admin in this function
        set(_pid, _allocPoint, _withUpdate);
    }

    function deposit(uint256 _pid, uint256 _amount) public override nonReentrant {
        require(_amount >= poolLimits[_pid].minDeposit, "deposit: amount is less than minDeposit");
        require(_pid < poolInfo.length, "deposit: invalid pid");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        if (user.amount > 0) {
            uint256 pending = user.amount * pool.accRewardPerShare / ACC_REWARD_PRECISION - user.rewardDebt;
            safeRewardTransfer(msg.sender, pending);
        }
        if(_amount > 0) {
            dmoToken.safeTransferFrom(msg.sender, address(this), _amount);
            IERC20 token = poolInfo[_pid].lpToken;
            StDMO(address(token)).mint(address(this), _amount);
            user.amount = user.amount + _amount;
        }
        user.rewardDebt = user.amount * pool.accRewardPerShare / ACC_REWARD_PRECISION;
        depositTimestamp[msg.sender][_pid] = block.timestamp;
        emit Deposit(msg.sender, _pid, _amount);
    }

    function withdraw(uint256 _pid, uint256 _amount) public override nonReentrant {
        require(block.timestamp >= (depositTimestamp[msg.sender][_pid] + poolLimits[_pid].lockPeriod) || _amount == 0, "withdraw: lock period is not over");
        require(_pid < poolInfo.length, "withdraw: invalid pid");

        PoolInfo storage pool = poolInfo[_pid];
        UserInfo storage user = userInfo[_pid][msg.sender];
        updatePool(_pid);
        uint256 pending = user.amount * pool.accRewardPerShare / ACC_REWARD_PRECISION - user.rewardDebt;
        user.amount = user.amount - _amount;
        user.rewardDebt = user.amount * pool.accRewardPerShare / ACC_REWARD_PRECISION;
        safeRewardTransfer(msg.sender, pending);

        IERC20 token = poolInfo[_pid].lpToken;
        StDMO(address(token)).burn(_amount);
        dmoToken.safeTransfer(msg.sender, _amount);

        emit Withdraw(msg.sender, _pid, _amount);
    }
}
