pragma solidity ^0.8.14;
contract OptimisticBlockRouter is IBlockRouter, Initializable, OwnableUpgradeable {
    struct BlockInput {
        uint16 chainId;
        uint256 blockNumber;
        uint256 blockConfirmation;
        bytes32 syncCommitteeRoot;
        bytes32 receiptHash;
        bytes32 blockHash;
    }

    struct BlockProof {
        uint256[2] a;
        uint256[2][2] b;
        uint256[2] c;
        uint256[] inputs;
    }

    uint256 public immutable publicInputSize;

    bytes32 public immutable syncCommitteeRoot;

    mapping(uint256 => ICircuitVerifier) public blockVerifier;

    mapping(uint16 => IMixtureBlockUpdater) public blockUpdater;

    constructor(bytes32 _syncCommitteeRoot, uint256 _publicInputSize) {
        syncCommitteeRoot = _syncCommitteeRoot;
        publicInputSize = _publicInputSize;
    }

    function initialize() public initializer {
        __Ownable_init();
    }

    function importBlock(bytes calldata _proof) external {
        BlockProof memory proofData;
        (proofData.a, proofData.b, proofData.c, proofData.inputs) = abi.decode(_proof, (uint256[2], uint256[2][2], uint256[2], uint256[]));

        uint256 blockSize = proofData.inputs.length / publicInputSize;
        require(blockSize > 0 && proofData.inputs.length == blockSize * publicInputSize, "invalid public input");
        require(address(blockVerifier[blockSize]) != address(0), "not set verifier");

        BlockInput[] memory parsedInputs = _parseBlockInput(proofData.inputs, blockSize);
        uint256 realBlockSize = blockSize;
        for (uint256 i = 0; i < blockSize; i++) {
            if (i > 0 && parsedInputs[i].blockHash == parsedInputs[i - 1].blockHash) {
                realBlockSize = i;
                break;
            }
            require(syncCommitteeRoot == parsedInputs[i].syncCommitteeRoot, "invalid committeeRoot");
            require(address(blockUpdater[parsedInputs[i].chainId]) != address(0), "invalid chainId");
        }

        uint256[1] memory compressInput;
        compressInput[0] = _hashBlockInput(proofData.inputs);
        require(blockVerifier[blockSize].verifyProof(proofData.a, proofData.b, proofData.c, compressInput), "invalid proof");

        for (uint256 i = 0; i < realBlockSize; i++) {
            blockUpdater[parsedInputs[i].chainId].importBlock(parsedInputs[i].blockNumber, parsedInputs[i].blockHash, parsedInputs[i].receiptHash, parsedInputs[i].blockConfirmation);
        }
    }

    function checkBlock(uint16 chainId, bytes32 _blockHash, bytes32 _receiptHash) external view returns (bool) {
        (bool exist,) = _checkBlock(chainId, _blockHash, _receiptHash);
        return exist;
    }

    function checkBlockConfirmation(uint16 _chainId, bytes32 _blockHash, bytes32 _receiptHash) external view returns (bool, uint256) {
        return _checkBlock(_chainId, _blockHash, _receiptHash);
    }

    function _checkBlock(uint16 _chainId, bytes32 _blockHash, bytes32 _receiptHash) internal view returns (bool, uint256) {
        return blockUpdater[_chainId].checkBlockConfirmation(_blockHash, _receiptHash);
    }

    function _parseBlockInput(uint256[] memory _inputs, uint256 _blockSize) internal pure returns (BlockInput[] memory) {
        uint256 index = 0;
        BlockInput[] memory result = new BlockInput[](_blockSize);
        for (uint256 i = 0; i < _blockSize; i++) {
            result[i].blockNumber = _inputs[index];
            index++;
            result[i].syncCommitteeRoot = bytes32(_inputs[index]);
            index++;
            result[i].receiptHash = bytes32((_inputs[index] << 128) | _inputs[index + 1]);
            index += 2;
            result[i].blockHash = bytes32((_inputs[index] << 128) | _inputs[index + 1]);
            index += 2;
            result[i].blockConfirmation = _inputs[index];
            index++;
            result[i].chainId = uint16(_inputs[index]);
            index++;
        }
        return result;
    }

    function _hashSyncCommitteeInput(uint256[3] memory _inputs) internal pure returns (uint256) {
        uint256 computedHash = uint256(keccak256(abi.encodePacked(_inputs[0], _inputs[1], _inputs[2])));
        return computedHash / 256;
    }

    function _hashBlockInput(uint256[] memory _inputs) internal pure returns (uint256) {
        uint256 n = _inputs.length;
        uint256 inputLength = _inputs.length * 32;
        bytes memory packedInputs;
        assembly {
            packedInputs := mload(0x40) // Get the free memory pointer
            mstore(0x40, add(packedInputs, add(inputLength, 0x20))) // Update the free memory pointer

            let inputOffset := packedInputs
            mstore(inputOffset, inputLength) // Store the length of the concatenated inputs
            inputOffset := add(inputOffset, 0x20) // Move the pointer to the start of the concatenated inputs

            for {let i := 0} lt(i, n) {i := add(i, 1)} {
                let inputValue := mload(add(_inputs, mul(add(i, 1), 0x20))) // Load the input value
                mstore(inputOffset, inputValue) // Store the input value at the current offset
                inputOffset := add(inputOffset, 0x20) // Move the pointer to the next position
            }
        }
        uint256 computedHash = uint256(keccak256(packedInputs));
        return computedHash / 256;
    }

    //----------------------------------------------------------------------------------
    // onlyOwner
    function setBlockVerifier(uint256 _blockSize, address _blockVerifier) external onlyOwner {
        require(_blockVerifier != address(0), "Zero address");
        blockVerifier[_blockSize] = ICircuitVerifier(_blockVerifier);
    }

    function setBlockUpdater(uint16 _chainId, address _blockUpdater) external onlyOwner {
        require(_blockUpdater != address(0), "Zero address");
        blockUpdater[_chainId] = IMixtureBlockUpdater(_blockUpdater);
    }

}
