pragma solidity 0.8.16;

import { ILockerMaster } from "../interfaces/ILockerMaster.sol";
import { ILockerToken } from "../interfaces/ILockerToken.sol";
import { ILiquidLocker, SafeLocker } from "../libraries/SafeLocker.sol";

import { Initializable } from "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import { IERC20, SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";


contract LockerMaster is ILockerMaster, Initializable {
    using SafeERC20 for IERC20;
    using SafeERC20 for ILockerToken;
    using SafeLocker for ILiquidLocker;

    bytes32 internal constant _ROLES_SLOT = bytes32(uint256(keccak256("spiral.finance.mastermind.roles")) - 1);
    bytes32 internal constant _LIQUID_LOCKER_SLOT = bytes32(uint256(keccak256("spiral.finance.mastermind.liquidlocker")) - 1);

    constructor() {
        _disableInitializers();
    }

    function _roles() internal pure returns (RolesSlot storage r) {
        bytes32 slot = _ROLES_SLOT;
        assembly {
            r.slot := slot
        }
    }

    function _locker() internal pure returns (LiquidLockerSlot storage r) {
        bytes32 slot = _LIQUID_LOCKER_SLOT;
        assembly {
            r.slot := slot
        }
    }

    function owner() external view override returns (address) {
        return _roles().owner;
    }

    function service() external view override returns (address) {
        return _roles().service;
    }

    modifier onlyOwner() {
        require(_roles().owner == msg.sender, "not an owner");
        _;
    }

    modifier onlyService() {
        require( _roles().service == msg.sender || _roles().owner == msg.sender, "not a service");
        _;
    }

    function updateService(address newService) external override onlyService {
        _roles().service = newService;
    }

    function updateOwner(address newOwner) external override onlyOwner {
        _roles().owner = newOwner;
    }

    function lockerInfo() external view returns (LiquidLockerSlot memory) {
        return _locker();
    }

    function init(address newOwner) external initializer {
        _roles().owner = newOwner;
    }

    /*
     *  @notice param unlockTime: zero-value means increasing unlock time to maximum on future locks
     */
    function initLocker(
        address locker,
        address mintableToken,
        address lockableToken_,
        uint256 unlockTime
    ) external onlyOwner {
        require(
            locker != address(0) &&
            mintableToken != address(0) &&
            lockableToken_ != address(0)
        );
        LiquidLockerSlot storage l = _locker();

        if (l.mintableToken != address(0)) revert LockerExists();

        l.locker = locker;
        l.mintableToken = mintableToken;
        l.lockableToken = lockableToken_;
        l.unlockTime = unlockTime;

        IERC20(lockableToken_).safeApprove(ILiquidLocker(locker).getTarget(), type(uint256).max);
        IERC20(lockableToken_).safeTransferFrom(msg.sender, address(this), 1e18);
        ILiquidLocker(l.locker).initializeLock(1e18, unlockTime);

        emit InitLocker(locker);
    }

    function _checkUnlockTime(uint256 unlockTime, uint256 newUnlockTime) internal view {
        if (
            (unlockTime >= newUnlockTime || block.timestamp > newUnlockTime) &&
            newUnlockTime != 0
        ) revert ILiquidLocker.WrongUnlockTime();
    }

    /*
     *  @notice param newUnlockTime: zero-value means increasing unlock time to maximum on every future lock
     */
    function updateUnlockTime(uint256 newUnlockTime) external onlyService {
        LiquidLockerSlot storage l = _locker();
        _checkUnlockTime(l.unlockTime, newUnlockTime);
        l.unlockTime = newUnlockTime;
        emit UpdateLocker();
    }

    function enableLocker(address locker_, uint256 newUnlockTime) external onlyOwner {
        LiquidLockerSlot storage l = _locker();
        l.locker = locker_;
        if (newUnlockTime != 0) {
            _checkUnlockTime(l.unlockTime, newUnlockTime);
            l.unlockTime = newUnlockTime;
        }
        IERC20(l.lockableToken).safeApprove(ILiquidLocker(locker_).getTarget(), type(uint256).max);
        emit EnableLocker();
    }

    function disableLocker() external onlyService {
        LiquidLockerSlot storage l = _locker();
        IERC20(l.lockableToken).safeApprove(ILiquidLocker(l.locker).getTarget(), 0);
        _locker().locker = address(0);
        emit DisableLocker();
    }

    function execLock(bytes calldata payload) external onlyService {
        ILiquidLocker(_locker().locker).execLock(payload);
    }

    function lock(uint256 amount) external {
        require(amount > 0);
        LiquidLockerSlot storage l = _locker();
        if (amount > 0) {
            IERC20(l.lockableToken).safeTransferFrom(msg.sender, address(this), amount);
            ILockerToken(l.mintableToken).mint(msg.sender, amount);
        }
        ILiquidLocker(l.locker).lockToken(amount, l.unlockTime);
        emit Lock(msg.sender, amount);
    }

    /*
     * @param amount: zero-value is ok => in this case lock time is simple extended to max time
     */
    function serviceLock(uint256 amount) external onlyOwner {
        LiquidLockerSlot storage l = _locker();
        if (amount > 0) {
            IERC20(l.lockableToken).safeTransferFrom(msg.sender, address(this), amount);
        }
        ILiquidLocker(l.locker).lockToken(amount, l.unlockTime);
        emit Lock(msg.sender, amount);
    }

    function releaseLock(address token, uint256 amount, bytes calldata payload) external onlyOwner {
        LiquidLockerSlot storage l = _locker();
        ILiquidLocker(l.locker).releaseLock(token, amount, payload);
        emit Release(msg.sender, amount);
    }
}
