interface ILockerMaster {
    struct RolesSlot {
        address owner;
        address service;
    }

    struct LiquidLockerSlot {
        address locker;
        address mintableToken;
        address lockableToken;
        uint256 unlockTime;
    }

    event InitLocker(address indexed locker);
    event UpdateLocker();
    event EnableLocker();
    event DisableLocker();
    event Lock(address indexed user, uint256 amount);
    event Release(address indexed user, uint256 amount);

    /* Views */
    function owner() external view returns (address);
    function service() external view returns (address);
    function lockerInfo() external view returns (LiquidLockerSlot memory);

    /* Mutators */
    function updateService(address newService) external;
    function updateOwner(address newOwner) external;
    /* Errors */
    error NotOwner();
    error NotService();
    error LockerExists();
    error NeedToMintGenesis();
}"
