pragma solidity ^0.8.0;

import "../interfaces/ILiquidLocker.sol";

library SafeLocker {
    error LockGenesisFailed(ILiquidLocker locker, address user, uint256 amount, uint256 unlockTime, string reason);
    error LockFailed(ILiquidLocker locker, address user, uint256 amount, uint256 unlockTime, string reason);
    error ReleaseFailed(ILiquidLocker locker, address user, uint256 amount, string reason);
    error LockedAmount(ILiquidLocker locker, string reason);
    error Target(ILiquidLocker locker, string reason);
    error ExecutionFailed(ILiquidLocker locker, string reason);

    function getTarget(ILiquidLocker locker) internal view returns (address) {
        (bool success, bytes memory result) = address(locker).staticcall(abi.encodeWithSignature("target()"));
        if(!success) revert LockedAmount(locker, string(result));
        return abi.decode(result, (address));
    }

    function lockedAmount(ILiquidLocker locker) internal view returns (uint256) {
        (bool success, bytes memory result) = address(locker).staticcall(abi.encodeWithSignature("locked()"));
        if(!success) revert LockedAmount(locker, string(result));
        return abi.decode(result, (uint256));
    }

    function initializeLock(ILiquidLocker locker, uint256 amount, uint256 unlockTime) internal {
        (bool success, bytes memory returndata) = address(locker).delegatecall(abi.encodeWithSignature("initialize(uint256,uint256)", amount, unlockTime));
        if(!success) revert LockGenesisFailed(locker, msg.sender, amount, unlockTime, string(returndata));
    }

    function lockToken(ILiquidLocker locker, uint256 amount, uint256 unlockTime) internal returns(uint256){
        (bool success, bytes memory returndata) = address(locker).delegatecall(abi.encodeWithSignature("lock(uint256,uint256)", amount, unlockTime));
        if(!success) revert LockFailed(locker, msg.sender, amount, unlockTime, string(returndata));
        return abi.decode(returndata, (uint256));
    }

    function releaseLock(ILiquidLocker locker, address token, uint256 amount, bytes calldata payload) internal {
        (bool success, bytes memory returndata) = address(locker).delegatecall(abi.encodeWithSignature("release(address,uint256,bytes)", token, amount, payload));
        if(!success) revert ReleaseFailed(locker, msg.sender, amount, string(returndata));
    }

    function execLock(ILiquidLocker locker, bytes calldata payload) internal {
        (bool success, bytes memory returndata) = address(locker).delegatecall(abi.encodeWithSignature("exec(bytes)", payload));
        if(!success) revert ExecutionFailed(locker, string(returndata));
    }
}"
