        for (uint256 i = 0; i < _baseURIsPermanentIndex;) {
            uris[index] = string(abi.encodePacked(_prefixBaseURIsPermanent[i], tokenId.toString(), _suffixBaseURIsPermanent[i]));
            unchecked {
                index++;
                i++;
            }
        }

