// File: ManillaPresaleETH.sol


pragma solidity ^0.8.21;






contract ManillaPresale is Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    address private walletForReceivedCoins;
    address private usdtErc20Token;
    address private roundChangeAdmin;

    uint256 private round1Price;
    uint256 private round2Price;

    uint256 private round1Tokens;
    uint256 private round2Tokens;

    uint256 private round1TokensSold;
    uint256 private round2TokensSold;

    uint8 private currentRound;

    mapping(address => uint256) private purchasedTokens;

    event TokensPurchased(address indexed buyer, uint256 indexed tokensPurchased);

    constructor(
        address _walletForReceivedCoins,
        address _usdtErc20Token,
        address _roundChangeAdmin,
        uint256 _round1Price,
        uint256 _round1Tokens,
        uint256 _round2Price,
        uint256 _round2Tokens,
        uint8 _currentRound
    ) {
        walletForReceivedCoins = _walletForReceivedCoins;
        usdtErc20Token = _usdtErc20Token;
        roundChangeAdmin = _roundChangeAdmin;
        round1Price = _round1Price;
        round1Tokens = _round1Tokens * (10 ** 18);
        round2Price = _round2Price;
        round2Tokens = _round2Tokens * (10 ** 18);
        currentRound = _currentRound;
    }

    function buyTokens(uint256 _amount, address _coin, uint8 _round, address _admin) external nonReentrant {
        require(_admin == roundChangeAdmin, "Not authorized");
        require(_round == 1 || _round == 2, "Token sale is finished");   
        require(_coin == usdtErc20Token, "Invalid coin");
        currentRound = _round;
        uint256 tokensToPurchase = calculateTokens(_amount);
        uint256 tokensAvailable = (currentRound == 1) ? round1Tokens.sub(round1TokensSold) : round2Tokens.sub(round2TokensSold);
        require(tokensToPurchase > 0 && tokensToPurchase <= tokensAvailable, "Not enough tokens available for sale");

        require(IERC20(_coin).allowance(msg.sender, address(this)) >= _amount, "Insufficient allowance");
        IERC20(_coin).transferFrom(msg.sender, walletForReceivedCoins, _amount);

        if (currentRound == 1) {
            round1TokensSold = round1TokensSold.add(tokensToPurchase); 
        } else if (currentRound == 2) {
            round2TokensSold = round2TokensSold.add(tokensToPurchase);
        }

        purchasedTokens[msg.sender] = purchasedTokens[msg.sender].add(tokensToPurchase);

        emit TokensPurchased(msg.sender, tokensToPurchase);
    }

    function calculateTokens(uint256 _amount) internal view returns (uint256) {
        uint256 tokensToPurchase;
        if (currentRound == 1) {
                tokensToPurchase = _amount.mul(10 ** 18).div(round1Price);
        } else if (currentRound == 2) {
                tokensToPurchase = _amount.mul(10 ** 18).div(round2Price);
        }

        return tokensToPurchase;
    }

    function getRound1Price() external view returns (uint256) {
        return round1Price;
    }

    function getRound2Price() external view returns (uint256) {
        return round2Price;
    }

    function getRound1Tokens() external view returns (uint256) {
        return round1Tokens;
    }

    function getRound2Tokens() external view returns (uint256) {
        return round2Tokens;
    }

    function getRound1TokensSold() external view returns (uint256) {
        return round1TokensSold;
    }

    function getRound2TokensSold() external view returns (uint256) {
        return round2TokensSold;
    }

    function getRemainingTokensForRound1() external view returns (uint256) {
    return round1Tokens.sub(round1TokensSold);
    }

    function getRemainingTokensForRound2() external view returns (uint256) {
        return round2Tokens.sub(round2TokensSold);
    }

    function getCurrentRoundPrice() external view returns (uint256) {
        if (currentRound == 1) {
            return round1Price;
        } else if (currentRound == 2) {
            return round2Price;
        } else {
            return round2Price;
        }
    }

    function finishTokenSale() external onlyOwner nonReentrant {
        require(currentRound == 2, "Cannot finish token sale before round 2");
        currentRound = 0;
    }

    function setRound2Start() external onlyOwner nonReentrant {
        require(currentRound == 1, "Round 2 has already started");
        currentRound = 2;
    }

    function setRound2Price(uint256 _round2Price) external onlyOwner nonReentrant {
        require(currentRound == 1, "Can only change round 2 price during round 1");
        round2Price = _round2Price;
    }

    function setRoundChangeAdmin(address _admin) external onlyOwner nonReentrant {
        roundChangeAdmin = _admin;
    }

    function getPurchasedTokens(address _buyer) external view returns (uint256) {
        return purchasedTokens[_buyer];
    }

}
