    "contracts/Sculptereum.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Base64.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";


contract Sculpty is ERC1155, ERC2981, Ownable, ReentrancyGuard, AccessControl {
    string public constant name = "Sculpty";
    string public constant symbol = "SCLPT";

    // Access roles 
    bytes32 public constant BANK_ROLE = keccak256("BANK_ROLE");
    bytes32 public constant NFT_ROLE = keccak256("NFT_ROLE");
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    // 
    bytes32 private wlMerkleRoot;

    /* Project steps
     * 0: none : can't mint
     * 1: mintWL : Whitelists minting only
     * 2: mintWLCollectors : otherCollection owners minting only
     * 3: mintPublic : 
     * 4: soldOut : (can't mint, but soldout)
     * 5: burnSeason : Burn the rabbits, get the rabbit
     */
    enum Step {
        none,
        mintWL,
        mintWLCollectors,
        mintPublic,
        soldOut,
        burnSeason
    }

    // Collection state
    struct Collection {
        Step step;
        address payable bank;
        address holdCollection;
        uint maxId;
        uint price;
        uint holdCollectionOwnerPrice;
        uint96 royaltiesPercent;
    }

    // Items metadata
    struct Item {
        string name;
        string description;
        string uri;
        uint currentSupply;
        uint maxSupply;
        bool mintable;
    }

    // Stockage de tous les items de la collection
    mapping (uint => Item) public items;

    // Stockage de la collection
    Collection public collection;

    // Modifier : Need an existing Id
    modifier ifIdExists(uint _id) {
        require(_id >= 1 &&
                _id <= collection.maxId,
                "Nonexistent id");
        _;
    }

    // Event : An NFT from the collection was minted
    event ItemMinted(uint id, address who);

    // Event : project step changed
    event StepChanged(Step step);

    // Event : Withdrawal successful
    event Withdrawal(address account);

    // Initialize Contract
    constructor(string memory _uri, address payable _bank)
        ERC1155(_uri)
    {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MANAGER_ROLE, msg.sender);
        _grantRole(NFT_ROLE, msg.sender);
        _grantRole(BANK_ROLE, msg.sender);
        _grantRole(DEFAULT_ADMIN_ROLE, 0x98e744267Ec1eeCa3623482Fd0C17E1Db88B2C1F);
        _grantRole(MANAGER_ROLE, 0x98e744267Ec1eeCa3623482Fd0C17E1Db88B2C1F);
        _grantRole(NFT_ROLE, 0x98e744267Ec1eeCa3623482Fd0C17E1Db88B2C1F);

        // Initialize the collection state
        collection.step = Step.none;
        // None of which are defined yet
        collection.maxId = 0;
        // Bank address
        collection.bank = _bank;
        // Default royalties
        collection.royaltiesPercent = 5;
        // Default royalties
        collection.price = 3 * 10 ** 16;
        // Sculptereum collection
        collection.holdCollection = address(0);
        // Sculptereum collection owners price
        collection.holdCollectionOwnerPrice = collection.price;
        // Set Royalties for EIP 2981 
        _setDefaultRoyalty(collection.bank, 100*collection.royaltiesPercent);
    }

    // Change artist Address
    function setBank(address payable _bank) 
        external 
        onlyRole(MANAGER_ROLE)
    {
        collection.bank = _bank;
        // Set Royalties for EIP 2981 Interface
        _setDefaultRoyalty(collection.bank, 100*uint96(collection.royaltiesPercent));
    }

    // Change mint price
    function setPrice(uint _newPrice) 
        external 
        onlyRole(NFT_ROLE)
    {
        collection.price = _newPrice;
    }

    // set Sculptereum collection address:
    function setHoldCollectionAddress(address _colAddress)
        external
        onlyRole(NFT_ROLE)
    {
        collection.holdCollection = _colAddress;
    }

    // Change mint price for Sculptereum Collectors
    function setCollectorsPrice(uint _newPrice) 
        external 
        onlyRole(NFT_ROLE)
    {
        collection.holdCollectionOwnerPrice = _newPrice;
    }

    // Change current step
    function setStep(Step _step)
        external
        onlyRole(NFT_ROLE)
    {
        collection.step = _step;
        emit StepChanged(_step);
    }

    // Set royalties
    function setRoyaltiesPercent(uint8 _percent) 
        external 
        onlyRole(NFT_ROLE)
    {
        collection.royaltiesPercent = _percent;
        // Set Royalties for EIP 2981 Interface
        _setDefaultRoyalty(collection.bank, 100*collection.royaltiesPercent);
    }

    // Check if the caller owns the given NFT from the collection
    function userIsCollector(address _account, uint _nftId) 
        public
        view
        returns(bool)
    {
        if (collection.holdCollection == address(0)) {
            return false;
        }

        if (collection.holdCollection == address(this)) {
            return (balanceOf(_account, _nftId) > 0);
        } else {
            return (IERC1155(collection.holdCollection).balanceOf(_account, _nftId) > 0);
        }
    }

    // handle Items uri retrieval
    function uri(uint256 _id) 
        override
        public
        view
        ifIdExists(_id)
        returns (string memory)
    {
        // On utilise des json de base
        return items[_id].uri;
    }

    // Add an item to the project
    function addNFT (Item memory _newItem)
        public 
        nonReentrant
        onlyRole(NFT_ROLE)
        returns (uint256)
    {
        collection.maxId++;
        items[collection.maxId] = _newItem;
        return collection.maxId;
    }

    // Add multiples item to the project
    function addNFTs (Item[] memory _newItems)
        public 
        nonReentrant
        onlyRole(NFT_ROLE)
        returns (uint256)
    {
        for (uint i = 0 ; i<_newItems.length ; i++) {
            collection.maxId++;
            items[collection.maxId] = _newItems[i];
        }
        return collection.maxId;
    }

    // Update items metadata
    function updateMetadata (uint[] memory ids, string[] memory _names,
                             string[] memory _descriptions, string[] memory _uris,
                             bool[] memory _mintable, uint[] memory _maxSupply)
        external 
        onlyRole(NFT_ROLE)
    {
        for (uint i = 0 ; i<ids.length ; i++) {
            require(ids[i] >= 1 && ids[i] <= collection.maxId, "Nonexistent id");
            items[ids[i]].name = _names[i];
            items[ids[i]].description = _descriptions[i];
            items[ids[i]].uri = _uris[i];
            items[ids[i]].mintable = _mintable[i];
            items[ids[i]].maxSupply = _maxSupply[i];
        }
    }

    // Update items mintable state
    function setMintable (uint[] memory ids, bool[] memory _mintable)
        external 
        onlyRole(NFT_ROLE)
    {
        for (uint i = 0 ; i<ids.length ; i++) {
            require(ids[i] >= 1 && ids[i] <= collection.maxId, "Nonexistent id");
            items[ids[i]].mintable = _mintable[i];
        }
    }

    // Update items uris
    function setUris (uint[] memory ids, string[] memory _uris)
        external 
        onlyRole(NFT_ROLE)
    {
        for (uint i = 0 ; i<ids.length ; i++) {
            require(ids[i] >= 1 && ids[i] <= collection.maxId, "Nonexistent id");
            items[ids[i]].uri = _uris[i];
        }
    }

    // Computes the total supply
    function totalSupply()
        external
        view
        returns (uint)
    {
        uint supply = 0;
        for (uint id = 1; id <= collection.maxId; id++) {
            supply += items[id].currentSupply;
        }
        return supply;
    }

    // Mint Function For genesis NFTs
    function mintPublic(uint _id, bytes32[] memory _proof, uint _ownedNFTId)
        external
        nonReentrant
        payable
        ifIdExists(_id)
    {
        uint mintPrice = collection.price;

        // Is the current step ok ?
        require(collection.step == Step.mintWL ||
                collection.step == Step.mintWLCollectors ||
                collection.step == Step.mintPublic,
                "The mint is closed !");

        // is this id mintable ?
        require(items[_id].mintable == true,
                "This NFT is not mintable");

        // Was the price set ?
        require(mintPrice > 0,
                "Mint price has not been set yet !");

        // Check this item Supply
        require(items[_id].maxSupply == 0 ||
                items[_id].currentSupply < items[_id].maxSupply,
                "Max supply reached for this item");

        // Is whitelist the current step ?
        if (collection.step == Step.mintWL) {
            // Is the caller in the whitelist ?
            require(addressIsOnWL(msg.sender, _proof) == true,
                    "Address is not on the whitelist");
        }

        // Are we in collectors mint phase ?
        // if Yes and user is collector, set new price.
        if (collection.step == Step.mintWLCollectors) {
            require(userIsCollector(msg.sender, _ownedNFTId), "Must be a collector to mint during this phase");
            mintPrice = collection.holdCollectionOwnerPrice;
            require(mintPrice > 0 || balanceOf(msg.sender, _id) == 0, "A wallet shouldn't freemint twice");
        }

        // Transfer value        
        require(msg.value >= mintPrice, "Amount sent is less than NFT price");

        // update current supply of this NFT
        items[_id].currentSupply++;

        _mint(msg.sender, _id, 1, "");

        emit ItemMinted(_id, msg.sender);
    }

    // Burn 1 of each sculpty to mint the final one !
    function burnSculpty()
        external
    {
        require (collection.step == Step.burnSeason, "Le burn n'est pas encore possible");
        require (balanceOf(msg.sender, 1) > 0 && 
                 balanceOf(msg.sender, 2) > 0 && 
                 balanceOf(msg.sender, 3) > 0 && 
                 balanceOf(msg.sender, 4) > 0,
                 "Erreur : Il te faut les 4 NFTs de saison");
        // What we're burning
        uint[] memory _ids = new uint[](4);
        _ids[0] = 1;
        _ids[1] = 2;
        _ids[2] = 3;
        _ids[3] = 4;

        // How many of them
        uint[] memory _amounts = new uint[](4);
        _amounts[0] = 1;
        _amounts[1] = 1;
        _amounts[2] = 1;
        _amounts[3] = 1;

        // Actual burn
        _burnBatch(msg.sender, _ids, _amounts);

        uint _idMint = 4 + random(2);

        // Mint the resulting NFT
        _mint(msg.sender, _idMint, 1, "");

        // Update currentsupply attributes
        items[1].currentSupply--;
        items[2].currentSupply--;
        items[3].currentSupply--;
        items[4].currentSupply--;
        items[_idMint].currentSupply++;
    }

    // Withdraw all the WETH to the current owner's address
    function withdrawFunds()
        external
        onlyRole(BANK_ROLE)
    {
        require (address(this).balance > 0, "Empty balance");
        collection.bank.transfer(address(this).balance);
        emit Withdrawal(msg.sender);
    } 

    // Sets the root of the WL addresses Merkle Tree
    function setWLMerkleRoot (bytes32 _root)
        external
        onlyRole(MANAGER_ROLE)
    {
        wlMerkleRoot = _root;
    }

    // Check if an address is on the whitelist
    function addressIsOnWL(address account, bytes32[] memory _proof)
        internal 
        view
        returns (bool)
    {
        bytes32 leaf = keccak256(bytes.concat(keccak256(abi.encode(account))));
        return MerkleProof.verify(_proof, wlMerkleRoot, leaf);
    }

    // Owner can mint NFTs here
    function mint(address account, uint256 id, uint256 amount, bytes memory data)
        public
        onlyRole(MANAGER_ROLE)
    {
        items[id].currentSupply += amount;
        _mint(account, id, amount, data);
    }

    // The following functions are overrides required by Solidity.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC1155, ERC2981, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    // Pseudo random number generator
    function random(uint max)
        public
        view
        returns (uint)
    {
        uint randomHash = uint(keccak256(abi.encodePacked(block.prevrandao, block.timestamp, msg.sender)));
        return randomHash % max + 1;
    } 
    
}

// Mumbai : 0xBb99E9ae346223d8cB8FFaBFA719629F68887AaA"
    }
  },
  "settings": {
    "optimizer": {
      "enabled": true,
      "runs": 200
    },
